INSERT INTO `loai_san_pham` (`id`, `ten_loai`, `hinh`, `ghi_chu`, `trang_thai`, `created_at`, `updated_at`) VALUES
(1, 'Thuốc', 'thuoc.jpg', '<p>Thuốc l&agrave; chế phẩm c&oacute; chứa dược chất hoặc dược liệu d&ugrave;ng cho người nhằm Mục đ&iacute;ch ph&ograve;ng bệnh, chẩn đo&aacute;n bệnh, chữa bệnh, Điều trị bệnh, giảm nhẹ bệnh, Điều chỉnh chức năng sinh l&yacute; cơ thể người bao gồm thuốc h&oacute;a dược, thuốc dược liệu, thuốc cổ truyền, vắc xin v&agrave; sinh phẩm.</p>', 1, '2021-06-18 05:56:04', '2021-07-01 09:28:31'),
(2, 'Dược mỹ phẩm', 'duoc-my-pham.jpg', '<p>Sản phẩm từ dược mỹ phẩm c&oacute; khả năng điều chỉnh v&agrave; phục hồi c&aacute;c vấn đề chuy&ecirc;n s&acirc;u về da. Cụ thể như l&atilde;o h&oacute;a, sạm n&aacute;m, vi&ecirc;m nhiễm, mụn m&agrave; vẫn duy tr&igrave; vẻ đẹp cho l&agrave;n da an to&agrave;n.</p>', 1, '2021-06-18 06:04:19', '2021-07-01 09:28:49'),
(3, 'Chăm sóc cá nhân', 'cham-soc-ca-nhan.jpg', '<p>Chăm s&oacute;c c&aacute; nh&acirc;n hoặc đồ vệ sinh c&aacute; nh&acirc;n l&agrave; sản phẩm ti&ecirc;u d&ugrave;ng được sử dụng để vệ sinh c&aacute; nh&acirc;n v&agrave; l&agrave;m đẹp.</p>', 1, '2021-06-18 06:05:15', '2021-07-01 09:28:57'),
(4, 'Thực phẩm chức năng', 'thuc-pham-chuc-nang.jpg', '<p>Thực phẩm chức năng l&agrave; c&aacute;c loại thực phẩm bổ sung v&agrave;o chế độ ăn uống của bạn để cải thiện sức khỏe, vẻ đẹp từ b&ecirc;n trong, đồng thời l&agrave;m giảm nguy cơ gặp c&aacute;c vấn đề về sức khỏe do thiếu chất.</p>', 1, '2021-06-18 06:05:54', '2021-07-01 09:29:11'),
(5, 'Trang thiết bị y tế', 'trang-thiet-bi-y-te.jfif', '<p>Với nhu cầu chăm s&oacute;c ng&agrave;y một được ch&uacute; trọng v&agrave; quan t&acirc;m ngo&agrave;i việc kh&aacute;m sức khoẻ định k&igrave; ở những bệnh viện lớn th&igrave; việc để &yacute; đến sức khoẻ trong hoạt động sinh hoạt hằng ng&agrave;y cũng v&ocirc; c&ugrave;ng quan trọng. Hiểu được nhu cầu của người ti&ecirc;u d&ugrave;ng, tr&ecirc;n thị trường đang c&oacute; rất nhiều loại thiết bị y tế gia đ&igrave;nh c&oacute; thể hỗ trợ cho sức khoẻ cho mọi người.</p>', 1, '2021-06-18 06:07:08', '2021-07-01 09:18:26');
