INSERT INTO `san_pham` (`id`, `ten_san_pham`, `ten_url`, `hinh_san_pham`, `gia_san_pham`, `giam_gia`, `mo_ta_tom_tat`, `chi_tiet`, `trang_thai`, `ma_loai`, `don_vi`, `nguon_goc`, `created_at`, `updated_at`) VALUES
(1, 'Fugacar Mebendazole', 'fugacar-mebendazole', 'P00779_1_l.jpg', 19000, 0, 'Thuốc điều trị nhiễm một hoặc nhiều loại giun: giun chỉ, giun đũa, giun tóc, giun móc, giun kim.', '<div>\r\n<p><strong>1. Chống chỉ định:&nbsp;</strong><br />\r\nD&ugrave;ng Fugacar cho trẻ em dưới 1 tuổi trong điều trị h&agrave;ng loạt c&aacute;c trường hợp nhiễm một hay nhiều loại giun. Mẫn cảm với th&agrave;nh phần của thuốc hoặc c&aacute;c t&aacute; dược của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nNhiễm một hay nhiều loại giun đường ruột: Giun đũa (Ascaris lumbricoides), giun t&oacute;c (Trichuris trichiura), giun m&oacute;c (Ancylostoma duodenale, Necator americanus), giun kim (Enterobius Vermicularis).<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống 1 vi&ecirc;n duy nhất, c&oacute; thể nhai trước khi nuốt. Tẩy giun định kỳ 4 &ndash; 6 th&aacute;ng/lần.<br />\r\n<strong>Thận trọng:</strong><br />\r\nKh&ocirc;ng n&ecirc;n d&ugrave;ng cho trẻ em dưới 12 th&aacute;ng tuổi trừ phi t&igrave;nh trạng nhiễm giun ảnh hưởng trầm trọng đ&aacute;ng kể đến t&igrave;nh trạng dinh dưỡng v&agrave; ph&aacute;t triển thể chất của trẻ.&nbsp;<br />\r\nKh&ocirc;ng sử dụng cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute;, đặc biệt 3 th&aacute;ng đầu của thai kỳ.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nỞ nhiệt độ 15 &ndash; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Mebendazole 500mg.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 vi&ecirc;n n&eacute;n<br />\r\n<strong>Xuất xứ thương hiệu:&nbsp;</strong>Nhật Bản<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Janssen</p>\r\n</div>', 1, 1, 'hộp', 'Nhật Bản', NULL, '2021-06-27 06:28:00'),
(2, 'Nước muối sinh lý', 'nuoc-muoi-sinh-ly', 'P10766_1.jpg', 8500, 0, 'Nước muối sinh lý Vĩnh Phúc Natriclorid 0.9% giúp làm sạch răng, khử mùi hôi khoang miệng đồng thời làm sạch bụi bẩn bám trên da mặt, ngăn ngừa mụn trứng cá', '<p><strong>1. Chống chỉ định</strong><br />\r\nKh&ocirc;ng c&oacute;</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng<br />\r\nChỉ định</strong><br />\r\nRửa l&agrave;m sạch bụi bẩn b&aacute;m tr&ecirc;n da mặt, ngăn ngừa mụn trứng c&aacute;.<br />\r\nGi&uacute;p l&agrave;m sạch răng, khử m&ugrave;i h&ocirc;i khoang miệng.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNgậm 20-30ml dung dịch kh&ocirc;ng pha lo&atilde;ng, s&uacute;c kỹ trong khoảng 30 gi&acirc;y rồi nhổ đi.<br />\r\nD&ugrave;ng b&ocirc;ng hoặc gạc mềm sạch tẩm dung dịch để lau rửa, massage mặt, loại bỏ bụi bẩn, b&atilde; nhờn, mụn trứng c&aacute;. Đặc biệt sau khi tham gia giao th&ocirc;ng hoặc l&agrave;m việc ở m&ocirc;i trường kh&oacute;i bụi, h&oacute;a chất. Lau rửa sau khi tẩy trang.<br />\r\nĐổ dung dịch ra ly (kh&ocirc;ng pha lo&atilde;ng), d&ugrave;ng l&agrave;m nước đ&aacute;nh răng hằng ng&agrave;y.<br />\r\n<strong>Bảo quản:&nbsp;</strong>Nơi kh&ocirc; r&aacute;o, tr&aacute;nh &aacute;nh nắng.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc<br />\r\nTh&agrave;nh phần:</strong><br />\r\nSodium Chloride&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..0.9%<br />\r\nAqua (Water)&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;99.1%<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Chai 500ml<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:&nbsp;</strong>Duoc Vinh Phuc</p>', 1, 3, 'chai', 'Việt Nam', '2021-06-19 02:01:31', '2021-07-12 01:48:01'),
(20, 'Cao dán Salonpas', 'cao-dan-salonpas', 'P10601.jpg', 22500, 0, 'Cao dán Salonpas dùng để giảm đau nhanh chóng, kháng viêm trong các cơn đau liên quan đến các triệu chứng đau khớp, đau cơ, đau lưng, căng cơ, bong gân, đau răng. Sản phẩm là giải pháp tiện lợi chăm sóc cho sức khỏe gia đình bạn.', '<p><strong>1. Chống chỉ định</strong><br />\r\nKh&ocirc;ng sử dụng tr&ecirc;n v&ugrave;ng da tổn thương, vết thương hở, mắt, v&ugrave;ng da quanh mắt, ni&ecirc;m mạc v&agrave; người dị ứng với bất kỳ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nKh&aacute;ng vi&ecirc;m, giảm đau trong c&aacute;c cơn đau li&ecirc;n quan đến: Đau vai, đau lưng, đau cơ, mỏi cơ, đau khớp, bầm t&iacute;m, bong g&acirc;n, căng cơ, đau đầu, đau răng.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNgười lớn v&agrave; trẻ em từ 12 tuổi trở l&ecirc;n: Rửa sạch, lau kh&ocirc; v&ugrave;ng da bị đau. Gỡ miếng d&aacute;n v&agrave; d&aacute;n v&agrave;o chỗ đau kh&ocirc;ng qu&aacute; 3 lần trong ng&agrave;y v&agrave; kh&ocirc;ng qu&aacute; 7 ng&agrave;y. Gỡ miếng d&aacute;n ra khỏi da sau 8 giờ.<br />\r\nTrẻ em dưới 12 tuổi: Hỏi &yacute; kiến B&aacute;c sĩ trước khi sử dụng.<br />\r\n<strong>Thận trọng:</strong><br />\r\nKhi sử dụng cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute;.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNhiệt độ dưới 30 độ C, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc</strong><br />\r\n<strong>Th&agrave;nh phần:</strong><br />\r\nHoạt chất: Methyl salicylate 6.29%, L-Menthol 5.71%, dl-Camphor 1.24%,Tocopherol acetate 2%.<br />\r\nT&aacute; dược: Raw Rubber, Polyisobutylene, Titanium Oxide, Perfume&hellip;<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:&nbsp;</strong>Hộp 2 bao x 10 miếng<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Nhật Bản<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Hisamitsu&nbsp;Pharmaceutical&nbsp;</p>', 1, 1, 'hộp', 'Nhật Bản', '2021-06-21 09:01:34', '2021-07-03 07:59:41'),
(4, 'Efferalgan', 'efferalgan', 'P00126_1_l.jpg', 42000, 35000, 'Thuốc dùng điều trị các chứng đau hoặc sốt như đau đầu, tình trạng như cúm, đau răng, nhức mỏi cơ, đau bụng kinh. Dạng viên nén sủi bọt sễ bẻ, dễ uống, tiện lợi cho người sử dụng.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nDị ứng với Paracetamol.<br />\r\nC&oacute; bệnh gan nặng.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị c&aacute;c chứng đau hoặc sốt như đau đầu, c&aacute;c t&igrave;nh trạng như c&uacute;m, đau răng, nhức mỏi cơ, đau bụng kinh.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nDạng thuốc n&agrave;y chỉ sử dụng cho người lớn v&agrave; trẻ em c&oacute; c&acirc;n nặng tr&ecirc;n 17kg.<br />\r\nKh&ocirc;ng vượt qu&aacute; 3000mg Paracetamol một ng&agrave;y (khoảng 6 vi&ecirc;n một ng&agrave;y). C&oacute; thể tăng đến 4000mg một ng&agrave;y (khoảng 8 vi&ecirc;n một ng&agrave;y).<br />\r\n<strong>Thận trọng:</strong><br />\r\nTrường hợp ăn ki&ecirc;ng muối hoặc ăn nhạt cần nhớ l&agrave; trong mỗi vi&ecirc;n thuốc c&oacute; chứa 412.4mg Na t&iacute;nh khẩu phần ăn h&agrave;ng ng&agrave;y.<br />\r\nKh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y tr&ecirc;n bệnh nh&acirc;n kh&ocirc;ng dung nạp với Fructose.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Paracetamol 500mg, t&aacute; dược vừa đủ.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 16 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Ph&aacute;p<br />\r\n<strong>Nh&agrave; sản xuất:&nbsp;</strong>Bristol Myers Squibb</p>', 1, 1, 'hộp', 'Pháp', '2021-06-19 02:03:21', '2021-07-27 11:18:12'),
(5, 'Máy Đo Đường Huyết', 'may-do-duong-huyet', '00028635.jpg', 1139000, 999000, 'Máy đo đường huyết MediUSA GM3300 được thiết kế dựa trên nhu cầu kiểm tra đường huyết thường xuyên của tất cả mọi người', '<p>M&aacute;y đo đường huyết MediUSA GM3300 được thiết kế dựa tr&ecirc;n nhu cầu kiểm tra đường huyết thường xuy&ecirc;n của tất cả mọi người. M&aacute;y đo đường huyết tự động MediUSA GM3300&nbsp;kiểm so&aacute;t đường huyết&nbsp;tự động h&oacute;a, k&iacute;ch cỡ mẫu m&aacute;u 0.7ul, đo nhanh trong 6 gi&acirc;y, kết quả do trung b&igrave;nh của 7 ng&agrave;y, bộ nhớ 250 kết quả, thiết kế gọn nhẹ.&nbsp;</p>', 1, 5, 'cái', 'Mỹ', '2021-06-19 08:46:44', '2021-07-12 02:28:38'),
(6, 'BPI CLA + Carnitine Rainbow Ice', 'bpi-cla-+-carnitine-rainbow-ice', 'P20540_1_l.jpg', 600000, 569000, 'Thực phẩm hỗ trợ tăng cơ đốt mỡ CLA+ Carnitine Rainbow Ice hỗ trợ xây dựng cơ nạc, giảm mỡ, khi tập luyện thì bạn sẽ không lo về vấn đề mất cơ vì CLA giúp bạn bảo vệ được khối cơ.', '<p><strong>Th&agrave;nh phần</strong><br />\r\nTrong 7g chứa:<br />\r\nCLA matrix (bột dầu dừa,bột dầu bơ, bột dầu c&acirc;y rum [cung cấp cla (cung cấp axit linoleic li&ecirc;n hợp)]) 2.5g, carnitine matrix (carnitine (như carnitine betaine khan, L-carnitine tartrate, Glycine propionyl L-carnitine HCL)) 2g, vitamin D3 250mcg.</p>\r\n\r\n<p>Th&agrave;nh phần kh&aacute;c: Chất chống đ&ocirc;ng v&oacute;n (E551), hương tự nhi&ecirc;n v&agrave; hương nh&acirc;n tạo, chất điều chỉnh độ acid (E330, E296), chất tạo ngọt (E955, E950), maltodextrin, muối.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng</strong><br />\r\n&ndash; Giảm c&acirc;n: Ho&agrave;n to&agrave;n kh&ocirc;ng k&iacute;ch th&iacute;ch v&agrave; gi&uacute;p cơ thể giảm c&acirc;n theo c&aacute;ch ho&agrave;n to&agrave;n tự nhi&ecirc;n với cơ chế hỗ trợ đốt mỡ v&agrave; tạo năng lượng nhanh ch&oacute;ng.<br />\r\n&ndash; Hiệu suất: Hiệu quả đốt mỡ nhiều c&ugrave;ng năng lượng tạo ra cao v&agrave; thời gian cực kỳ nhanh ch&oacute;ng cực k&igrave; hiệu quả trong c&aacute;c hoạt động thể thao như tập gym.<br />\r\n&ndash; Ph&aacute;t triển cơ bắp nạc: Mặc d&ugrave; tập trung đ&aacute;nh v&agrave;o lượng mỡ dư thừa trong cơ thể nhưng vẫn giữ nguy&ecirc;n được khối lượng nạc v&agrave; hỗ trợ sử dụng cơ bắp cho kết quả ho&agrave;n hảo.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng</strong><br />\r\nCho một muỗng (7g) v&agrave; 8 ounce (1 ounce= 29,57 ml) nước lạnh v&agrave;o buổi s&aacute;ng v&agrave; một muỗng v&agrave;o buổi tối.</p>\r\n\r\n<p><em>Cảnh b&aacute;o:</em>&nbsp;Kh&ocirc;ng d&ugrave;ng cho người dưới 18 tuổi. Nếu bạn đang d&ugrave;ng thuốc, c&oacute; một t&igrave;nh trạng y tế, đang mang thai, đang cho con b&uacute; hoặc nghĩ về việc mang thai phải được chấp thuận của b&aacute;c sĩ mới sử dụng. Ngưng sử dụng trước hai tuần khi phẫu thuật.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;350g/hộp.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Mỹ</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Mỹ</p>', 1, 4, 'hộp', 'Mỹ', '2021-06-20 06:40:22', '2021-07-12 01:48:46'),
(7, 'Dầu gió Trường Sơn', 'dau-gio-truong-son', 'P00024.jpg', 11000, 0, 'Dầu gió Trường Sơn với các thầnh phần từ tinh dầu tự nhiên giúp chuyên trị cảm cúm, nhức đầu, sổ mũi, say tàu xe, sưng viêm, đau trật gân, muỗi chích, kiến cắn, buồn nôn, đau bụng, nhức mỏi… Tiện lợi cho sức khỏe gia đình bạn.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nChiết xuất tinh dầu bạc h&agrave;: 3g, Menthol: 22g, tinh dầu đinh hương: 0.2g, long n&atilde;o: 2g, Eucalypol: 4.8g.Dầu Parafin vđ: 100ml.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nTrị cảm ho, sổ mũi, nhức đầu, đau bụng, ch&oacute;ng mặt, nhức mỏi tay ch&acirc;n, say s&oacute;ng khi đi t&agrave;u xe, buồn n&ocirc;n, sưng vi&ecirc;m, trật g&acirc;n, kiến cắn, muỗi ch&iacute;ch&hellip;</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:&nbsp;</strong><br />\r\nThoa v&agrave; xoa b&oacute;p chỗ đau, pha với nước ấm để x&ocirc;ng, thoa b&ocirc;i l&ecirc;n mũi để h&iacute;t v&agrave; ngửi.</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>Chai 6ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 1, 'chai', 'Việt Nam', '2021-06-21 08:25:59', '2021-07-12 02:04:05'),
(8, 'Vitamin E Mega Enat 400', 'vitamin-e-mega-enat-400', 'P00194.jpg', 100000, 0, 'Điều trị và dự phòng tình trạng thiếu Vitamin E. Vitamin E thiên nhiên giúp duy trì vẻ đẹp của làn da, làm giảm tiến trình lão hóa của da và giúp ngăn ngừa xuất hiện nếp nhăn trên da. Đồng thời Vitamin E còn có tác dụng như là một chất chống oxy hóa, giúp cho quá trình bảo vệ các tế bào bị tổn thương gây ra bởi quá trình oxy hóa của các gốc tự do.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nBệnh nh&acirc;n giảm Thrombinemia do thiếu hụt vitamin K.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị v&agrave; dự ph&ograve;ng t&igrave;nh trạng thiếu Vitamin E.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống 1 vi&ecirc;n mỗi ng&agrave;y sau mỗi bữa ăn hoặc theo &yacute; ki&ecirc;n B&aacute;c sĩ.<br />\r\n<strong>Thận trọng:</strong><br />\r\nCần thận trọng khi d&ugrave;ng chung với thuốc chống đ&ocirc;ng, warfarin hay c&aacute;c thuốc l&agrave;m lo&atilde;ng m&aacute;u, aspirin.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, dưới 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Vitamin E 400IU, t&aacute; dược vừa đủ 1 vi&ecirc;n nang.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 3 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Th&aacute;i Lan<br />\r\n<strong>Sản xuất tại:&nbsp;</strong>Th&aacute;i Lan</p>', 1, 4, 'hộp', 'Thái Lan', '2021-06-21 08:27:55', '2021-07-12 02:28:57'),
(9, 'Albendazol STELLA', 'albendazol-stella', 'P02522.jpg', 3800, 0, 'Albendazol Stada 400mg điều trị hiễm các loại giun như: Giun đũa, giun kim, giun móc, giun tóc, giun lươn. Nhiễm các loại sán như: Sán hạt dưa, sán lợn, sán bò, sán lá gai loại Opisthorchis và O.sinensis.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nQu&aacute; mẫn cảm với nh&oacute;m Benzimidazol hoặc với bất cứ&nbsp;th&agrave;nh phần n&agrave;o của thuốc.<br />\r\nTiền sử nhiễm độc tủy xương. Phụ nữ c&oacute; thai.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị hiễm c&aacute;c loại giun như: Giun đũa, giun kim, giun m&oacute;c, giun t&oacute;c, giun lươn.<br />\r\nNhiễm c&aacute;c loại s&aacute;n như: S&aacute;n hạt dưa, s&aacute;n lợn, s&aacute;n b&ograve;, s&aacute;n l&aacute; gai loại Opisthorchis v&agrave; O.sinensis.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNhai vi&ecirc;n v&agrave; uống với một ly nước, hoặc nghiền vi&ecirc;n v&agrave; trộn với thức ăn. Kh&ocirc;ng cần phải nhịn đ&oacute;i hoặc tẩy.<br />\r\nLiều lượng<br />\r\na. Giun đũa, giun kim, giun m&oacute;c hoặc giun t&oacute;c:<br />\r\nNgười lớn v&agrave; trẻ em tr&ecirc;n 2 tuổi: Liều giống nhau: 400mg uống 1 liều duy nhất trong 1 ng&agrave;y. C&oacute; thể điều trị lại sau 3 tuần.<br />\r\nTrẻ em cho tới 2 tuổi: 200mg 1 liều duy nhất uống trong 1 ng&agrave;y. C&oacute; thể điều trị lại sau 3 tuần.<br />\r\nb. Ấu tr&ugrave;ng di tr&uacute; ở da:<br />\r\nNgười lớn: Uống 400mg, ng&agrave;y uống 1 lần, uống 3 ng&agrave;y.<br />\r\nTrẻ em: uống 5mg/kg/ng&agrave;y, uống 3 ng&agrave;y.<br />\r\nc. Ấu tr&ugrave;ng s&aacute;n lợn ở n&atilde;o:<br />\r\nNgười lớn: 15mg/kg/ng&agrave;y trong 30 ng&agrave;y. Điều trị c&oacute; thể lặp lại sau 3 tuần.<br />\r\nTrẻ em: Xem liều người lớn.<br />\r\nd. Bệnh nang s&aacute;n:<br />\r\nNgười lớn: uống 800mg mỗi ng&agrave;y, chia l&agrave;m 2 lần, trong 28 ng&agrave;y. Điều trị c&oacute; thể lặp lại sau 2 &ndash; 3 tuần nếu cần, c&oacute; khi cho tới 2 hoặc 3 đợt điều trị. Nếu nang s&aacute;n kh&ocirc;ng phẫu thuật được, c&oacute; thể cho tới 5 đợt.<br />\r\nTrẻ em từ 6 tuổi trở l&ecirc;n: uống 10 &ndash; 15mg/kg/ng&agrave;y, trong 28 ng&agrave;y. Điều trị c&oacute; thể lặp lại nếu cần.<br />\r\nTrẻ em cho tới 6 tuổi: Liều lượng chưa được x&aacute;c định.<br />\r\ne. S&aacute;n d&acirc;y, s&aacute;n hạt dưa, giun lươn:<br />\r\nNgười lớn: uống 400mg/ng&agrave;y/lần trong 3 ng&agrave;y. Điều trị c&oacute; thể lặp lại sau 3 tuần.<br />\r\nTrẻ em 2 tuổi trở l&ecirc;n: Liều giống liều người lớn.<br />\r\nTrẻ em cho tới 2 tuổi: uống 200mg/ng&agrave;y/lần trong 3 ng&agrave;y li&ecirc;n tiếp. Điều trị c&oacute; thể lập lại sau 3 tuần.<br />\r\n<strong>Thận trọng:</strong><br />\r\nNgười c&oacute; bệnh về gan, người c&oacute; bệnh về m&aacute;u. Phụ nữ cho con b&uacute;.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:&nbsp;</strong>Albendazol 400mg, t&aacute; dược vừa đủ 1 vi&ecirc;n.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:&nbsp;</strong>Đức<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;STADA</p>', 1, 1, 'hộp', 'Đức', '2021-06-21 08:29:27', '2021-07-12 01:49:35'),
(10, 'Thuốc Nhỏ Mắt Osla', 'thuoc-nho-mat-osla', 'P00541.jpg', 19000, 0, 'Thuốc nhỏ mắt dùng cho mỏi mắt, mắt khô, ngứa, cộm, đỏ mắt, mờ mắt, chảy nước mắt và khó chịu mắt. Rửa mắt để loại bỏ các vật lạ như bụi bẩn vào mắt, làm sạch ghèn mắt. Phòng ngừa các bệnh đau mắt.', '<p><strong>1. Chống chỉ định:&nbsp;</strong><br />\r\nDị ứng với bất kỳ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nD&ugrave;ng cho mỏi mắt, mắt kh&ocirc;, ngứa, cộm, đỏ mắt, mờ mắt, chảy nước mắt v&agrave; kh&oacute; chịu mắt.<br />\r\nRửa mắt để loại bỏ c&aacute;c vật lạ như bụi bẩn v&agrave;o mắt, l&agrave;m sạch gh&egrave;n mắt.<br />\r\nPh&ograve;ng ngừa c&aacute;c bệnh đau mắt.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNgười lớn v&agrave; trẻ em: Nhỏ 3 &ndash; 5 giọt mỗi mắt/lần x 2 &ndash; 4 lần/ng&agrave;y. C&oacute; thể nhỏ nhiều lần hơn nếu cần. Đậy k&iacute;n nắp sau khi d&ugrave;ng.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nNơi kh&ocirc; m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong><br />\r\nNatri clorid 0.033g, Borneol, Acid boric, Natri borat, t&aacute; dược vừa đủ 15ml.&nbsp;<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:&nbsp;</strong>Hộp 1 chai 15ml<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:&nbsp;</strong>Merap Group</p>', 1, 3, 'chai', 'Việt Nam', '2021-06-21 08:30:44', '2021-07-12 01:49:44'),
(11, 'Medoral', 'medoral', 'P13194.jpg', 89000, 0, 'Nước súc miệng họng Medoral hỗ trợ điều trị và ngăn ngừa viêm, nhiễm khuẩn ở họng/miệng như: Viêm họng, viêm amidan, viêm lợi, viêm miệng… đồng thời sát khuẩn, ức chế sự hình thành mảng bám trên răng, kiểm soát loét miệng, viêm miệng và nhiễm nấm Candida miệng.', '<p><strong>1. Chống chỉ định:&nbsp;</strong>Dị ứng với bất cứ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nHỗ trợ điều trị v&agrave; ngăn ngừa vi&ecirc;m, nhiễm khuẩn ở họng/miệng như: Vi&ecirc;m họng, vi&ecirc;m amidan, vi&ecirc;m lợi, vi&ecirc;m miệng&hellip;<br />\r\nS&aacute;t khuẩn, ức chế sự h&igrave;nh th&agrave;nh mảng b&aacute;m tr&ecirc;n răng, vệ sinh răng miệng.<br />\r\nĐẩy mạnh l&agrave;m l&agrave;nh vết thương sau phẫu thuật hoặc điều trị nha khoa.<br />\r\nKiểm so&aacute;t lo&eacute;t miệng t&aacute;i ph&aacute;t.<br />\r\nKiểm so&aacute;t răng giả g&acirc;y vi&ecirc;m miệng v&agrave; nhiễm nấm Candida miệng.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nS&uacute;c họng/miệng trong khoảng 1 ph&uacute;t với 10ml, mỗi ng&agrave;y 2 lần.<br />\r\nTrong phẫu thuật răng: S&uacute;c miệng trong 1 ph&uacute;t trước khi điều trị.<br />\r\nĐiều trị vi&ecirc;m lợi: D&ugrave;ng trong 1 th&aacute;ng.<br />\r\nVi&ecirc;m miệng: Rửa sạch v&agrave; ng&acirc;m h&agrave;m răng giả trong Medoral trong 15 ph&uacute;t, 2 lần mỗi ng&agrave;y.<br />\r\n<strong>Bảo quản:</strong><br />\r\nBảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t. Tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong>&nbsp;Chlorhexidin digluconat, Matito, Xylitol, Tinh dầu cam, Methol, Ethanol, Nước tinh khiết<br />\r\n<strong>Xuất xứ thương hiệu:</strong><br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 chai x 250ml<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Merap Group</p>', 1, 3, 'chai', 'Việt Nam', '2021-06-21 08:32:12', '2021-07-12 01:50:03'),
(12, 'Thuốc nhỏ mắt mũi Natri Clorid', 'thuoc-nho-mat-mui-natri-clorid', 'P18002.jpg', 2900, 0, 'Natri Clorid 0.9% dùng để rửa mắt, rửa mũi, hỗ trợ điều trị nghẹt mũi, sổ mũi, viêm mũi do dị ứng, dùng được cho trẻ sơ sinh.', '<p><strong>1. Chống chỉ định:&nbsp;</strong>Kh&ocirc;ng c&oacute;</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nD&ugrave;ng để rửa mắt, rửa mũi.<br />\r\nPhụ trị nghẹt mũi, sổ mũi, vi&ecirc;m mũi do dị ứng.<br />\r\nD&ugrave;ng được cho trẻ sơ sinh.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNhỏ hoặc rửa mắt, hốc mũi, mỗi lần 1 &ndash; 3 giọt, ng&agrave;y 1 &ndash; 3 lần hoặc nhiều hơn.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Natri clorid 0.9%, t&aacute; dược vừa đủ 10ml.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 chai x 10ml<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Pharmedic</p>', 1, 3, 'chai', 'Việt Nam', '2021-06-21 08:33:21', '2021-06-27 06:59:07'),
(13, 'Viên sủi Berocca', 'vien-sui-berocca', 'P00053.jpg', 70000, 0, 'Viên sủi bổ sung Vitamin Berocca Performance dùng để phòng ngừa và bổ sung trong các tình trạng tăng nhu cầu hoặc tăng nguy cơ thiếu Vitamin, như khi bị stress hoặc mệt mỏi, bứt rứt khó chịu, mất ngủ.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nMẫn cảm với bất cứ th&agrave;nh phần n&agrave;o của thuốc.<br />\r\nSuy giảm chức năng thận hoặc sỏi đường niệu.<br />\r\nKh&ocirc;ng n&ecirc;n d&ugrave;ng Vitamin C cho bệnh nh&acirc;n Oxalate niệu.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nPh&ograve;ng ngừa v&agrave; bổ sung trong c&aacute;c t&igrave;nh trạng tăng nhu cầu hoặc tăng nguy cơ thiếu c&aacute;c Vitamin nh&oacute;m B, Vitamin C, Calci v&agrave; magnesi như khi bị stress sinh l&yacute;, mệt mỏi, kh&oacute; chịu, mất ngủ&hellip;D&ugrave;ng cho người lớn v&agrave; trẻ em tr&ecirc;n 15 tuổi.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\n1 đến 2 vi&ecirc;n n&eacute;n sủi bọt mỗi ng&agrave;y hoặc theo hướng dẫn của thầy thuốc.<br />\r\nH&ograve;a tan vi&ecirc;n sủi bọt trong 1 ly nước để c&oacute; một loại nước uống c&oacute; hương vị thơm ngon.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bảo quản dưới&nbsp;25 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:</strong><br />\r\nVitamin B115mg, Vitamin B2 15mg, Vitamin B6 10mg, Vitamin B12 0.01mg, Vitamin B3 50mg, Vitamin B5 23mg, Vitamin B8 0.15mg, Vitamin B9 0.40mg, Vitamin C 500mg, Calci 100mg, Magnesi 100mg, Kẽm 10mg.<br />\r\nT&aacute; dược vừa đủ 1 vi&ecirc;n sủi.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;10 vi&ecirc;n sủi/tu&yacute;p<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Đức<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Bayer HealthCare Pharmaceuticals</p>', 1, 4, 'hộp', 'Đức', '2021-06-21 08:38:23', '2021-06-27 07:05:24'),
(14, 'Panadol Extra', 'panadol-extra', 'P01392.jpg', 214000, 199000, 'Panadol Extra có chứa Paracetamol giúp giảm đau – hạ sốt hữu hiệu. Điều trị đau nhẹ đến đau vừa và hạ sốt như đau đầu, đau nửa đầu, đau bụng kinh, đau xương, sốt và đau sau khi tiêm Vacxin, đau sau nhỏ răng hoặc sau các thủ thuật nha khoa, đau răng, đau do viêm xương khớp.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nKh&ocirc;ng d&ugrave;ng cho c&aacute;c bệnh nh&acirc;n c&oacute; tiền sử qu&aacute; mẫn với Paracetamol, Caffeine hoặc bất kỳ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nGiảm đau, hạ sốt. Điều trị c&aacute;c triệu chứng đau đầu, đau cơ, đau bụng kinh, đau họng, đau cơ xương&hellip;<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNgười lớn (kể cả người cao tuổi) v&agrave; trẻ em từ 12 tuổi trở l&ecirc;n. Chỉ d&ugrave;ng đường uống.<br />\r\nN&ecirc;n d&ugrave;ng 500mg paracetamol/ 65mg caffeine đến 1000mg paracetamol/ 130mg caffeine (1 hoặc 2 vi&ecirc;n) mỗi 4 đến 6 giờ nếu cần.<br />\r\nLiều tối đa h&agrave;ng ng&agrave;y: 4000mg/520mg (paracetamol/ caffeine).<br />\r\nThời gian tối thiểu d&ugrave;ng liều lặp lại: 4 giờ.<br />\r\nKh&ocirc;ng d&ugrave;ng c&aacute;c thuốc kh&aacute;c c&oacute; chưa paracetamol.<br />\r\nKh&ocirc;ng d&ugrave;ng qu&aacute; liều chỉ định.<br />\r\nTrẻ em dưới 12 tuổi: Kh&ocirc;ng khuyến nghị d&ugrave;ng thuốc n&agrave;y cho trẻ em dưới 12 tuổi.<br />\r\n<strong>Thận trọng:</strong><br />\r\nThận trọng khi d&ugrave;ng thuốc k&eacute;o d&agrave;i, nhất l&agrave; trong những trường hợp bệnh nh&acirc;n bị suy thận hay suy gan, d&ugrave;ng qu&aacute; liều tối đa được khuyến c&aacute;o. Phụ nữ c&oacute; thai v&agrave; phụ nữ đang cho con b&uacute;.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, dưới 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:</strong><br />\r\nMỗi vi&ecirc;n chứa Paracetamol: 500mg v&agrave; Caffeine: 65 mg<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 15 vỉ x 12 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:&nbsp;</strong>Anh<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;GlaxoSmithKline</p>', 1, 1, 'hộp', 'Anh', '2021-06-21 08:39:40', '2021-07-12 02:29:54'),
(15, 'Panadol Cold Flu', 'panadol-cold-flu', 'P03029.jpg', 180000, 0, 'Thuốc giảm các triệu chứng cảm cúm không gây buồn ngủ Panadol Cold Flu làm giảm triệu chứng của cảm cúm như sốt, đau và xung huyết mũi.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nMẫn cảm với th&agrave;nh phần thuốc.&nbsp;<br />\r\nBệnh nh&acirc;n đang d&ugrave;ng hoặc đ&atilde; d&ugrave;ng trong 2 tuần gần đ&acirc;y c&aacute;c thuốc ức chế Monoamine Oxidase.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nL&agrave;m giảm triệu chứng của cảm c&uacute;m như sốt, đau v&agrave; xung huyết mũi.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nD&ugrave;ng 1 hoặc 2 vi&ecirc;n/lần, c&oacute; thể d&ugrave;ng tới 4 lần/ng&agrave;y.<br />\r\nLiều tối đa h&agrave;ng ng&agrave;y: 8 vi&ecirc;n trong 24 giờ, c&aacute;ch nhau 4 giờ. Tối đa sử dụng trong 7 ng&agrave;y.<br />\r\n<strong>Thận trọng:</strong><br />\r\nBệnh nh&acirc;n rối loạn/suy giảm chức năng gan &amp; thận (kh&ocirc;ng d&ugrave;ng chung với c&aacute;c thuốc c&oacute; chứa Paracetamol kh&aacute;c).<br />\r\nPhụ nữ c&oacute; thai v&agrave; phụ nữ đang cho con b&uacute; .<br />\r\nTham khảo &yacute; kiến b&aacute;c sỹ đối với những bệnh nh&acirc;n c&oacute;: Bệnh tim mạch, cường gi&aacute;p, Glaucoma g&oacute;c đ&oacute;ng&hellip;<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Paracetamol 500mg, Caffeine 25mg, Phenylephrine hydrochloride5mg, t&aacute; dược vừa đủ.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 15 vỉ x 12 vi&ecirc;n&nbsp;<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Anh<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;GlaxoSmithKline</p>', 1, 1, 'hộp', 'Anh', '2021-06-21 08:42:47', '2021-07-12 02:30:16'),
(16, 'Goldream', 'goldream', 'P11051.jpg', 175000, 0, 'Thực phẩm bảo vệ sức khỏe hỗ trợ giúp ngủ ngon, bớt mệt mỏi khi thức dậy Goldream giúp trị chứng mất ngủ kinh niên, lo âu, căng thẳng, suy nhược thần kinh hay chứng trầm cảm', '<p><strong>Th&agrave;nh phần</strong><br />\r\n&ndash; Chiết xuất Nữ lang: 300mg<br />\r\n&ndash; Cao B&igrave;nh v&ocirc;i: 160mg (tương đương 1440mg dược liệu)<br />\r\n&ndash; Cao Trinh nữ: 30mg (tương đương 345mg dược liệu)<br />\r\n&ndash; Cao l&aacute; Sen: 30mg (tương đương 235mg dược liệu)<br />\r\n&ndash; Magi&ecirc; (dưới dạng Magnesium lactate dihydrate): 6mg<br />\r\n&ndash; Vitamin B6 (Pyridoxine hydrochloride): 1mg<br />\r\nPhụ liệu: Lactose, magnesium stearate, m&agrave;ng bao phim m&agrave;u trắng (opadry AMB white) vừa đủ</p>\r\n\r\n<p><strong>C&ocirc;ng dụng</strong><br />\r\nGi&uacute;p an thần, giảm stress v&agrave; căng thẳng thần kinh, hỗ trợ đem lại giấc ngủ tự nhi&ecirc;n v&agrave; cải thiện chất lượng giấc ngủ</p>\r\n\r\n<p><strong>Đối tượng sử dụng</strong><br />\r\nNgười trưởng th&agrave;nh kh&oacute; ngủ, mất ngủ với biểu hiện bị stress v&agrave; mệt mỏi.<br />\r\nNgười bị suy nhược thần kinh, ngủ kh&ocirc;ng ngon giấc</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng</strong><br />\r\nNgười trưởng th&agrave;nh kh&oacute; ngủ, mất ngủ với biểu hiện bị stress v&agrave; mệt mỏi.<br />\r\nNgười bị suy nhược thần kinh, ngủ kh&ocirc;ng ngon giấc</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc;, tho&aacute;ng, nhiệt độ dưới 300<br />\r\nC v&agrave; tr&aacute;nh &aacute;nh nắng trực tiếp</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 2 vỉ x 10 vi&ecirc;n</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>', 1, 4, 'hộp', 'Việt Nam', '2021-06-21 08:44:28', '2021-06-27 07:06:00'),
(23, 'Hoạt huyết Nhất Nhất', 'hoat-huyet-nhat-nhat', 'P00641.jpg', 99000, 0, 'Hoạt huyết Nhất Nhất trị các chứng huyết hư, ứ trệ. Phòng ngừa và điều trị thiểu năng tuần hoàn não, ngoại vi. Hỗ trợ phòng ngừa và điều trị xơ vữa động mạch, bệnh mạch vành, nghẽn mạch, tai biến mạch máu não.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nPhụ nữ c&oacute; thai, người đang chảy m&aacute;u.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nTrị c&aacute;c chứng huyết hư, ứ trệ. Ph&ograve;ng ngừa v&agrave; điều trị thiểu năng tuần ho&agrave;n n&atilde;o (mệt mỏi,&nbsp; đau đầu, ch&oacute;ng mặt, mất thăng bằng, hoa mắt, mất ngủ, suy giảm tr&iacute; nhớ, suy nhược thần kinh), thiểu năng tuần ho&agrave;n ngoại vi (đau mỏi vai g&aacute;y, t&ecirc; cứng cổ, đau c&aacute;ch hồi, đau cơ, t&ecirc; b&igrave; ch&acirc;n tay). Hỗ trợ ph&ograve;ng ngừa v&agrave; điều trị xơ vữa động mạch, nghẽn mạch, tai biến mạch m&aacute;u n&atilde;o.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNg&agrave;y 2 lần, mỗi lần&nbsp; 2 vi&ecirc;n. Với bệnh m&atilde;n t&iacute;nh n&ecirc;n uống thuốc li&ecirc;n tục &iacute;t nhất 3 th&aacute;ng.<br />\r\n<strong>Thận trọng:</strong><br />\r\nNgười c&oacute; rối loạn đ&ocirc;ng m&aacute;u, đang xuất huyết, người huyết &aacute;p thấp.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong><br />\r\n672mg cao kh&ocirc; tương đương: Đương quy 1,50g, thục địa 1,50g, Xuy&ecirc;n khung 0,75g, ngưu tất 1,50g, &Iacute;ch mẫu 1,50g, X&iacute;ch thược 0,75g. T&aacute; dược vừa đủ 1 vi&ecirc;n.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 3 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Dược phẩm Nhất Nhất</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:24:02', '2021-06-27 06:34:29'),
(17, 'SRM Dermoviva Oil', 'srm-dermoviva-oil', 'P13453.jpg', 189000, 179000, 'Sữa rửa mặt tẩy da chết tinh chất trà xanh Dermoviva Oil Control Face Scrub với chiết xuất từ tràm trà, hạt nho và chanh giúp làm sạch da hiệu quả, tiêu diệt các vi khuẩn gây mụn đồng thời kiểm soát dầu trên da cho làn da khỏe đẹp mịn màng và không còn bóng nhờn.', '<p><strong>Th&agrave;nh phần:</strong>&nbsp;Aqua, Ammonium Lauryl Sulfate, Bambusa Arundinacea Stem Extract, Citric Acid,&hellip;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong>&nbsp;Với chiết xuất từ tr&agrave;m tr&agrave;, hạt nho, v&agrave; chanh cho da dầu, gi&uacute;p kiểm so&aacute;t dầu tr&ecirc;n da, giảm mụn, cho l&agrave;n da mịn m&agrave;ng.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong>&nbsp;Lấy một lượng vừa đủ thoa đều l&ecirc;n to&agrave;n khu&ocirc;n mặt, tr&aacute;nh v&ugrave;ng mắt v&agrave; m&ocirc;i, nhẹ nh&agrave;ng massage rồi rửa lại với nước ấm.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;150ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;U.A.E</p>\r\n\r\n<p><strong>Sản xu&acirc;t tại:</strong>&nbsp;U.A.E</p>', 1, 2, 'tuýt', 'U.A.E', '2021-06-21 08:45:44', '2021-06-27 06:53:54'),
(18, 'SRM E100', 'srm-e100', 'P12933.jpg', 24000, 0, 'Sữa rửa mặt tinh chất sữa bò E100 với thành phần chứa tinh chất sữa bò tươi có khả năng loại bỏ vẩy tế bào khô, diệt khuẩn gây mụn cám và làm sạch sâu bề mặt da, cho bạn làn da tươi trẻ sáng mịn.', '<p><strong>Th&agrave;nh phần:</strong>&nbsp;Water, Stearic Acid, Myristic Acid, Ceteareth 25,&hellip;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:&nbsp;</strong>Loại bỏ vẩy tế b&agrave;o kh&ocirc;, diệt khuẩn g&acirc;y mụn c&aacute;m v&agrave; l&agrave;m sạch s&acirc;u bề mặt da, cho bạn l&agrave;n da tươi trẻ s&aacute;ng mịn.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong>&nbsp;L&agrave;m ướt mặt, cho một &iacute;t sữa rửa mặt v&agrave;o l&ograve;ng b&agrave;n tay, tạo bọt, thoa nhẹ nh&agrave;ng tr&ecirc;n da mặt v&agrave; m&aacute;t xa nhẹ, tr&aacute;nh v&ugrave;ng mắt. Rửa sạch mặt với nước. Th&iacute;ch hợp sử dụng h&agrave;ng ng&agrave;y cho mọi loại da.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nơi tho&aacute;ng m&aacute;t, tr&aacute;nh nhiệt độ cao v&agrave; &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;80ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 2, 'tuýt', 'Việt Nam', '2021-06-21 08:46:41', '2021-07-12 02:37:56'),
(19, 'Ích Niệu Khang', 'ich-nieu-khang', 'P15718.jpg', 150000, 0, 'Thực phẩm bảo vệ sức khỏe hỗ trợ giảm tiểu đêm, tiểu nhiều lần, tiểu són Ích Niệu Khang giúp giảm chứng rối loạn tiểu tiện: Tiểu đêm, tiểu nhiều lần, tiểu không kiểm soát (không tự chủ), tiểu rắt, tiểu són.', '<p>Th&agrave;nh phần: Mỗi vi&ecirc;n chứa Cao đỗ trọng 350mg, L-Carnitine fumarate 150mg, Go-less (hỗn hợp chứa 87.5% chiết xuất hạt b&iacute; ng&ocirc; v&agrave; 12.5% chiết xuất mầm đậu tương) 25mg, phụ liệu</p>\r\n\r\n<p>C&ocirc;ng dụng: Hỗ trợ giảm chứng rối loạn tiểu tiện: Tiểu đ&ecirc;m, tiểu nhiều lần, tiểu kh&ocirc;ng kiểm so&aacute;t (kh&ocirc;ng tự chủ), tiểu rắt, tiểu s&oacute;n</p>\r\n\r\n<p>Đối tượng sử dụng: Người bị rối loạn tiểu tiện do b&agrave;ng quan k&iacute;ch th&iacute;ch (b&agrave;ng quang hoạt động qu&aacute; mức &ndash; OAB), tiểu đ&ecirc;m, tiểu nhiều lần, tiểu kh&ocirc;ng kiểm so&aacute;t (kh&ocirc;ng tự chủ), tiểu rắt, tiểu s&oacute;n</p>\r\n\r\n<p>Hướng dẫn sử dụng:<br />\r\nUống ng&agrave;y 2 lần, mỗi lần 2-3 vi&ecirc;n (10 ng&agrave;y đầu uống 3 vi&ecirc;n/lần x 2 lần/ng&agrave;y, từ ng&agrave;y 11 uống 2 vi&ecirc;n/lần x 2 lần/ng&agrave;y)<br />\r\nN&ecirc;n uống c&ugrave;ng nước ấm trước bữa ăn 30 ph&uacute;t hoặc sau khi ăn 1 giờ<br />\r\nC&oacute; thể sử dụng sản phẩm l&acirc;u d&agrave;i, c&oacute; thể sử dụng c&ugrave;ng thuốc t&acirc;y<br />\r\nN&ecirc;n sử dụng li&ecirc;n tục một đợt từ 3-6 th&aacute;ng để c&oacute; kết quả tốt nhất</p>\r\n\r\n<p>Bảo quản: Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp</p>\r\n\r\n<p>Sản xuất tại: Việt Nam</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-21 08:47:44', '2021-06-27 06:46:01'),
(21, 'Miếng dán hạ sốt Kool Fever', 'mieng-dan-ha-sot-kool-fever', 'P17801.jpg', 15000, 0, 'Miếng dán hạ sốt trẻ em Kool Fever là miếng dán được yêu thích nhất Nhật Bản giúp hạ nhiệt hiệu quả, an toàn với trẻ nhỏ, không kích ứng, đem lại cảm giác dễ chịu cho bé.', '<p><strong>C&ocirc;ng dụng:</strong>&nbsp;Koolfever hạ sốt ho&agrave;n to&agrave;n tự nhi&ecirc;n dựa v&agrave;o cơ chế hấp thụ nhiệt từ v&ugrave;ng da n&oacute;ng sốt v&agrave; khuếch t&aacute;n ra ngo&agrave;i qua lỗ th&ocirc;ng hơi tr&ecirc;n lớp vải, gi&uacute;p hạ nhiệt độ tr&ecirc;n da khoảng 2 độ C.<br />\r\nBổ sung c&aacute;c hạt l&agrave;m m&aacute;t v&agrave;o lớp hydrogel d&agrave;y gi&uacute;p hấp thu v&agrave; khuếch t&aacute;n nhiệt nhanh hơn, hiệu quả hơn, gi&uacute;p hạ nhiệt độ tr&ecirc;n bề mặt da l&ecirc;n đến 2 độ C, tạo cảm gi&aacute;c dễ chịu cho người sử dụng. Hạt l&agrave;m m&aacute;t l&agrave; c&ocirc;ng nghệ độc quyền của Kobayashi, chỉ c&oacute; ở Miếng d&aacute;n hạ sốt Koolfever.<br />\r\nHiệu quả l&agrave;m m&aacute;t được duy tr&igrave; ổn định trong suốt 8 tiếng nhờ lớp gel d&agrave;y chứa lượng nước l&ecirc;n đến 77%.&nbsp;<br />\r\nMiếng d&aacute;n hạ sốt Koolfever c&oacute; c&ocirc;ng thức an to&agrave;n cho da trẻ nhỏ, kh&ocirc;ng chứa cồn, kh&ocirc;ng g&acirc;y k&iacute;ch ứng cho da trẻ; độ b&aacute;m d&iacute;nh vừa phải, kh&ocirc;ng để lại căn gel tr&ecirc;n da v&agrave; kh&ocirc;ng đau khi gỡ.</p>\r\n\r\n<p><strong>Th&agrave;nh phần:</strong>&nbsp;Hydrogel, hạt l&agrave;m m&aacute;t, hợp chất thơm</p>\r\n\r\n<p><strong>Đối tượng</strong><br />\r\nBị sốt do vi khuẩn, virus, vi&ecirc;m đường h&ocirc; hấp, ti&ecirc;m chủng, mọc răng, say nắng&hellip;<br />\r\nĐau đầu, đau răng<br />\r\nDa bị ch&aacute;y nắng</p>\r\n\r\n<p><strong>C&aacute;ch d&ugrave;ng:</strong>&nbsp;Gỡ bỏ miếng phim v&agrave; d&aacute;n l&ecirc;n v&ugrave;ng da n&oacute;ng sốt (tr&aacute;n, n&aacute;ch, bẹn, g&aacute;y, v&ugrave;ng da bị ch&aacute;y nắng&hellip;); c&oacute; thể cắt miếng d&aacute;n theo k&iacute;ch cỡ ph&ugrave; hợp (nếu cần).</p>\r\n\r\n<p><em>Lưu &yacute;:</em><br />\r\nKh&ocirc;ng sử dụng Koolfever l&ecirc;n v&ugrave;ng da bị k&iacute;ch ứng hoặc bị tổn thương<br />\r\nKh&ocirc;ng lấy hạt l&agrave;m m&aacute;t ra ngo&agrave;i v&igrave; hạt c&oacute; thể bị vỡ v&agrave; nước trong hạt bắn v&agrave;o mắt<br />\r\nKh&ocirc;ng để trẻ nhỏ ăn hoặc d&aacute;n v&agrave;o mũi, miệng<br />\r\nNếu c&oacute; triệu chứng k&iacute;ch ứng khi sử dụng sản phẩm, h&atilde;y ngưng sử dụng v&agrave; gặp b&aacute;c sỹ để được kh&aacute;m v&agrave; chữa trị</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 8 g&oacute;i x 2 miếng</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Nhật Bản</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Nhật Bản</p>', 1, 3, 'cái', 'Nhật Bản', '2021-06-21 09:02:32', '2021-07-12 02:04:30'),
(24, 'GazGo', 'gazgo', 'P15336.jpg', 178000, 0, 'GazGo (simethicone 200mg) điều trị các triệu chứng liên quan đến tích tụ hơi (ở vùng dạ dày ruột) như là khó chịu hoặc căng đau, trướng bụng và đầy hơi.', '<p><strong>Th&agrave;nh phần hoạt t&iacute;nh:</strong>&nbsp;Simethicon 200mg</p>\r\n\r\n<p><strong>Chỉ định</strong></p>\r\n\r\n<p>Điều trị c&aacute;c triệu chứng li&ecirc;n quan đến t&iacute;ch tụ hơi (ở v&ugrave;ng dạ d&agrave;y ruột) như l&agrave; kh&oacute; chịu hoặc căng đau, trướng bụng v&agrave; đầy hơi.</p>\r\n\r\n<p>Được d&ugrave;ng dưới dạng liệu ph&aacute;p hỗ trợ trong nhiều trường hợp m&agrave; việc t&iacute;ch tụ hơi c&oacute; thể g&acirc;y n&ecirc;n c&aacute;c vấn đề như l&agrave; ứ hơi sau phẫu thuật, chứng nuốt hơi, chứng kh&oacute; ti&ecirc;u chức năng, lo&eacute;t ti&ecirc;u h&oacute;a, ruột kết kh&oacute; chịu hoặc bị co thắt mạnh hoặc vi&ecirc;m t&uacute;i thừa.</p>\r\n\r\n<p><strong>Chống chỉ định:</strong>&nbsp;Mẫn cảm hoặc kh&ocirc;ng dung nạp với thuốc.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng</strong></p>\r\n\r\n<p>Người trưởng th&agrave;nh: 1 vi&ecirc;n/lần x 1-2 lần/ng&agrave;y, sau bữa ăn hoặc khi đi ngủ hoặc theo hướng dẫn của b&aacute;c sĩ.</p>\r\n\r\n<p>Trẻ em: Theo chỉ dẫn của b&aacute;c sĩ.</p>\r\n\r\n<p>Kh&ocirc;ng được nhai vi&ecirc;n thuốc.</p>\r\n\r\n<p><strong>T&aacute;c dụng phụ:</strong>&nbsp;Th&ocirc;ng b&aacute;o cho b&aacute;c sĩ nếu gặp phải t&aacute;c dụng phụ n&agrave;o.</p>\r\n\r\n<p><strong>Thận trọng:</strong>&nbsp;Kh&ocirc;ng n&ecirc;n d&ugrave;ng cho phụ nữ c&oacute; thai v&agrave; phụ nữ đang cho con b&uacute; m&agrave; kh&ocirc;ng c&oacute; chỉ dẫn y tế.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Dưới 30<sup>o</sup>C ở nơi kh&ocirc; r&aacute;o, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 3 vỉ x 10 vi&ecirc;n nang mềm</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Th&aacute;i Lan</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Th&aacute;i Lan</p>\r\n\r\n<p><em>*Pharmacity cam kết chỉ b&aacute;n sản phẩm c&ograve;n d&agrave;i hạn sử dụng.</em></p>', 1, 1, 'hộp', 'Thái Lan', '2021-06-24 10:25:07', '2021-07-12 02:31:04'),
(25, 'Normagut', 'normagut', 'P01327.jpg', 210000, 0, 'Normagut 250mg thiết lập và duy trì hoạt động hiệu quả của đường ruột và cân bằng hệ vi sinh đường ruột. Phòng và điều trị tiêu chảy do Clostridium Difficile, do đi du lịch hay do dùng kháng sinh.', '\"1. Chống chỉ định:\r\nMẫn cảm với nấm men hay bất cứ thành phần nào của thuốc.\r\nBệnh nhân suy giảm miễn dịch như nhiễm HIV, cấy ghép cơ quan, xạ trị.\r\n\r\n2. Hướng dẫn sử dụng:\r\nChỉ định:\r\nThiết lập và duy trì hoạt động hiệu quả của đường ruột và cân bằng hệ vi sinh đường ruột.\r\nPhòng và điều trị tiêu chảy do Clostridium Difficile, do đi du lịch hay do dùng kháng sinh.\r\nLiều dùng và cách dùng:\r\nNgười lớn, trẻ trên 12 tuổi: Thiết lập và duy trì hoạt động hiệu quả của đường ruột và cân bằng hệ vi sinh đường ruột 1 viên x 1 – 2 lần/ngày.\r\nTiêu chảy 1 viên x 1 – 2 lần/ngày.\r\nNgăn ngừa tiêu chảy do đi du lịch 1 viên x 1 – 2 lần/ngày, bắt đầu từ 5 ngày trước khi đi du lịch.\r\nTrẻ 2 – 12 tuổi: 1 viên x 1 – 2 lần/ngày.\r\nThận trọng:\r\nNếu sau 2 ngày điều trị vẫn bị tiêu chảy, cần tham khảo ý kiến Bác sĩ.\r\nBảo quản: \r\nBảo quản ở nhiệt độ không quá 30 độ C.\r\n\r\n3. Thông tin thuốc:\r\nThành phần:\r\nMen Saccharomyces Boulardii 250mg, tá dược vừa đủ 1 viên nang.\r\nQuy cách đóng gói: Hộp 3 vỉ x 10 viên\r\nXuất xứ thương hiệu: Thái Lan\r\nNhà sản xuất: Đức\r\n\r\nĐể xa tầm tay trẻ em. Đọc kỹ hướng dẫn sử dụng trước khi dùng.\r\nNếu cần thêm thông tin xin hỏi ý kiến Bác sĩ.\"', 1, 1, 'hộp', 'Thái Lan', '2021-06-24 10:26:04', '2021-06-24 10:26:04'),
(26, 'Phytilax', 'phytilax', 'P02886.jpg', 20000, 0, 'Phytilax được dùng để thông mật, nhuận trường, trị táo bón nói chung và đặc biệt cho người già thiểu năng gan mật.', '<p>&quot;1. Chống chỉ định: Kh&ocirc;ng d&ugrave;ng cho người đang bị ti&ecirc;u chảy, người vi&ecirc;m tắc mật, người thể h&agrave;n, dương hư h&agrave;n, tiểu đường. Kh&ocirc;ng d&ugrave;ng cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute; 2. Hướng dẫn sử dụng: Chỉ định: Th&ocirc;ng mật, nhuận trường, trị t&aacute;o b&oacute;n n&oacute;i chung v&agrave; đặc biệt cho người gi&agrave; thiểu năng gan mật Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng: Nhuận trường: 1 vi&ecirc;n uống sau bữa ăn tối, ng&agrave;y 1 lần Xổ: 2-3 vi&ecirc;n uống sau bữa ăn tối, ng&agrave;y 1 lần Bảo quản: Nơi kh&ocirc;, nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C 3. Th&ocirc;ng tin thuốc: Th&agrave;nh phần: Cho 1 vi&ecirc;n bao đường chứa: Bột mật heo&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;0.100g L&ocirc; hội&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;0.050g Bột Thảo quyết minh&hellip;&hellip;&hellip;.0.050g T&aacute; dược vừa đủ 1 vi&ecirc;n bao đường Quy c&aacute;ch đ&oacute;ng g&oacute;i: Hộp 3 vỉ x 10 vi&ecirc;n bao đường Xuất xứ thương hiệu: Việt Nam Nh&agrave; sản xuất: Dược phẩm S&agrave;i G&ograve;n Sagopha Để xa tầm tay trẻ em. Đọc kỹ hướng dẫn sử dụng trước khi d&ugrave;ng. Nếu cần th&ecirc;m th&ocirc;ng tin xin hỏi &yacute; kiến B&aacute;c sĩ.&quot;</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:27:22', '2021-06-27 06:46:25'),
(27, 'Choliver', 'choliver', 'P11299.jpg', 40000, 0, 'Hoạt chất: Cao khô actisô 18,43mg; Cao khô mật heo 18,42mg; Bột nghệ 50mg\r\nCông dụng: Điều trị rối loạn đường mật,viêm gan\r\nĐối tượng sử dụng: Người lớn và trẻ em\r\nHình thức: Viên nén bao đường\r\nThương hiệu: Dược phẩm Hậu Giang (Việt Nam)\r\n*Thuốc chỉ dùng theo đơn của bác sĩ.', '<p><strong>Th&agrave;nh phần</strong><br />\r\nCao kh&ocirc; actis&ocirc; (Extractum siccum Cynarae scolymi) &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.. 18,42mg<br />\r\n(Tương đương cao actis&ocirc; tỉ lệ 1/30 25 mg, tương đương l&aacute; actis&ocirc; tươi 750 mg)<br />\r\nCao kh&ocirc; mật heo (Extractum siccum Fellis) &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.18,42mg<br />\r\n(Tương đương cao mật heo tỉ lệ 1/10 25 mg, tương đương mật heo tươi 250 mg)<br />\r\nBột nghệ (Pulvis Curcumae longae) &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip; 50mg<br />\r\n(Tương đương củ nghệ kh&ocirc; 65 mg)<br />\r\nT&aacute; dược vừa đủ &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip; 1 vi&ecirc;n<br />\r\n(Đường trắng, magnesi carbonat, tinh bột m&igrave;, talc, g&ocirc;m arabic, PVA, titan dioxyd,<br />\r\nnipagin, g&ocirc;m laque, oxyd sắt đỏ, oxyd sắt đen, s&aacute;p carnauba, parafin rắn).</p>\r\n\r\n<p><strong>Chỉ định</strong>&nbsp;(Thuốc d&ugrave;ng cho bệnh g&igrave;?)<br />\r\nPh&ograve;ng v&agrave; điều trị rối loạn đường mật, vi&ecirc;m mật, lợi tiểu. Điều trị chứng kh&oacute; ti&ecirc;u: trướng bụng, chậm ti&ecirc;u h&oacute;a, ợ, đầy hơi, buồn n&ocirc;n do suy giảm chức năng gan. Điều trị vi&ecirc;m gan, v&agrave;ng da. Chống mụn nhọt, ngứa, nổi mề đay. Gi&uacute;p ăn ngon miệng. Chống t&aacute;o b&oacute;n.</p>\r\n\r\n<p><strong>Chống chỉ định</strong>&nbsp;(Khi n&agrave;o kh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y?)<br />\r\nQu&aacute; mẫn với bất cứ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng</strong><br />\r\nNgười lớn: 2 &ndash; 4 vi&ecirc;n x 3 lần/ ng&agrave;y.<br />\r\nTrẻ em: 1 &ndash; 2 vi&ecirc;n x 3 lần/ ng&agrave;y.</p>\r\n\r\n<p><strong>T&aacute;c dụng phụ</strong><br />\r\nTrường hợp bị ti&ecirc;u chảy hay đau bụng n&ecirc;n gi&aacute;n đoạn việc d&ugrave;ng thuốc.<br />\r\nTh&ocirc;ng b&aacute;o cho b&aacute;c sĩ những t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải khi sử dụng thuốc.<br />\r\nQU&Aacute; LIỀU<br />\r\nChưa t&igrave;m thấy t&agrave;i liệu.</p>\r\n\r\n<p><strong>Thận trọng</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng thuốc)<br />\r\nKh&ocirc;ng n&ecirc;n d&ugrave;ng nếu bị nghẽn đường mật, suy gan. Người bị bệnh đ&aacute;i th&aacute;o đường.<br />\r\nPHỤ NỮ C&Oacute; THAI V&Agrave; CHO CON B&Uacute;<br />\r\nKh&ocirc;ng được d&ugrave;ng thuốc cho phụ nữ mang thai. Thuốc chưa c&oacute; nghi&ecirc;n cứu tr&ecirc;n phụ nữ cho con b&uacute;.<br />\r\nL&Aacute;I XE V&Agrave; VẬN H&Agrave;NH M&Aacute;Y M&Oacute;C<br />\r\nThuốc kh&ocirc;ng ảnh hưởng tới khả năng l&aacute;i xe v&agrave; vận h&agrave;nh m&aacute;y m&oacute;c.</p>\r\n\r\n<p><strong>Tương t&aacute;c thuốc</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng chung thuốc với thực phẩm hoặc thuốc kh&aacute;c)<br />\r\nChưa t&igrave;m thấy t&agrave;i liệu</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Để nơi kh&ocirc; r&aacute;o, tr&aacute;nh &aacute;nh s&aacute;ng, bảo quản nhiệt độ dưới 30 độ C.</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 chai x 100 vi&ecirc;n.</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Dược phẩm Hậu Giang</p>\r\n\r\n<p><strong>Nơi sản xuất:</strong>&nbsp;Việt Nam</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:28:14', '2021-06-27 06:38:08');
INSERT INTO `san_pham` (`id`, `ten_san_pham`, `ten_url`, `hinh_san_pham`, `gia_san_pham`, `giam_gia`, `mo_ta_tom_tat`, `chi_tiet`, `trang_thai`, `ma_loai`, `don_vi`, `nguon_goc`, `created_at`, `updated_at`) VALUES
(28, 'Kim tiền thảo', 'kim-tien-thao', 'P11096.jpg', 54700, 0, 'Hoạt chất: Cao Kim tiền thảo 167mg\r\nCông dụng: Giúp lợi tiểu, hỗ trợ tăng bài tiết cặn sỏi, giúp hạn chế sỏi tiết niệu\r\nĐối tượng sử dụng: Dùng cho người bị sỏi bàng quang, sỏi thận, sỏi niệu quản.\r\nHình thức: Viên nén bao đường\r\nThương hiệu: Dược Hậu Giang(Việt Nam)', '<p><strong>Th&agrave;nh phần</strong><br />\r\nH&Agrave;M LƯỢNG TRONG 1 VI&Ecirc;N:<br />\r\nCao Kim tiền thảo &hellip;&hellip;&hellip;&hellip;&hellip; 167 mg<br />\r\n(Tương đương 1,5 g kim tiền thảo kh&ocirc;)<br />\r\nPhụ liệu: Đường trắng, tinh bột m&igrave;, chất chống đ&ocirc;ng v&oacute;n (talc &ndash; INS 553(iii)), lactose monohydrat, chất độn (microcrystalline cellulose M101 &ndash; INS 460(i)), chất kết d&iacute;nh (g&ocirc;m arabic &ndash; INS 414), m&agrave;u thực phẩm nh&acirc;n tạo (oxyd sắt đen &ndash; INS 172(i), titan dioxyd &ndash; INS 171, oxyd sắt đỏ &ndash; INS 172(ii), m&agrave;u amaranth &ndash; INS 123), magnesi stearat, chất tạo m&agrave;ng bao (PVA 205 &ndash; INS 1203), parafin rắn, gelatin, chất l&agrave;m b&oacute;ng (s&aacute;p carnauba &ndash; INS 903), chất bảo quản (methylparaben &ndash; INS 218).</p>\r\n\r\n<p><strong>Chỉ định</strong>&nbsp;(Thuốc d&ugrave;ng cho bệnh g&igrave;?)<br />\r\nD&ugrave;ng cho người bị sỏi b&agrave;ng quang, sỏi thận, sỏi niệu quản.<br />\r\nNgười nguy cơ bị sỏi đường tiết niệu, người b&iacute; tiểu, tiểu rắt, tiểu buốt.</p>\r\n\r\n<p><strong>Chống chỉ định</strong>&nbsp;(Khi n&agrave;o kh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y?)<br />\r\nKh&ocirc;ng sử dụng cho người mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của sản phẩm.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng</strong><br />\r\nNgười lớn: uống 5 vi&ecirc;n x 3 lần/ ng&agrave;y, uống sau khi ăn với nhiều nước.</p>\r\n\r\n<p><strong>T&aacute;c dụng phụ</strong><br />\r\nKh&ocirc;ng d&ugrave;ng cho người bị sỏi tiết niệu lớn, sỏi san h&ocirc;, sỏi đ&agrave;i bể thận, người bị u xơ tiền liệt tuyến nặng.</p>\r\n\r\n<p><strong>Thận trọng</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng thuốc)<br />\r\nThận trọng cho người đ&aacute;i th&aacute;o đường, người bị đau dạ d&agrave;y uống l&uacute;c no.</p>\r\n\r\n<p><strong>Tương t&aacute;c thuốc</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng chung thuốc với thực phẩm hoặc thuốc kh&aacute;c)<br />\r\nThực phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc, kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh.</p>\r\n\r\n<p><strong>Bảo quản</strong>: Nơi kh&ocirc;, nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C, tr&aacute;nh &aacute;nh s&aacute;ng</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i</strong>: Hộp 1 chai x 100 vi&ecirc;n</p>\r\n\r\n<p><strong>Thương hiệu</strong>: Dược Hậu Giang (Việt Nam)</p>\r\n\r\n<p><strong>Nơi sản xuất</strong>: Việt Nam</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:29:39', '2021-06-27 06:38:36'),
(29, 'Enterogermina', 'enterogermina', 'P00328.jpg', 140000, 0, 'Hoạt chất: 2 tỷ bào tử kháng đa kháng sinh Bacillus clausii\r\nCông dụng: Điều trị, phòng ngừa rối loạn vi khuẩn đường ruột, kém hấp thu vitamin…\r\nĐối tượng sử dụng: Người lớn và trẻ em\r\nHình thức: Hỗn dịch uống\r\nThương hiệu: Sanofi (Pháp)', '<p><strong>Th&agrave;nh phần</strong><br />\r\n&ndash; Th&agrave;nh phần dược chất: B&agrave;o tử kh&aacute;ng đa kh&aacute;ng sinh&nbsp;<em>Bacillus clausii</em>&nbsp;2 tỷ<br />\r\n&ndash; Th&agrave;nh phần t&aacute; dược: Nước cất vừa đủ 5ml</p>\r\n\r\n<p><strong>Chỉ định</strong>&nbsp;(Thuốc d&ugrave;ng cho bệnh g&igrave;?)<br />\r\n&ndash; Enterogermina được d&ugrave;ng để điều trị v&agrave; ph&ograve;ng ngừa rối loạn hệ vi khuẩn đường ruột v&agrave; bệnh l&yacute; k&eacute;m hấp thu vitamin nội sinh.<br />\r\n&ndash; Điều trị hỗ trợ để phục hồi hệ vi khuẩn đường ruột bị ảnh hưởng khi d&ugrave;ng kh&aacute;ng sinh hoặc ho&aacute; trị liệu.<br />\r\n&ndash; Rối loạn ti&ecirc;u h&oacute;a cấp v&agrave; mạn t&iacute;nh ở trẻ em do nhiễm độc hoặc rối loạn hệ vi khuẩn đường ruột v&agrave; k&eacute;m hấp thu vitamin.</p>\r\n\r\n<p><strong>Chống chỉ định</strong>&nbsp;(Khi n&agrave;o kh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y?)<br />\r\nTiền sử dị ứng với một trong c&aacute;c th&agrave;nh phần của thuốc.<br />\r\nTrong trường hợp nghi ngờ, h&atilde;y xin &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng, c&aacute;ch d&ugrave;ng</strong><br />\r\n<em><strong>Liều lượng</strong></em><br />\r\nNgười lớn: 2 &ndash; 3 ống/ ng&agrave;y<br />\r\nTrẻ em: 1 &ndash; 2 ống/ng&agrave;y<br />\r\nTrẻ nhỏ: 1 &ndash; 2 ống/ng&agrave;y<br />\r\nLưu &yacute; quan trọng: Kh&ocirc;ng d&ugrave;ng qu&aacute; liều khuyến c&aacute;o trước khi xin &yacute; kiến b&aacute;c sĩ.</p>\r\n\r\n<p><em><strong>C&aacute;ch d&ugrave;ng v&agrave; đường d&ugrave;ng</strong></em><br />\r\nLắc kỹ ống thuốc trước khi d&ugrave;ng. Xoắn vặn phần tr&ecirc;n để mở ống thuốc, uống thuốc trong ống hoặc h&ograve;a thuốc trong ống với sữa, nước tr&agrave; hoặc nước cam. Khi mở ống thuốc ra phải d&ugrave;ng trong thời gian ngắn để tr&aacute;nh bị hỏng thuốc, uống thuốc c&aacute;ch nhau đều đặn trong ng&agrave;y (khoảng 3 &ndash; 4 giờ). H&atilde;y xin &yacute; kiến b&aacute;c sĩ nếu bệnh t&aacute;i ph&aacute;t hoặc nếu ghi nhận gần đ&acirc;y c&oacute; sự thay đổi về đặc điểm của bệnh. Trong thời gian sử dụng kh&aacute;ng sinh, n&ecirc;n uống Enterogermlna xen kẽ giữa c&aacute;c liều d&ugrave;ng kh&aacute;ng sinh.</p>\r\n\r\n<p><em><strong>Thời gian điều trị</strong></em><br />\r\nLưu &yacute; quan trọng: chỉ sử dụng điều trị trong một thời gian ngắn.<br />\r\nNếu qu&ecirc;n sử dụng Enterogermina: Kh&ocirc;ng c&oacute; vấn đề g&igrave; đặc biệt. Tuy nhi&ecirc;n, điều n&ecirc;n nhớ l&agrave; việc sử dụng đ&uacute;ng v&agrave; đều đặn gi&uacute;p tăng hiệu quả điều trị.<br />\r\nKhi ngưng sử dụng Enterogermina: Kh&ocirc;ng c&oacute; vấn đề g&igrave; đặc biệt được ghi nhận, ngo&agrave;i việc k&eacute;m hiệu quả điều trị.</p>\r\n\r\n<p><strong>T&aacute;c dụng phụ</strong><br />\r\nMột v&agrave;i trường hợp phản ứng dị ứng như ph&aacute;t ban v&agrave; m&agrave;y đay đ&atilde; được b&aacute;o c&aacute;o. H&atilde;y th&ocirc;ng b&aacute;o cho b&aacute;c sĩ hoặc dược sĩ sự xuất hiện của bất kỳ t&aacute;c dụng kh&ocirc;ng mong muốn n&agrave;o kh&ocirc;ng được liệt k&ecirc; trong tờ hướng dẫn sử dụng n&agrave;y.<br />\r\nNhư bất kỳ thuốc n&agrave;o, Enterogermina c&oacute; thể g&acirc;y một số t&aacute;c dụng kh&ocirc;ng mong muốn tr&ecirc;n một số người.</p>\r\n\r\n<p>QU&Aacute; LIỀU V&Agrave; C&Aacute;CH XỬ TR&Iacute;<br />\r\nĐến nay, kh&ocirc;ng c&oacute; trường hợp l&acirc;m s&agrave;ng qu&aacute; liều n&agrave;o được b&aacute;o c&aacute;o.</p>\r\n\r\n<p><strong>Thận trọng</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng thuốc)<br />\r\nC&oacute; thể nh&igrave;n thấy &ndash; c&aacute;c hạt nhỏ trong ống thuốc Enterogermina do sự tập hợp của c&aacute;c b&agrave;o tử&nbsp;<em>Bacillus clausii</em>, điều đ&oacute; chỉ ra rằng thuốc kh&ocirc;ng bị thay đổi chất lượng. Kh&ocirc;ng c&oacute; chống chỉ định sử dụng Enterogermina tr&ecirc;n bệnh nh&acirc;n bị ti&ecirc;u chảy mỡ.<br />\r\nLắc kỹ ống thuốc trước khi uống.<br />\r\nThận trọng khi d&ugrave;ng trong thời gian điều trị với thuốc kh&aacute;ng sinh, n&ecirc;n uống Enterogermina xen kẽ giữa c&aacute;c liều d&ugrave;ng kh&aacute;ng sinh.<br />\r\nĐể thuốc ngo&agrave;i tầm tay trẻ em.<br />\r\nTrong trường hợp nghi ngờ, h&atilde;y xin &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</p>\r\n\r\n<p>PHỤ NỮ C&Oacute; THAI V&Agrave; CHO CON B&Uacute;<br />\r\nEnterogermina c&oacute; thể được d&ugrave;ng cho phụ nữ c&oacute; thai v&agrave; nu&ocirc;i con bằng sữa mẹ.<br />\r\nNhư một nguy&ecirc;n tắc chung, h&atilde;y lu&ocirc;n xin &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ trước khi sử dụng bất kỳ thuốc g&igrave; nếu đang mang thai hoặc đang cho con b&uacute;.</p>\r\n\r\n<p>L&Aacute;I XE V&Agrave; VẬN H&Agrave;NH M&Aacute;Y M&Oacute;C<br />\r\nKh&ocirc;ng ảnh hưởng hoặc ảnh hưởng kh&ocirc;ng đ&aacute;ng kể.</p>\r\n\r\n<p><strong>Tương t&aacute;c thuốc</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng chung thuốc với thực phẩm hoặc thuốc kh&aacute;c)<br />\r\nNếu đang sử dụng bất kỳ thuốc n&agrave;o kh&aacute;c, h&atilde;y xin &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.<br />\r\nKh&ocirc;ng c&oacute; tương t&aacute;c thuốc khi d&ugrave;ng chung với c&aacute;c thuốc kh&aacute;c.<br />\r\nThức ăn, thức uống kh&ocirc;ng ảnh hưởng đến hiệu quả của Enterogermina.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C. Khi mở ống thuốc phải d&ugrave;ng trong thời gian ngắn.</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 20 ống (5 ml)</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Sanofi (Ph&aacute;p)</p>\r\n\r\n<p><strong>Nơi sản xuất:</strong>&nbsp;&Yacute;</p>', 1, 1, 'hộp', 'Pháp', '2021-06-24 10:30:52', '2021-07-12 02:31:43'),
(30, 'Odistad', 'odistad', 'P14147.jpg', 320000, 289000, 'Odistad 120mg kết hợp cùng với chế độ ăn giảm nhẹ calo trong điều trị béo phì và ngừa tăng cân trở lại ở bệnh nhân có chỉ số khối cơ thể (BMI) ≥ 30 kg/m2 hoặc bệnh nhân thừa cân (BMI ≥ 28 kg/m²) kèm theo các yếu tố nguy cơ (như tăng huyết áp, tiểu đường, tăng lipid huyết).', '<p><strong>1. Chống chỉ định:</strong><br />\r\nQu&aacute; mẫn với orlistat hoặc với bất cứ th&agrave;nh phần n&agrave;o của thuốc<br />\r\nHội chứng k&eacute;m hấp thu mạn t&iacute;nh hoặc bệnh ứ mật</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:<br />\r\nChỉ định:&nbsp;</strong>Kết hợp c&ugrave;ng với chế độ ăn giảm nhẹ calo trong điều trị b&eacute;o ph&igrave; v&agrave; ngừa tăng c&acirc;n trở lại ở bệnh nh&acirc;n c&oacute; chỉ số khối cơ thể (BMI) &ge; 30 kg/m2 hoặc bệnh nh&acirc;n thừa c&acirc;n (BMI &ge; 28 kg/m&sup2;) k&egrave;m theo c&aacute;c yếu tố nguy cơ (như tăng huyết &aacute;p,&nbsp;tiểu đường, tăng lipid huyết)<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nĐiều trị b&eacute;o ph&igrave; v&agrave; ngừa tăng c&acirc;n trở lại ở người lớn v&agrave; thanh thiếu ni&ecirc;n từ 12 tuổi trở l&ecirc;n: 120 mg x 3 lần/ng&agrave;y trong mỗi bữa ăn ch&iacute;nh c&oacute; chất b&eacute;o<br />\r\nNgười thừa c&acirc;n từ 18 tuổi trở l&ecirc;n: 60 mg x 3 lần/ng&agrave;y trong mỗi bữa ăn c&oacute; chất b&eacute;o<br />\r\n<strong>C&aacute;ch d&ugrave;ng:</strong><br />\r\nN&ecirc;n uống thuốc với nước ngay trước, trong khi ăn hoặc cho đến 1 giờ sau mỗi bữa ăn ch&iacute;nh. Nếu bỏ lỡ một bữa ăn hoặc bữa ăn kh&ocirc;ng c&oacute; chất b&eacute;o th&igrave; kh&ocirc;ng cần d&ugrave;ng orlistat<br />\r\nLiều d&ugrave;ng qu&aacute; 3 lần/ ng&agrave;y kh&ocirc;ng tăng th&ecirc;m lợi &iacute;ch<br />\r\nNếu bệnh nh&acirc;n kh&ocirc;ng thể giảm c&acirc;n sau 12 tuần điều trị với orlistat, n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ hoặc dược sĩ. Nếu cần c&oacute; thể ngưng điều trị<br />\r\n<strong>Bảo quản:</strong>&nbsp;Trong bao b&igrave; k&iacute;n, nơi kh&ocirc;. Nhiệt độ kh&ocirc;ng qu&aacute; 30<sup>o</sup>C</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong>&nbsp;Mỗi vi&ecirc;n nang chứa orlistat (dưới dạng vi hạt orlistat) 120mg<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 2 vỉ x 21 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Đức<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Stada</p>', 1, 1, 'hộp', 'Đức', '2021-06-24 10:32:06', '2021-06-27 06:40:37'),
(31, 'Gimfastnew 180', 'gimfastnew-180', 'P14199.jpg', 46000, 0, 'Gimfastnew 120mg điều trị chứng viêm mũi dị ứng như hắt hơi, chảy nước mũi, ngứa mũi, ngứa vòm miệng và họng, mắt ngứa đỏ và chảy nước mắt, mày đay vô căn mạn tính.', '<p><strong>Th&agrave;nh phần</strong><br />\r\nMỗi vi&ecirc;n chứa:<br />\r\nFexofenadin hydroclorid 180mg<br />\r\nT&aacute; dược vừa đủ</p>\r\n\r\n<p><strong>Chỉ định</strong>&nbsp;(Thuốc d&ugrave;ng cho bệnh g&igrave;?)<br />\r\nĐiều trị chứng vi&ecirc;m mũi dị ứng như hắt hơi, chảy nước mũi, ngứa mũi, ngứa v&ograve;m miệng v&agrave; họng, mắt ngứa đỏ v&agrave; chảy nước mắt.</p>\r\n\r\n<p><strong>Chống chỉ định</strong>&nbsp;(Khi n&agrave;o kh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y?)<br />\r\nDị ứng với bất cứ th&agrave;nh phần n&agrave;o của thuốc.<br />\r\nTrẻ em dưới 12 tuổi.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng</strong><br />\r\nNgười lớn v&agrave; trẻ từ 12 tuổi trở l&ecirc;n: Uống 1 vi&ecirc;n/lần, 1 lần/ng&agrave;y.<br />\r\nLiều cao hơn kh&ocirc;ng l&agrave;m tăng t&aacute;c dụng điều trị.<br />\r\nBệnh nh&acirc;n suy gan kh&ocirc;ng cần giảm liều.</p>\r\n\r\n<p><strong>T&aacute;c dụng phụ</strong><br />\r\nThường gặp nhức đầu, buồn ngủ, buồn n&ocirc;n, đau bụng kinh, kh&oacute; ti&ecirc;u, mệt mỏi.<br />\r\nTh&ocirc;ng b&aacute;o cho b&aacute;c sĩ nếu gặp phải c&aacute;c triệu chứng kh&ocirc;ng mong muốn.</p>\r\n\r\n<p><strong>Thận trọng</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng thuốc)<br />\r\nThận trọng với người lớn tuổi, người suy gan, suy thận<br />\r\nNgưng thuốc &iacute;t nhất 1-2 ng&agrave;y trước khi tiến h&agrave;nh c&aacute;c thử nghiệm kh&aacute;ng nguy&ecirc;n trong da.</p>\r\n\r\n<p><strong>Tương t&aacute;c thuốc</strong>&nbsp;(Những lưu &yacute; khi d&ugrave;ng chung thuốc với thực phẩm hoặc thuốc kh&aacute;c)<br />\r\nD&ugrave;ng c&aacute;ch 2 giờ với c&aacute;c thuốc giảm tiết acid nh&ocirc;m hay magnesi.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nhiệt độ dưới 30 độ C, tr&aacute;nh ẩm v&agrave; &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 2 vỉ x 10 vi&ecirc;n</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Agimexpharm (Việt Nam)</p>\r\n\r\n<p><strong>Nơi sản xuất:</strong>&nbsp;Việt Nam</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:33:25', '2021-06-27 06:46:59'),
(32, 'Voltaren', 'voltaren', 'P03076.jpg', 80000, 0, 'Voltaren 100mg điều trị các triệu chứng cho người mắc bệnh đau vai cấp, viêm gân, bệnh nhân Gout, đau lưng', '<p><strong>1. Chống chỉ định:</strong><br />\r\nLo&eacute;t dạ d&agrave;y &ndash; t&aacute; tr&agrave;ng tiến triển.<br />\r\nQu&aacute; mẫn cảm với th&agrave;nh phần thuốc.<br />\r\nTiền sử hen, mề đay, vi&ecirc;m mũi khi d&ugrave;ng NSAID kh&aacute;c.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị vi&ecirc;m đa khớp dạng thấp, vi&ecirc;m cột sống d&iacute;nh khớp, hư khớp, vi&ecirc;m khớp đốt sống, đau cột sống, bệnh thấp ngo&agrave;i khớp, gout cấp.<br />\r\nVi&ecirc;m v&agrave; sưng sau chấn thương hoặc sau phẫu thuật.<br />\r\nĐau bụng kinh, vi&ecirc;m phần phụ.<br />\r\nHỗ trợ trong nhiễm tr&ugrave;ng cấp tai, mũi, họng.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nD&ugrave;ng liều thấp nhất c&oacute; hiệu quả v&agrave; thời gian ngắn nhất c&oacute; thể.<br />\r\nNgười lớn: 100 &ndash; 150mg/ng&agrave;y.<br />\r\nTrẻ em: 0.5 &ndash; 2mg/kg/ng&agrave;y, chia 2 &ndash; 3 lần.<br />\r\n<strong>Thận trọng:</strong><br />\r\nBệnh nh&acirc;n bệnh tim mạch, đ&aacute;i th&aacute;o đường, bệnh đường ti&ecirc;u h&oacute;a, suy gan, loạn chuyển h&oacute;a &ndash; Porphyrin, suy tim, suy thận, bệnh nh&acirc;n đang d&ugrave;ng lợi tiểu, rối loạn đ&ocirc;ng m&aacute;u.<br />\r\nNgười lớn tuổi.<br />\r\nN&ecirc;n kiểm tra chức năng gan, thận, c&ocirc;ng thức m&aacute;u.<br />\r\nPhụ nữ c&oacute; thai v&agrave; cho con b&uacute;.<br />\r\nL&aacute;i xe v&agrave; vận h&agrave;nh m&aacute;y.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Diclofenac Natri 50mg, t&aacute; dược vừa đủ 1 vi&ecirc;n.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 10 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Thụy Sĩ<br />\r\n<strong>Nh&agrave; sảm xuất:&nbsp;</strong>Novartis</p>', 1, 1, 'hộp', 'Thụy Sĩ', '2021-06-24 10:35:10', '2021-06-27 06:41:45'),
(33, 'Loperamide STELLA', 'loperamide-stella', 'P01281.jpg', 25000, 0, 'Loperamid được chỉ định kiểm soát và làm giảm triệu chứng tiêu chảy cấp không đặc trưng và tiêu chảy mãn tính do bệnh viêm ruột. Đồng thời cũng chỉ định để làm giảm thể tích chất thải sau thủ thuật mở thông hồi tràng, trực tràng.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nQu&aacute; mẫn cảm với Loperamid hoặc với bất cứ th&agrave;nh phần n&agrave;o của thuốc.<br />\r\nTrường hợp đau bụng m&agrave; kh&ocirc;ng c&oacute; ti&ecirc;u chảy.<br />\r\nKh&ocirc;ng d&ugrave;ng trong trường hợp: Trướng bụng, lỵ cấp, vi&ecirc;m lo&eacute;t đại tr&agrave;ng chảy m&aacute;u giai đoạn cấp, vi&ecirc;m đại tr&agrave;ng giả mạc.<br />\r\nPhụ nữ c&oacute; thai v&agrave; cho con b&uacute;, trẻ em dưới 2 tuổi.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nLoperamid được chỉ định kiểm so&aacute;t v&agrave; l&agrave;m giảm triệu chứng ti&ecirc;u chảy cấp kh&ocirc;ng đặc trưng v&agrave; ti&ecirc;u chảy m&atilde;n t&iacute;nh do bệnh vi&ecirc;m ruột. Đồng thời cũng chỉ định để l&agrave;m giảm thể t&iacute;ch chất thải sau thủ thuật mở th&ocirc;ng hồi tr&agrave;ng, trực tr&agrave;ng.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\na. Ti&ecirc;u chảy cấp:<br />\r\nNgười lớn:&nbsp;4mg. Tiếp theo 2mg sau mỗi lần đi ti&ecirc;u lỏng, kh&ocirc;ng qu&aacute; 16mg/ng&agrave;y.<br />\r\nTiến triển l&acirc;m s&agrave;ng thường được theo d&otilde;i trong v&ograve;ng 48 giờ.<br />\r\nTrẻ em từ 6 &ndash; 8 tuổi:&nbsp;2mg x 2 lần/ng&agrave;y. (20 &ndash; 30kg)<br />\r\nTrẻ em từ 8 &ndash; 12 tuổi: 2mg x 3 lần/ng&agrave;y. ( tr&ecirc;n 30kg)<br />\r\nLiều&nbsp;h&agrave;ng ng&agrave;y tiếp theo: 1mg/10kg/ng&agrave;y, chỉ d&ugrave;ng sau khi đi ti&ecirc;u lỏng. Tổng liều h&agrave;ng ng&agrave;y kh&ocirc;ng vượt qu&aacute; liều ng&agrave;y đầu ti&ecirc;n.<br />\r\nb. Ti&ecirc;u chảy m&atilde;n t&iacute;nh:<br />\r\nNgười lớn:<em>&nbsp;</em><br />\r\nLiều&nbsp;khởi đầu:&nbsp;4 mg. Tiếp theo 2mg sau mỗi lần đi ti&ecirc;u lỏng cho đến khi kiểm so&aacute;t được ti&ecirc;u chảy.&nbsp;Sau đ&oacute;&nbsp;c&oacute; thể giảm liều.<br />\r\nKhi đ&atilde; x&aacute;c định được liều tối ưu h&agrave;ng ng&agrave;y, c&oacute; thể d&ugrave;ng liều n&agrave;y một lần hoặc chia nhiều lần.<br />\r\nLiều duy tr&igrave;&nbsp;4 &ndash; 8mg/ng&agrave;y. Hiếm khi vượt qu&aacute;&nbsp;16mg/ng&agrave;y.<br />\r\nHầu như kh&ocirc;ng kiểm so&aacute;t được c&aacute;c triệu chứng ở liều cao hơn nếu l&acirc;m s&agrave;ng kh&ocirc;ng cải thiện sau khi điều trị với liều 16mg/ng&agrave;y trong &iacute;t nhất 10 ng&agrave;y.<br />\r\nTrẻ em:<br />\r\nLiều chưa được x&aacute;c định.<br />\r\n<strong>Thận trọng:</strong><br />\r\nTham khảo &yacute; kiến&nbsp;b&aacute;c sỹ nếu t&igrave;nh trạng ti&ecirc;u chảy kh&ocirc;ng cải thiện.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản trong bao b&igrave; k&iacute;n, nơi kh&ocirc;, tr&aacute;nh &aacute;nh s&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&nbsp;độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:</strong><br />\r\nMỗi vi&ecirc;n chứa: Loperamid hydroclorid&nbsp;2mg,&nbsp;t&aacute; dược vừa đủ.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 5 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Đức<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;STADA</p>', 1, 1, 'hộp', 'Đức', '2021-06-24 10:35:58', '2021-06-27 06:42:21'),
(34, 'Cholapan', 'cholapan', 'P15319.jpg', 45000, 0, 'Viên mật nghệ Cholapan dùng trong các chứng đau gan, mật kém, đau dạ dày, cải thiện tốt triệu chứng táo bón, chán ăn.', '<p><strong>1. Chống chỉ định:&nbsp;</strong><br />\r\nKh&ocirc;ng d&ugrave;ng cho phụ nữ c&oacute; thai<br />\r\nKh&ocirc;ng d&ugrave;ng cho trẻ em dưới 5 tuổi do dạng b&agrave;o chế kh&ocirc;ng th&iacute;ch hợp<br />\r\nKh&ocirc;ng d&ugrave;ng cho bệnh nh&acirc;n mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của thuốc</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:<br />\r\nChỉ định:&nbsp;</strong><br />\r\nD&ugrave;ng trong c&aacute;c chứng đau gan, mật k&eacute;m, đau dạ d&agrave;y<br />\r\nTrị c&aacute;c chứng: Đau v&ugrave;ng thượng vị, ợ chua n&oacute;ng r&aacute;t v&ugrave;ng thượng vị, ăn uống kh&oacute; ti&ecirc;u, đầy hơi, s&igrave;nh bụng<br />\r\nCải thiện tốt triệu chứng t&aacute;o b&oacute;n, ch&aacute;n ăn<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống trước bữa ăn ch&iacute;nh<br />\r\nNgười lớn: Uống mỗi lần 2 vi&ecirc;n, ng&agrave;y 2 lần<br />\r\nTrẻ em từ 5 tuổi đến dưới 15 tuổi: Uống mỗi lần 1 vi&ecirc;n, ng&agrave;y 2 lần<br />\r\nTrường hợp đau nhiều, uống gấp đ&ocirc;i<br />\r\n<strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, nhiệt độ dưới 30 độ C</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong><br />\r\nCao cồn nghệ 75,00mg<br />\r\n(tương đương nghệ 540,35mg)<br />\r\nTrần b&igrave; 139,00mg<br />\r\nCao mật heo 60,00,mg<br />\r\nT&aacute; dược vừa đủ 1 vi&ecirc;n<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:&nbsp;</strong>Hộp 5 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:&nbsp;</strong>Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:&nbsp;</strong>OPC</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:37:17', '2021-06-27 06:47:18'),
(35, 'Ferrovit', 'ferrovit', 'P03620.jpg', 72000, 0, 'Bổ sung sắt cho phụ nữ có thai và các chứng thiếu máu thiếu sắt ở trẻ em, thanh thiếu niên, người lớn bị chảy máu bên trong như chảy máu đường ruột, chảy máu do loét, người đang điều trị tách máu.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nKh&ocirc;ng sử dụng cho bệnh nh&acirc;n dị ứng với bất kỳ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị thiếu m&aacute;u do thiếu sắt cho phụ nữ c&oacute; thai v&agrave; c&aacute;c chứng thiếu m&aacute;u thiếu sắt ở trẻ em, thanh thiếu ni&ecirc;n, người lớn bị chảy m&aacute;u b&ecirc;n trong như chảy m&aacute;u đường ruột, chảy m&aacute;u do lo&eacute;t, người đang điều trị t&aacute;ch m&aacute;u hay phẫu thuật dạ d&agrave;y.<br />\r\nDự ph&ograve;ng thiếu sắt v&agrave; Acid Folic ở trẻ em l&agrave; đối tượng cần bổ sung nhiều sắt để tăng trưởng v&agrave; ph&aacute;t triển ở thiếu nữ tại c&aacute;c giai đoạn h&agrave;nh kinh, ở phụ nữ trong độ tuổi sinh đẻ bị mất m&aacute;u v&agrave; ở phụ nữ mang thai cần nhiều sắt để tăng thể t&iacute;ch m&aacute;u v&agrave; ph&aacute;t triển của b&agrave;o thai.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống 1 vi&ecirc;n, ng&agrave;y 1 &ndash; 2 lần sau khi ăn.<br />\r\n<strong>Thận trọng:</strong><br />\r\nKh&ocirc;ng n&ecirc;n sử dụng Acid Folic cho đến khi loại bỏ được chứng thiếu m&aacute;u &aacute;c t&iacute;nh qua chuẩn đo&aacute;n.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong><br />\r\nSắt Fumarate: 162mg, Acid Folic: 0.75mg, Vitamin B12: 7.5mg<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:&nbsp;</strong>Hộp&nbsp;5 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Th&aacute;i Lan<br />\r\n<strong>Nh&agrave; sản xuất:&nbsp;</strong>Mega Lifesciences</p>', 1, 1, 'hộp', 'Thái Lan', '2021-06-24 10:38:06', '2021-07-12 02:33:26'),
(36, 'Dụng cụ cắt thuốc Hương Sơn', 'dung-cu-cat-thuoc-huong-son', 'P02991.jpg', 24000, 0, 'Dụng cụ cắt thuốc Hương Sơn dễ dàng, nhanh gọn, tiện lợi, hợp vệ sinh.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nNhựa APS-HI, dao bằng th&eacute;p rất sắc.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nDễ d&agrave;ng cắt vi&ecirc;n thuốc ra nhiều miếng nhỏ đều nhau, gi&uacute;p b&eacute; v&agrave; người bệnh dễ uống hơn.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o, sạch sẽ, tr&aacute;nh lửa v&agrave; nhiệt độ cao.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 5, 'cái', 'Việt Nam', '2021-06-24 10:38:48', '2021-06-27 07:12:07'),
(37, 'Dụng cụ nghiền thuốc Sơn Hương', 'dung-cu-nghien-thuoc-son-huong', 'P12851.jpg', 37500, 0, 'Dụng cụ nghiền thuốc nhỏ gọn, chất lượng, giúp dễ dàng nghiền thuốc khi cần.', '<p><strong>Đặc điểm sản phẩm</strong><br />\r\n&ndash; Dụng cụ nghiền thuốc được l&agrave;m từ nhựa, kh&ocirc;ng độc hại, an to&agrave;n khi sử dụng.<br />\r\n&ndash; Nghiền thuốc đơn giản, nhanh ch&oacute;ng để b&eacute; uống thuốc dễ d&agrave;ng.<br />\r\n&ndash; Sản phẩm tiện lợi, nhỏ gọn, dễ mang theo khi cần.</p>\r\n\r\n<p><em>Lưu &yacute;:</em><br />\r\n&ndash; N&ecirc;n hỏi b&aacute;c sĩ, dược sĩ xem theo toa thuốc c&oacute; thể được nghiền được hay kh&ocirc;ng. Kh&ocirc;ng phải tất cả thuốc đều th&iacute;ch hợp cho việc nghiền n&aacute;t.<br />\r\n&ndash; Khi một số thuốc bị nghiền n&aacute;t, bụi thuốc sẽ lẫn kh&ocirc;ng kh&iacute; v&agrave; c&oacute; thể h&iacute;t v&agrave;o phổi. Một số loại thuốc c&oacute; thể g&acirc;y ra những phản ứng bất lợi đối với sức khoẻ, kể cả với người khỏe mạnh.<br />\r\n&ndash; Ngo&agrave;i phương ph&aacute;p nghiền, c&oacute; thể d&ugrave;ng thuốc ở dạng kh&aacute;c như dạng lỏng, miếng d&aacute;n, ti&ecirc;m&hellip; để kh&ocirc;ng phải nuốt vi&ecirc;n. Trao đổi với b&aacute;c sĩ, dược sĩ nếu cần c&aacute;c lựa chọn kh&aacute;c ngo&agrave;i thuốc vi&ecirc;n.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 5, 'cái', 'Việt Nam', '2021-06-24 10:39:36', '2021-06-27 07:12:34'),
(38, 'Hộp đựng thuốc Hofaco', 'hop-dung-thuoc-hofaco', 'P04609.jpg', 11000, 0, 'Hộp đựng thuốc Hofaco giúp bạn không nhầm lẫn khi uống thuốc.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nNhựa PP nhập khẩu.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nĐựng thuốc v&agrave; ph&acirc;n biệt c&aacute;c bữa khi d&ugrave;ng thuốc.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o, sạch sẽ, tr&aacute;nh lửa v&agrave; nhiệt độ cao.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 5, 'cái', 'Việt Nam', '2021-06-24 10:40:23', '2021-06-27 07:12:58'),
(39, 'Viên ngậm cai thuốc lá Acetium', 'vien-ngam-cai-thuoc-la-acetium', 'P17606.jpg', 205000, 0, '\"Viên ngậm cai thuốc lá Acetium giúp cai thuốc và giảm sự lệ thuộc vào thuốc lá. Các thành phần trong viên hỗ trợ việc từ bỏ thuốc lá thành công trong 3-6 tháng. Sản phẩm giúp tránh được các tác dụng phụ thường thấy ở các hình thức cai nghiện thuốc lá thông thường như phụ thuộc vào nicotin sau cai.\r\n\"', '<p><strong>C&ocirc;ng dụng</strong><br />\r\nVi&ecirc;n ngậm Acetium c&oacute; chứa L-cystein l&agrave; một loại acid amin tự nhi&ecirc;n được giải ph&oacute;ng dần dần trong qu&aacute; tr&igrave;nh ngậm vi&ecirc;n khi h&uacute;t thuốc. L-cystein t&aacute;c động đến 90% lượng kh&oacute;i thuốc l&aacute;, gi&uacute;p v&ocirc; hiệu h&oacute;a hoạt động của acetaldehyde, l&agrave; chất g&acirc;y nghiện nicotin v&agrave; g&acirc;y ra ung thư.<br />\r\nVi&ecirc;n ngậm kh&ocirc;ng l&agrave;m giảm c&aacute;c t&aacute;c hại li&ecirc;n quan đến sức khỏe do thuốc g&acirc;y ra.</p>\r\n\r\n<p><strong>Th&agrave;nh phần hoạt t&iacute;nh</strong><br />\r\nHoạt chất h&ograve;a tan chậm: L-cystein.<br />\r\nTh&agrave;nh phần kh&aacute;c: xylitol, sorbitol, hương vị&hellip;<br />\r\nKh&ocirc;ng chứa lactose, gluten hoặc nicotin.</p>\r\n\r\n<p><strong>Chỉ định</strong><br />\r\nNgười cần cai nghiện thuốc l&aacute;.</p>\r\n\r\n<p><strong>Chống chỉ định</strong><br />\r\nPhụ nữ mang thai v&agrave; cho con b&uacute;.<br />\r\nNgười rối loạn chức năng thận hoặc cystinuria.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng</strong><br />\r\nC&oacute; thể sử dụng tối đa 40 vi&ecirc;n ngậm/ng&agrave;y.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng</strong><br />\r\nNgậm một vi&ecirc;n Acetium ngay khi bắt đầu mồi thuốc.<br />\r\nĐể vi&ecirc;n ngậm tan từ từ trong miệng trong khi h&uacute;t thuốc, kh&ocirc;ng được nhai.<br />\r\nNếu bạn tiếp tục h&uacute;t thuốc m&agrave; vi&ecirc;n đ&atilde; tan ho&agrave;n to&agrave;n, ngậm th&ecirc;m một vi&ecirc;n Acetium.<br />\r\nKh&ocirc;ng n&ecirc;n sử dụng vi&ecirc;n ngậm c&ugrave;ng với nước hoặc thức ăn kh&aacute;c v&igrave; sẽ l&agrave;m giảm hiệu quả.</p>\r\n\r\n<p><strong>T&aacute;c dụng phụ</strong><br />\r\nVi&ecirc;n ngậm chứa chất l&agrave;m ngọt (xylitol, sorbitol). Sử dụng qu&aacute; mức c&oacute; thể g&acirc;y t&aacute;c dụng nhuận tr&agrave;ng v&agrave;/hoặc đầy hơi.<br />\r\nC&aacute;c triệu chứng th&ocirc;ng thường của người cai thuốc l&aacute;: thay đổi t&acirc;m trạng, mất ngủ, thiếu tập trung, nhức đầu, giảm nhịp tim v&agrave; tăng sự th&egrave;m ăn.</p>\r\n\r\n<p><strong>Thận trọng</strong><br />\r\nTr&aacute;nh xa tầm tay trẻ em.<br />\r\nKh&ocirc;ng d&ugrave;ng cho người dị ứng với bất kỳ th&agrave;nh phần n&agrave;o của sản phẩm.</p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, nhiệt độ dưới 25<sup>o</sup>C.</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 3 vỉ x 10 vi&ecirc;n ngậm.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Phần Lan</p>', 1, 4, 'hộp', 'Việt Nam', '2021-06-24 10:41:38', '2021-06-27 07:06:31'),
(40, 'Genshu', 'genshu', 'P14100.jpg', 550000, 529000, 'Thuốc Genshu với thành phần chứa các loại thảo dược quý hiếm có tác dụng bổ thận, tráng dương, sinh tinh ở nam giới, gia tăng lượng testosterone trong máu, bồi bổ nguyên khí và nâng cao sức khỏe toàn diện cho cánh mày râu.', '<p><strong>1. Chống chỉ định:&nbsp;</strong>Kh&ocirc;ng d&ugrave;ng cho trẻ em v&agrave; phụ nữ c&oacute; thai</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nSinh dục suy nhược (Rối loạn cương)<br />\r\nGiảm ham muốn t&igrave;nh dục, xuất tinh sớm<br />\r\nChứng thận dương suy như đau lưng, mỏi gối, lạnh tay ch&acirc;n, tiểu đ&ecirc;m.<br />\r\nSuy giảm chức năng sinh dục, yếu sinh l&yacute;, di mộng tinh.<br />\r\nSuy nhược cơ thể, kiệt sức, mệt mỏi.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nTrường hợp điều trị: Uống mỗi lần 2 vi&ecirc;n, ng&agrave;y uống 2 lần. Uống li&ecirc;n tục trong 4-8 tuần để đạt hiệu quả cao nhất.<br />\r\nTrường hợp sử dụng để ngăn ngừa hội chứng m&atilde;n dục nam: Uống mỗi lần 1 vi&ecirc;n, ng&agrave;y uống 2 lần.<br />\r\nTrường hợp sử dụng để tăng cường sức khỏe, tăng khả năng miễn dịch: Uống mỗi lần 1 vi&ecirc;n, ng&agrave;y uống 1 lần.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh s&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong>&nbsp;Mỗi vi&ecirc;n nang cứng chứa:<br />\r\n331mg cao hỗn hợp dược liệu quy về kh&ocirc;, tương đương với:<br />\r\nBạch tật l&ecirc; 1000mg, D&acirc;m dương hoặc 750mg, Hải m&atilde; 330mg, Lộc nhung 330mg, Hải h&agrave; 330mg, Nh&acirc;n s&acirc;m 297mg, Tinh ho&agrave;n c&aacute; sấu 250mg, Qu&ecirc; nhục 50mg<br />\r\n83mg bột dược liệu, tuong đương với:<br />\r\nQu&ecirc; nhục 50mg, Nh&acirc;n s&acirc;m 33mg, T&aacute; dược vừa đủ 1 vi&ecirc;n.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;3 vỉ x 10 vi&ecirc;n/hộp<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Sagopha</p>', 1, 4, 'hộp', 'Việt Nam', '2021-06-24 10:42:31', '2021-06-27 07:07:38'),
(41, 'Thực phẩm bảo vệ sức khỏe  Gout Relief', 'thuc-pham-bao-ve-suc-khoe--gout-relief', 'P16083.jpg', 160000, 0, '\"Sản phẩm Gout Relief hỗ trợ giúp làm giảm acid uric trong máu, hỗ trợ giảm các biểu hiện của viêm khớp do gout.\r\n\"', '<p><strong>Th&agrave;nh phần cấu tạo</strong><br />\r\nTrong 1 vi&ecirc;n n&eacute;n 750 mg c&oacute; chứa:<br />\r\nCao D&acirc;y gắm&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.. 120 mg<br />\r\nCao T&iacute;a t&ocirc;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip; 100 mg<br />\r\nCao Thổ phục linh&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;. 100 mg<br />\r\nCao Diệp hạ ch&acirc;u&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip; 80 mg<br />\r\nCao R&acirc;u m&egrave;o&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;. 80 mg<br />\r\nCao Sa nh&acirc;n&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.. 80 mg<br />\r\nCao Sả&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip; 50 mg<br />\r\nPhụ liệu: Microctystalline cellulose, Polyvinylpyrrolidone, Talc, Polyethylene Glycol vừa đủ cho 1 vi&ecirc;n sản phẩm</p>\r\n\r\n<p><strong>C&ocirc;ng dụng</strong><br />\r\nHỗ trợ gi&uacute;p l&agrave;m giảm acid uric trong m&aacute;u, hỗ trợ giảm c&aacute;c biểu hiện của vi&ecirc;m khớp do gout.</p>\r\n\r\n<p><em><strong>Ch&uacute; &yacute;:</strong></em><br />\r\nThực phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc v&agrave; kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh. Kh&ocirc;ng sử dụng cho người mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của sản phẩm.</p>\r\n\r\n<p><strong>Đối tượng sử dụng</strong><br />\r\nNgười c&oacute; acid uric trong m&aacute;u cao, người bị gout g&acirc;y vi&ecirc;m khớp với c&aacute;c biểu hiện: Sưng, n&oacute;ng đỏ, đau c&aacute;c khớp.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng</strong><br />\r\n2 vi&ecirc;n/ng&agrave;y, d&ugrave;ng sau bữa ăn 30 ph&uacute;t. Uống c&ugrave;ng với 1 cốc nước nhỏ.</p>\r\n\r\n<p><strong>Hướng dẫn bảo quản</strong><br />\r\nBảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;30 vi&ecirc;n/hộp.</p>\r\n\r\n<p><strong>Thời hạn sử dụng:</strong>&nbsp;36 th&aacute;ng từ ng&agrave;y sản xuất.</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 4, 'hộp', 'Việt Nam', '2021-06-24 10:44:16', '2021-06-27 07:08:17'),
(42, 'Dạ Minh Châu', 'da-minh-chau', 'P14489.jpg', 50000, 0, 'Viên uống tiểu đêm thảo dược Dạ Minh Châu dùng cho cả nam và nữ mắc chứng đi tiểu nhiều lần đặc biệt là vào ban đêm (chứng tiểu đêm), tiểu đục, tiểu tiện không tự chủ hoặc tiểu nhiều vào ban ngày, đồng thời bổ thận, tăng khả năng lọc và tái hấp thu nước của thận.', '<p><strong>Đối tượng sử dụng:&nbsp;</strong><br />\r\nD&ugrave;ng cho người tiểu tiện kh&ocirc;ng tự chủ, tiểu đục, tiểu đ&ecirc;m nhiều lần hoặc đi tiểu nhiều lần v&agrave;o ban ng&agrave;y, đặc biệt l&agrave; người gi&agrave; sức khỏe to&agrave;n th&acirc;n suy giảm. Người c&oacute; bệnh l&yacute; huyết khối, chấn thương bầm t&iacute;m.</p>\r\n\r\n<p><strong>Th&agrave;nh phần:</strong><br />\r\nThỏ ty tử: 0,83g, X&agrave; s&agrave;ng tử: 0,83g, &Iacute;ch tr&iacute; nh&acirc;n: 0,38g, Tiểu hồi hương: 0,83g, Cao đậu tương l&ecirc;n men: 0,17g, Phụ liệu vừa đủ 1 vi&ecirc;n.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nGi&uacute;p &ocirc;n bổ thận dương, l&agrave;m giảm cục m&aacute;u đ&ocirc;ng trong l&ograve;ng mạch m&aacute;u, hỗ trợ liệu ph&aacute;p điều trị c&aacute;c chứng đau mỏi lưng, tiểu đ&ecirc;m nhiều lần.</p>\r\n\r\n<p><strong>Liều d&ugrave;ng &amp; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống v&agrave;o buổi tối, trước bữa ăn 1 giờ hoặc sau bữa ăn 2 giờ.<br />\r\nHỗ trợ điều trị: Ng&agrave;y uống 1 lần, mỗi lần 2 &ndash; 3 vi&ecirc;n.<br />\r\nDuy tr&igrave; t&aacute;c dụng: Ng&agrave;y uống 1 lần, mỗi lần 1 &ndash; 2 vi&ecirc;n.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi&nbsp;kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:&nbsp;</strong>Hộp 2 vỉ x 6 vi&ecirc;n</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 4, 'hộp', 'Việt Nam', '2021-06-24 10:45:31', '2021-06-27 07:08:45'),
(43, 'Kukumin IP', 'kukumin-ip', 'P10961.jpg', 250000, 0, 'Kukumin IP là sản phẩm hỗ trợ điều trị viêm loét dạ dày tá tràng, trào ngược dạ dày. Với 2 thành phần Curcumin phytosome và ImmunePath IP, mỗi thành phần sẽ có những cơ chế tác động riêng nhưng hỗ trợ hiệp đồng để cùng đạt hiệu quả tốt. Giúp giảm triệu chứng viêm loét dạ dày-tá tràng, viêm ruột cấp và mạn tính, tăng cường sức đề kháng, tái tạo niêm mạc và cân bằng hệ vi sinh đường ruột.', '<p><strong>Đối tượng sử dụng:</strong><br />\r\nNgười bị vi&ecirc;m lo&eacute;t dạ d&agrave;y-t&aacute; tr&agrave;ng, tr&agrave;o ngược dạ d&agrave;y, vi&ecirc;m ruột cấp v&agrave; m&atilde;n t&iacute;nh.<br />\r\nNgười đang trong đợt điều trị kh&aacute;ng sinh d&agrave;i ng&agrave;y, điều trị c&aacute;c bệnh m&atilde;n t&iacute;nh.<br />\r\nNgười gầy yếu do rối loạn vi sinh đường ruột, khả năng hấp thu dinh dưỡng k&eacute;m.</p>\r\n\r\n<p><strong>Th&agrave;nh phần:&nbsp;</strong><br />\r\nCurcumin Phytosome<br />\r\nImmunePath-IP</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nGi&uacute;p giảm triệu chứng vi&ecirc;m lo&eacute;t dạ d&agrave;y &ndash; t&aacute; tr&agrave;ng, vi&ecirc;m ruột cấp v&agrave; m&atilde;n t&iacute;nh.<br />\r\nGi&uacute;p tăng cường sức đề kh&aacute;ng, khả năng miễn dịch của cơ thể, t&aacute;i tạo ni&ecirc;m mạc v&agrave; c&acirc;n bằng hệ vi sinh đường ruột.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong><br />\r\nLiều hỗ trợ điều trị: ng&agrave;y 2 lần, mỗi lần 1 vi&ecirc;n.<br />\r\nTăng cường sức khỏe v&agrave; ph&ograve;ng ngừa: 1 vi&ecirc;n/ng&agrave;y.<br />\r\nN&ecirc;n sử dụng sản phẩm trước bữa ăn 30 ph&uacute;t hoặc sau khi ăn 2 giờ.<br />\r\nN&ecirc;n sử dụng một đợt từ 3-6 th&aacute;ng để c&oacute; kết quả tốt nhất.<br />\r\nSản phẩm c&oacute; thể d&ugrave;ng thời gian d&agrave;i m&agrave; kh&ocirc;ng c&oacute; t&aacute;c dụng phụ.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 2 vỉ x 10 vi&ecirc;n.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 4, 'hộp', 'Việt Nam', '2021-06-24 10:46:47', '2021-06-27 07:09:13'),
(44, 'Sucralfate', 'sucralfate', 'P01246.jpg', 22500, 0, 'Sucralfate điều trị loét dạ dày – tá tràng, viêm dạ dày mãn tính, loét lành tính. Phòng tái phát loét tá tràng, phòng loét dạ dày do stress. Điều trị bệnh trào ngược dạ dày – thưc quản.', '<p><strong>1. Chống chỉ định:&nbsp;</strong><br />\r\nMẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của thuốc.&nbsp;</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị lo&eacute;t dạ d&agrave;y &ndash; t&aacute; tr&agrave;ng, vi&ecirc;m dạ d&agrave;y m&atilde;n t&iacute;nh, lo&eacute;t l&agrave;nh t&iacute;nh. Ph&ograve;ng t&aacute;i ph&aacute;t lo&eacute;t t&aacute; tr&agrave;ng, ph&ograve;ng lo&eacute;t dạ d&agrave;y do stress. Điều trị bệnh tr&agrave;o ngược dạ d&agrave;y &ndash; thưc quản.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nN&ecirc;n uống l&uacute;c đ&oacute;i<br />\r\nLo&eacute;t t&aacute; tr&agrave;ng: Uống 2g/lần, 2 lần/ng&agrave;y v&agrave;o mỗi buổi s&aacute;ng v&agrave; trước khi đi ngủ.<br />\r\nLo&eacute;t dạ d&agrave;y l&agrave;nh t&iacute;nh: 1g/lần, ng&agrave;y uống 4 lần, điều trị 6-8 tuần.<br />\r\nPh&ograve;ng t&aacute;i ph&aacute;t lo&eacute;t t&aacute; tr&agrave;ng: Uống 1g/lần, ng&agrave;y uống 2 lần, điều trị kh&ocirc;ng qu&aacute; 6 th&aacute;ng.&nbsp;<br />\r\nĐiều trị tr&agrave;o ngược dạ d&agrave;y-thực quản: Uống 1g/lần, ng&agrave;y uống 4 lần, c&aacute;ch 1 giờ trước mỗi bữa ăn v&agrave; trước khi ngủ.<br />\r\n<strong>Thận trọng:</strong><br />\r\nNgười suy thận do nguy cơ tăng nồng độ nh&ocirc;m trong huyết thanh, nhất l&agrave; khi d&ugrave;ng d&agrave;i ng&agrave;y. Trường hợp suy thận nặng, n&ecirc;n tr&aacute;nh d&ugrave;ng. Phụ nữ c&oacute; thai v&agrave; phụ nữ đang cho con b&uacute;.&nbsp;<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Sucralfat 1g, t&aacute; dược vừa đủ 1 vi&ecirc;n.&nbsp;<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 2 vỉ x 10 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Vidipha</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-24 10:47:57', '2021-06-27 08:19:18'),
(45, 'Thuốc ngừa thai Postinor', 'thuoc-ngua-thai-postinor', 'P02182.jpg', 33000, 0, 'Postinor 1 là thuốc ngừa thai khẩn cấp, được dùng trong vòng 72 giờ sau khi giao hợp mà không dùng biện pháp tránh thai, hoặc biện pháp tránh thai đã và đang dùng không đạt hiệu quả.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nKh&ocirc;ng d&ugrave;ng Postinor 1, nếu:<br />\r\nBạn đ&atilde; biết l&agrave; bạn bị dị ứng với bất cứ th&agrave;nh phần n&agrave;o của thuốc.<br />\r\nBạn mắc bệnh gan nặng.<br />\r\nNếu bạn mang thai.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nNgừa thai khẩn cấp, được d&ugrave;ng trong v&ograve;ng 72 giờ sau khi giao hợp mà kh&ocirc;ng d&ugrave;ng biện ph&aacute;p tr&aacute;nh thai, hoặc biện ph&aacute;p tr&aacute;nh thai đ&atilde; v&agrave; đang d&ugrave;ng kh&ocirc;ng đạt hiệu quả.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống ngay một vi&ecirc;n thuốc, c&agrave;ng sớm c&agrave;ng tốt (t&ocirc;́t nh&acirc;́t trong 12 giờ đầu), kh&ocirc;ng để muộn hơn 72 giờ (3 ng&agrave;y) sau khi xảy ra cuộc giao hợp mà kh&ocirc;ng d&ugrave;ng biện ph&aacute;p tr&aacute;nh thai. Uống nguy&ecirc;n vi&ecirc;n thuốc v&agrave; c&oacute; thể uống với nước. Kh&ocirc;ng n&ecirc;n tr&igrave; ho&atilde;n việc uống thuốc.<br />\r\nNếu đang sử dụng một biện ph&aacute;p ngừa thai thường xuy&ecirc;n kh&aacute;c (như vi&ecirc;n thuốc tr&aacute;nh thai hàng ngày), th&igrave; vẫn tiếp tục dùng biện ph&aacute;p n&agrave;y như thường lệ.<br />\r\n<strong>Thận trọng:</strong><br />\r\na. Nếu bạn ở trong bất kỳ t&igrave;nh trạng n&agrave;o sau đ&acirc;y, cần b&aacute;o cho b&aacute;c sĩ biết trước khi d&ugrave;ng Postinor 1 v&igrave; c&oacute; thể thuốc ngừa thai khẩn cấp kh&ocirc;ng ph&ugrave; hợp với bạn. B&aacute;c sĩ c&oacute; thể gi&uacute;p bạn lựa chọn một biện ph&aacute;p tr&aacute;nh thai kh&aacute;c ph&ugrave; hợp hơn.<br />\r\nNếu bạn đang mang thai hoặc nghĩ rằng c&oacute; lẽ bạn đ&atilde; thụ thai.<br />\r\nBạn bị trễ kinh, hoặc kỳ kinh cuối của bạn kh&ocirc;ng giống như b&igrave;nh thường.<br />\r\nKể từ kỳ kinh cuối, nếu bạn đ&atilde; c&oacute; một cuộc giao hợp kh&ocirc;ng d&ugrave;ng biện ph&aacute;p tr&aacute;nh thai, xảy ra đ&atilde; qu&aacute; 72 giờ (v&igrave; c&oacute; thể sự thụ thai đ&atilde; xảy ra, n&ecirc;n việc điều trị sẽ kh&ocirc;ng c&oacute; kết quả).&nbsp;<br />\r\nNếu bạn mắc một bệnh n&agrave;o đ&oacute; về đường ruột (v&iacute; dụ, bệnh Crohn), ảnh hưởng đến sự ti&ecirc;u ho&aacute; thức ăn.<br />\r\nNếu c&oacute; bệnh suy gan nặng.<br />\r\nThuốc c&oacute; chứa 142.5mg lactose monohydrat. Những người c&oacute; bị bệnh di truyền hiếm gặp về kh&ocirc;ng dung nạp galactose, kh&ocirc;ng dung nạp lactose hoặc bị bệnh k&eacute;m hấp thu glucose-galactose kh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y.<br />\r\nPostinor 1 kh&ocirc;ng phải l&agrave; biện ph&aacute;p tr&aacute;nh thai thường xuy&ecirc;n v&agrave; chỉ ph&ugrave; hợp như một biện ph&aacute;p khẩn cấp. Phụ nữ cần d&ugrave;ng lặp lại thuốc tr&aacute;nh thai khẩn cấp được khuy&ecirc;n n&ecirc;n d&ugrave;ng c&aacute;c biện ph&aacute;p tr&aacute;nh thai l&acirc;u d&agrave;i.<br />\r\nD&ugrave;ng thuốc tr&aacute;nh thai khẩn cấp kh&ocirc;ng thay thế cho c&aacute;c biện ph&aacute;p ngăn ngừa bệnh l&acirc;y truyền qua đường t&igrave;nh dục.<br />\r\nb. Nếu bạn lo lắng về việc c&oacute; thể mắc phải c&aacute;c bệnh l&acirc;y truyền qua đường t&igrave;nh dục<br />\r\nThuốc n&agrave;y kh&ocirc;ng bảo vệ bạn khỏi c&aacute;c bệnh l&acirc;y truyền qua đường t&igrave;nh dục; chỉ c&oacute; bao cao su mới c&oacute; t&aacute;c dụng n&agrave;y. H&atilde;y trao đổi mối lo lắng của bạn với b&aacute;c sĩ, dược sĩ, c&aacute;c y sĩ thực h&agrave;nh hay tư vấn tại c&aacute;c ph&ograve;ng kh&aacute;m kế hoạch h&oacute;a gia đ&igrave;nh.<br />\r\n<strong>Bảo quản:&nbsp;</strong><br />\r\nBảo quản ở nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:&nbsp;</strong>Levonorgestrel 1.5mg, t&aacute; dược vừa đủ 1 vi&ecirc;n.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 vi&ecirc;n<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Hungary<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Gedeon Richter</p>', 1, 1, 'hộp', 'Hungary', '2021-06-24 10:49:03', '2021-06-27 06:44:11'),
(46, 'Xanh Methylen', 'xanh-methylen', 'P13401.jpg', 4000, 0, 'Xanh Methylen 1% điều trị Nhiễm virus ngoài da như Herpes simplex, điều trị chốc đầu, lở loét, viêm da mủ.', '<p><strong>1. Chống chỉ định:</strong>&nbsp;Chưa c&oacute; th&ocirc;ng tin</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:<br />\r\nChỉ định:</strong>&nbsp;Điều trị Nhiễm virus ngo&agrave;i da như Herpes simplex. Chốc đầu, lở lo&eacute;t. Vi&ecirc;m da mủ<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong>&nbsp;Rửa sạch vết lở lo&eacute;t, thấm kh&ocirc;. B&ocirc;i dung dịch Xanh methylen 1% ng&agrave;y 2 &ndash; 3 lần. Thuốc d&ugrave;ng b&ocirc;i ngo&agrave;i da.<br />\r\n<strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:&nbsp;</strong><br />\r\nXanh Methylen&hellip;&hellip;&hellip;&hellip;&hellip;..200mg<br />\r\nNước tinh khiết&hellip;&hellip;&hellip;&hellip;&hellip;..vừa đủ 20ml<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Chai 20ml<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Dược VTYT Hải Dương</p>', 1, 1, 'chai', 'Việt Nam', '2021-06-24 10:50:59', '2021-07-12 02:05:38');
INSERT INTO `san_pham` (`id`, `ten_san_pham`, `ten_url`, `hinh_san_pham`, `gia_san_pham`, `giam_gia`, `mo_ta_tom_tat`, `chi_tiet`, `trang_thai`, `ma_loai`, `don_vi`, `nguon_goc`, `created_at`, `updated_at`) VALUES
(47, 'SRM  Dabo Foam Cleanser', 'srm--dabo-foam-cleanser', 'P08207.jpg', 120000, 0, 'Sữa rửa mặt cao cấp tinh chất trà xanh Dabo Foam Cleanser với tính năng diệt khuẩn hiệu quả từ trà xanh, đẩy sạch các nguy cơ gây mụn, sần, ngứa trên da. Làm sạch nhưng không gây khô rát, khó chịu kể cả làn da nhạy cảm. Tinh chất trà xanh giúp dưỡng ẩm, giúp da khỏe mạnh và luôn rạng rỡ.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nWater, Glycerin, Stearic Acid, Myristic Acid, Lauric Acid, tinh chất tr&agrave; xanh thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nKh&aacute;ng khuẩn, trị mụn, l&agrave;m sạch c&aacute;c chất cặn b&atilde; b&aacute;m v&agrave;o lỗ ch&acirc;n l&ocirc;ng, loại bỏ b&atilde; nhờn, sẽ kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, gi&uacute;p da căng tr&agrave;n sức sống.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nL&agrave;m ướt mặt, cho một lượng sản phẩm vừa đủ v&agrave;o l&ograve;ng b&agrave;n tay tạo bọt, m&aacute;t-xa nhẹ nh&agrave;ng l&ecirc;n khu&ocirc;n mặt sau đ&oacute; rửa lại với nước.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;180ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;H&agrave;n Quốc</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;H&agrave;n Quốc</p>', 1, 2, 'tuýt', 'Mỹ', '2021-06-24 10:52:32', '2021-06-27 06:54:45'),
(48, 'SRM trắng da Ponds', 'srm-trang-da-ponds', 'P03348.jpg', 38000, 0, 'Sữa rửa mặt Pond’s trắng da tinh khiết với thành phần Carbon hoạt tính sẽ giúp da bạn trắng lên nhanh chóng và đạt đến độ tinh khiết hoàn hảo. Sản phẩm dịu nhẹ và không gây kích ứng da. Bạn sẽ cảm nhận sự khác biệt sau thời gian ngắn sử dụng.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nMyristic Acid, Glycerin, Water, Propylene Glycol, Stearic Acid, Decyl Glucoside..</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nL&agrave;m sạch s&acirc;u tận lỗ ch&acirc;n l&ocirc;ng, cho l&agrave;n da sạch s&acirc;u, khỏe khoắn, ngăn ngừa khuẩn mụn. Cho l&agrave;n da trắng s&aacute;ng tự nhi&ecirc;n.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nL&agrave;m ướt mặt, cho sữa rửa mặt v&agrave;o l&ograve;ng b&agrave;n tay, tạo bọt với nước. M&aacute;t-xa nhẹ nh&agrave;ng l&ecirc;n mặt v&agrave; cổ. Rửa sạch lại với nước.</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Trọng lượng:</strong>&nbsp;50g</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Anh</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Indonesia</p>', 1, 2, 'tuýt', 'Anh', '2021-06-24 10:55:43', '2021-07-12 02:38:47'),
(49, 'Kem dưỡng da Ponds', 'kem-duong-da-ponds', 'P03354_1_l.jpg', 111000, 0, 'Kem dưỡng da Ponds trắng hồng tinh khiết ban đêm được chiết xuất từ nhân sâm Hàn Quốc và nghệ tây có tác dụng làm mờ các vết thâm, nám trên da, đem lại cho bạn một làn da mịn màng và sáng hồng tinh khiết. Kem dưỡng da Ponds có sự kết hợp công thức dưỡng ẩm và làm mịn da, giúp cải thiện và tái tạo làn da.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nChiết xuất nghệ t&acirc;y v&agrave; nh&acirc;n s&acirc;m H&agrave;n Quốc, Stearic Acid, Glycerin, Niacinamide&hellip;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nDưỡng trắng da, nu&ocirc;i dưỡng l&agrave;n da từ b&ecirc;n trong, l&agrave;m mờ c&aacute;c vết th&acirc;m n&aacute;m gi&uacute;p da trở n&ecirc;n đều m&agrave;u, chống nắng, kh&ocirc;ng g&acirc;y cảm gi&aacute;c b&oacute;ng nhờn suốt cả ng&agrave;y.</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong><br />\r\nNơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Trọng lượng:</strong>&nbsp;50g</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Anh</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 2, 'hộp', 'Anh', '2021-06-24 10:57:05', '2021-07-12 02:39:15'),
(50, 'Kem dưỡng da chân Lana Footgel', 'kem-duong-da-chan-lana-footgel', 'P15829.jpg', 60000, 0, 'Kem dưỡng da chân Lana Footgel 1+ với thành phần tăng cường thêm dưỡng chất mềm da, giúp làm lành hiệu quả nứt nẻ da gót chân, da bàn chân. Bên cạnh đó, sản phẩm còn có tác dụng làm mềm vùng da quanh móng chân và mất hẳn hiện tượng bắp chân bị sần, “mốc trắng” do da khô.', '<p><strong>Th&agrave;nh phần:&nbsp;</strong>Aqua, Stearic Acid, Glycerin, Palmitic Acid, Glyceryl Stearate, Cetyl Palmitate, Curcuma Longa Rhizome Extract,&hellip;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:&nbsp;</strong><br />\r\nL&agrave;m l&agrave;nh hiệu quả nứt nẻ da ch&acirc;n.<br />\r\nL&agrave;m mềm v&ugrave;ng da quanh m&oacute;ng ch&acirc;n v&agrave; mất hẳn hiện tượng bắp ch&acirc;n bị sần, &ldquo;mốc trắng&rdquo; do da kh&ocirc;.<br />\r\nL&agrave;m l&agrave;nh nhanh ch&oacute;ng c&aacute;c vết nứt nẻ g&oacute;t ch&acirc;n, da b&agrave;n ch&acirc;n, c&aacute;c trường hợp &ldquo;nước ăn da&rdquo;.&nbsp;</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong><br />\r\nRửa sạch b&agrave;n ch&acirc;n, lau kh&ocirc;. Thoa kem l&ecirc;n v&ugrave;ng da ch&acirc;n bị nứt nẻ, kẽ ng&oacute;n ch&acirc;n, v&ugrave;ng da quanh m&oacute;ng ch&acirc;n v&agrave; bắp ch&acirc;n. Để kh&ocirc; tự nhi&ecirc;n.<br />\r\nMỗi ng&agrave;y thoa 1-2 lần. Thoa li&ecirc;n tục hằng ng&agrave;y đến khi l&agrave;nh hẳn.&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp</p>\r\n\r\n<p><strong>Trọng lượng:</strong>&nbsp;50g</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam&nbsp;</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam&nbsp;</p>', 1, 2, 'tuýt', 'Việt Nam', '2021-06-24 10:58:18', '2021-06-27 06:55:50'),
(51, 'Kem trị nứt gót chân', 'kem-tri-nut-got-chan', 'P01007.jpg', 26000, 0, 'Kem trị nứt gót chân Gót Sen kết hợp tinh chất Curcumin và Vitamin E giúp tái tạo tế bào da mới, giữ ẩm và làm tăng sự đàn hồi cho da, nhanh chóng làm mềm và liền các vết nứt nẻ chân tay do thời tiết khô hanh, do tiếp xúc với các chất tẩy rửa và môi trường lao động ô nhiễm.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nCurcuma Longa Extract, Propylen Glycol, GMS, PEG &ndash; 75 Lanolin, Glycerin, Cetostearyl Alcol, Acid Stearic, Nipagin, Nipasol, nước hoa.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nGiữ ẩm, l&agrave;m tăng sự đ&agrave;n hồi cho da, gi&uacute;p t&aacute;o tạo tế b&agrave;o da mới. D&ugrave;ng cho c&aacute;c trường hợp kh&ocirc; nứt da do thời tiết hoặc tiếp x&uacute;c với chất tẩy rửa v&agrave; m&ocirc;i trường lao động &ocirc; nhiễm. C&aacute;c trường hợp nứt nẻ g&oacute;t ch&acirc;n, nứt nẻ da tay, da ch&acirc;n, kh&ocirc; nẻ da mặt, ph&ograve;ng nứt nẻ da bụng, da đ&ugrave;i khi mang thai, bỏng da nhẹ.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nD&ugrave;ng 4 &ndash; 6 lần/ng&agrave;y<br />\r\nRửa sạch, lau kh&ocirc; v&ugrave;ng da nứt nẻ. Thoa một lớp kem mỏng l&ecirc;n d&ugrave;ng da cần điều trị rồi xoa b&oacute;p nhẹ nh&agrave;ng cho khi kem thấm.</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Trọng lượng:</strong>&nbsp;20g</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 2, 'tuýt', 'Việt Nam', '2021-06-24 10:59:01', '2021-06-27 06:53:25'),
(52, 'Kính chống giọt bắn Face Shield', 'kinh-chong-giot-ban-face-shield', 'P18003.jpg', 150000, 80000, 'Kính chống giọt bắn Face Shield là dụng cụ hỗ trợ đặc biệt nhằm che chắn người sử dụng trước nguy cơ phơi nhiễm các loại vi khuẩn, virus độc hại, đặc biệt là Virus Sas COVID-19 và COVID-21 hiện nay. Dụng cụ có thể tái sử dụng nhiều lần sau khi đã vệ sinh khử trùng bằng xà phòng hoặc cồn', '<p><strong>C&ocirc;ng dụng</strong><br />\r\n&ndash; Ngăn giọt bắn<br />\r\n&ndash; Ngăn sương m&ugrave;<br />\r\n&ndash; Ngăn kh&oacute;i bụi<br />\r\n&ndash; Ngăn văng dầu mỡ<br />\r\n&ndash; Ngăn kh&iacute; thải<br />\r\n&ndash; Ngăn gi&oacute; c&aacute;t</p>\r\n\r\n<p><strong>Đặc điểm</strong><br />\r\nNhựa trong suốt, che phủ to&agrave;n bộ v&ugrave;ng mắt, mũi, miệng</p>\r\n\r\n<p><strong>C&aacute;ch d&ugrave;ng</strong><br />\r\nĐeo như k&iacute;nh th&ocirc;ng thường, sử dụng khi ở m&ocirc;i trường c&oacute; nguy cơ l&acirc;y nhiễm bệnh qua đường h&ocirc; hấp hoặc c&oacute; c&aacute;t bụi, h&oacute;a chất độc hại&hellip; Sử dụng kết hợp với khẩu trang.</p>\r\n\r\n<p><strong>Đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 1 c&aacute;i</p>\r\n\r\n<p><strong>Xuất xứ:</strong>&nbsp;Trung Quốc</p>', 1, 5, 'cái', 'Trung Quốc', '2021-06-24 11:02:16', '2021-06-27 07:13:48'),
(59, 'Máy đo huyết áp bắp tay Microlife', 'may-do-huyet-ap-bap-tay-microlife', 'P16045_1_l.jpg', 1350000, 0, 'Máy đo huyết áp bắp tay Microlife BP B3 Basic là sản phẩm thế hệ mới của thương hiệu Microlife đến từ Thụy Sĩ, dùng để đo huyết áp và nhịp tim. Sản phẩm ứng dụng công nghệ MAM hiện đại thông minh mang lại cho người dùng trải nghiệm an toàn như được bác sĩ đo đạc với kết quả nhanh chóng và chính xác.', '<p><strong>Th&ocirc;ng số kỹ thuật:</strong><br />\r\nTầm đo: Huyết &aacute;p 20 &ndash; 280 mmHg<br />\r\nNhịp tim: 40 &ndash; 200 nhịp/ph&uacute;t<br />\r\nK&iacute;ch thước: 138 x 94.5 x 62.5 mm<br />\r\nTrọng lượng: 402g (bao gồm pin)<br />\r\nBộ nhớ lưu 99 lần gần nhất ghi r&otilde; ng&agrave;y giờ/th&aacute;ng năm&nbsp;<br />\r\nNguồn điện: 4 pin AA, hoặc bộ đổi nguồn DC 6V, 600 mA</p>\r\n\r\n<p><strong>Ưu điểm nổi bật:</strong><br />\r\nĐo huyết &aacute;p v&agrave; nhịp tim<br />\r\nỨng dụng c&ocirc;ng nghệ MAM th&ocirc;ng minh gi&uacute;p người d&ugrave;ng c&oacute; sự trải nghiệm tuyệt vời an to&agrave;n nhất như được b&aacute;c sĩ đo đạc, thăm kh&aacute;m bệnh (M&aacute;y sẽ tự động đo 3 lần v&agrave; lấy kết quả trung b&igrave;nh từ 3 lần cộng lại để đưa ra một kết quả ch&iacute;nh x&aacute;c nhất)<br />\r\nChức năng kiểm tra t&uacute;i hơi<br />\r\nM&agrave;n h&igrave;nh hiển thị r&otilde; r&agrave;ng, dễ d&agrave;ng xem kết quả<br />\r\nDễ sử dụng, đo một c&aacute;ch nhanh ch&oacute;ng, ch&iacute;nh x&aacute;c, đơn giản v&agrave; thoải m&aacute;i</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong>&nbsp;Đ&iacute;nh k&egrave;m trong hộp</p>\r\n\r\n<p><strong>Bảo h&agrave;nh:</strong>&nbsp;5 năm</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong>Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh nhiệt độ v&agrave; độ ẩm cao</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Thụy Sĩ&nbsp;</p>', 1, 5, 'cái', 'Thụy Sĩ', '2021-06-27 07:37:00', '2021-06-27 07:37:00'),
(53, 'Khẩu Trang 3D Mask Unicharm', 'khau-trang-3d-mask-unicharm', 'P18004.jpg', 15000, 12500, 'Khẩu Trang Ngăn Khói Bụi Unicharm 3D Mask Super Fit Size M Gói 5 Miếng TU3D00005CBđược thiết kế 3D thông minh cho phép sản phẩm ôm vừa vặn đường cong khuôn mặt, cho cảm giác thoải mái, dễ chịu, không gây bí thở mà vẫn đảm bảo giữ nhiệt và độ ẩm cho mũi và họng vào mùa đông.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nPolyester, Polyolefin, lớp lọc.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nNgăn chặn c&aacute;c vi-r&uacute;t g&acirc;y bệnh, ngăn kh&oacute;i bụi, phấn hoa g&acirc;y dị ứng.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nMở rộng khẩu trang từ vị tr&iacute; mũi t&ecirc;n, lần lượt đeo hai quai v&agrave;o v&agrave;nh tai, điều chỉnh cho vừa vặn.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, sạch sẽ, tr&aacute;nh &aacute;nh nắng v&agrave; nhiệt độ cao.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Nhật Bản</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Nhật Bản</p>', 1, 5, 'gói', 'Nhật Bản', '2021-06-24 11:07:37', '2021-07-12 02:07:15'),
(54, 'Phosphalugel', 'phosphalugel', 'P18005.jpg', 104000, 0, 'Phosphalugel là một thuốc kháng axit. Nó làm giảm độ axit của dạ dày, điều trị cơn đau, bỏng rát và tình trạng khó chịu do axit gây ra ở dạ dày hoặc thực quản.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nQu&aacute; mẫn cảm với Aluminium Phosphate hoặc với bất kỳ th&agrave;nh phần n&agrave;o của thuốc. Nếu bạn bị bệnh thận nặng.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nPhosphalugel l&agrave; một thuốc kh&aacute;ng axit. N&oacute; l&agrave;m giảm độ axit của dạ d&agrave;y, điều trị cơn đau, bỏng r&aacute;t v&agrave; t&igrave;nh trạng kh&oacute; chịu do axit g&acirc;y ra ở dạ d&agrave;y hoặc thực quản.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nUống 1 &ndash; 2 g&oacute;i ng&agrave;y uống 2 &ndash; 3 lần. N&ecirc;n d&ugrave;ng khi xuất hiện cơn đau hoặc theo hướng dẫn của B&aacute;c sĩ.<br />\r\n<strong>Thận trọng:</strong><br />\r\nBệnh nh&acirc;n mắc bệnh di truyền hiếm gặp kh&ocirc;ng dung nạp Fructose th&igrave; kh&ocirc;ng n&ecirc;n d&ugrave;ng thuốc n&agrave;y.<br />\r\nThuốc n&agrave;y chứa Sorbitol n&ecirc;n c&oacute; thể g&acirc;y ảnh hưởng nhẹ tr&ecirc;n đường ti&ecirc;u h&oacute;a (ti&ecirc;u chảy).<br />\r\nThận trọng khi sử dụng cho phụ nữ c&oacute; thai v&agrave; phụ nữ đang cho con b&uacute;.<br />\r\nKh&ocirc;ng uống thuốc kh&aacute;c Axit c&ugrave;ng l&uacute;c với thuốc kh&aacute;c (&iacute;t nhất c&aacute;ch 2 giờ).<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:</strong><br />\r\nColloidal aluminium Phosphate gel 20%: 12.380g, t&aacute; dược vừa đủ.<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 26 g&oacute;i<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Đức<br />\r\n<strong>Nh&agrave; sản xuất:&nbsp;</strong>Boehringer Ingelheim</p>', 1, 1, 'hộp', 'Đức', '2021-06-25 07:39:44', '2021-06-27 06:48:22'),
(55, 'Misanlugel', 'misanlugel', 'P18006.jpg', 65000, 0, 'Hỗn dịch uống điều trị viêm dạ dày cấp và mạn tính Misanlugel hỗ trợ điều trị viêm thực quản, viêm dạ dày cấp và mạn tính, loét dạ dày – tá tràng, kích ứng dạ dày, điều trị các hội chứng thừa acid như rát bỏng, ợ chua, và hiện tượng tiết nhiều acid, ví dụ như trong thời kỳ thai nghén, điều trị những rối loạn của dạ dày do thuốc, do sai chế độ ăn hoặc sau khi dùng quá nhiều nicotin, cafe, kẹo, hoặc thức ăn quá nhiều gia vị, điều trị biến chứng của thoát vị cơ hoành, viêm đại tràng (bệnh Crohn).', '<p><strong>1. Chống chỉ định:</strong><br />\r\nC&aacute;c trường hợp mẫn cảm với nh&ocirc;m hoặc c&aacute;c chế phẩm chứa nh&ocirc;m.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:</strong><br />\r\n<strong>Chỉ định:</strong><br />\r\nĐiều trị vi&ecirc;m thực quản, vi&ecirc;m dạ d&agrave;y cấp v&agrave; mạn t&iacute;nh, lo&eacute;t dạ d&agrave;y &ndash; t&aacute; tr&agrave;ng, k&iacute;ch ứng dạ d&agrave;y.<br />\r\nĐiều trị c&aacute;c hội chứng thừa acid như r&aacute;t bỏng, ợ chua, v&agrave; hiện tượng tiết nhiều acid, v&iacute; dụ như trong thời kỳ thai ngh&eacute;n.<br />\r\nĐiều trị những rối loạn của dạ d&agrave;y do thuốc, do sai chế độ ăn hoặc sau khi d&ugrave;ng qu&aacute; nhiều nicotin, cafe, kẹo, hoặc thức ăn qu&aacute; nhiều gia vị.<br />\r\nĐiều trị biến chứng của tho&aacute;t vị cơ ho&agrave;nh, vi&ecirc;m đại tr&agrave;ng (bệnh Crohn).<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nD&ugrave;ng 1-2 g&oacute;i hỗn dịch uống giữa c&aacute;c bữa ăn hoặc khi thấy kh&oacute; chịu. Bệnh trĩ m&atilde;n: 2 vi&ecirc;n/ng&agrave;y.<br />\r\n<strong>Thận trọng:</strong><br />\r\nTr&aacute;nh điều trị liều cao l&acirc;u ng&agrave;y ở người suy thận.<br />\r\nT&aacute;c dụng kh&ocirc;ng mong muốn<br />\r\nThường gặp: T&aacute;o b&oacute;n.<br />\r\n<strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc;, dưới 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:</strong><br />\r\n<strong>Th&agrave;nh phần:</strong><br />\r\nNh&ocirc;m phosphat gel 20% &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..12,4 g<br />\r\nT&aacute; dược &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.vừa đủ 1 g&oacute;i<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 26 g&oacute;i x 20g<br />\r\n<strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;Hasan &ndash; Dermapharm</p>', 1, 1, 'hộp', 'Việt Nam', '2021-06-25 07:41:01', '2021-06-27 06:48:51'),
(56, 'Chỉ nha khoa Oral-B', 'chi-nha-khoa-oral-b', 'P00989_1_l.jpg', 53550, 0, 'Chỉ nha khoa Oral-B Essential Floss có tác dụng làm sạch giữa các khe răng của bạn cho sức khỏe răng miệng tối ưu! Với chiều dài 50m được quấn gọn trong hộp nhỏ, giúp bạn dễ dàng đem theo bên mình, tiện lợi mọi lúc mọi nơi. Sản phẩm đem lại cho bạn cảm giác thoải mái và hơi thở thơm tho.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nNylon, Pebax, Microcrystaline Wax, Mint Flavor, hương bạc h&agrave;.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nL&agrave;m sạch mảng b&aacute;m thức ăn dư thừa trong kẽ răng.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nLấy một đoạn chỉ d&agrave;i khoảng 30 &ndash; 40cm, cuộn 2 đầu chỉ v&agrave;o ng&oacute;n tay rồi căng ra, k&eacute;o nhẹ nh&agrave;ng để chỉ lọt v&agrave;o kẽ răng, rồi đẩy l&ecirc;n xuống v&agrave;i lần.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nBảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp v&agrave; nhiệt độ cao</p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Hộp 50m</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Ireland</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Ireland</p>', 1, 3, 'chai', 'Ireland', '2021-06-25 07:43:02', '2021-06-27 07:00:27'),
(57, 'Nước Rửa Tay Khô Green Cross', 'nuoc-rua-tay-kho-green-cross', 'P18007.jpg', 24500, 0, 'Gel rửa tay hương tự nhiên Green Cross ra đời như một cứu cánh giúp bạn vệ sinh đôi bàn tay sạch sẽ, tránh được các bệnh truyền nhiễm hiệu quả. Sản phẩm có hương thơm tự nhiên, tinh khiết, nhẹ nhàng.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nEthanol, Water, Glycerin, Carbomer, Fragrance, Triethanolamine, hương tổng hợp&hellip;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nL&agrave;m sạch vi khuẩn v&agrave; bụi bẩn b&aacute;m tr&ecirc;n tay, ngăn ngừa c&aacute;c bệnh l&acirc;y nhiễm qua tay.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nCho một lượng nhỏ tr&ecirc;n tay rồi xoa đều, kh&ocirc;ng cần rửa lại với nước.</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp v&agrave; nơi c&oacute; nhiệt độ cao.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;100ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;USA</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 3, 'chai', 'Việt Nam', '2021-06-25 07:46:04', '2021-06-27 07:01:53'),
(58, 'Sữa Rửa Tay Lifebuoy', 'sua-rua-tay-lifebuoy', '0000441.png', 31000, 0, 'Sữa rửa tay Lifebuoy 180g bảo vệ vượt trội bảo vệ vi khuẩn và đem lại cảm giác sảng khoái suốt cả ngày. Thường xuyên rửa tay với nước rửa tay Lifebuoy bảo vệ vượt trội giúp phòng ngừa các bệnh về tiêu hóa, bảo vệ sức khỏe cho cả gia đình.', '<p><strong>Th&agrave;nh phần:&nbsp;</strong><br />\r\nWater, Myristic Acid, Lauric Acid, Palmitic Acid, Glycerin, Citric Acid, Silver Oxide&hellip;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nL&agrave;m sạch vi khuẩn g&acirc;y bệnh, giữ cho tay lu&ocirc;n sạch sẽ.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng: &nbsp;</strong><br />\r\nVặn mở nắp chai, cho một &iacute;t nước rửa tay v&agrave;o l&ograve;ng b&agrave;n tay, tạo bọt trong 10 gi&acirc;y rồi rửa lại với nước.&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong><br />\r\nĐể nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp v&agrave; nhiệt độ cao.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;177ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;H&agrave; Lan</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 3, 'chai', 'Việt Nam', '2021-06-25 07:48:14', '2021-06-27 07:02:22'),
(60, 'Máy đo huyết áp điện tử cổ tay Microlife', 'may-do-huyet-ap-dien-tu-co-tay-microlife', 'P16046_1_l.jpg', 920000, 0, 'Máy đo huyết áp điện tử cổ tay Microlife W3 Comfort là sản phẩm thế hệ mới của thương hiệu Microlife đến từ Thụy Sĩ. Máy đo huyết áp hoàn toàn tự động với thiết kế siêu mỏng nhẹ, được trang bị công nghệ IMT độc đáo, cải tiến cách đo huyết áp truyền thống, mang lại trải nghiệm đo huyết áp thoải mái, dễ chịu, cùng kết quả nhanh chóng và chính xác.', '<p><strong>Th&ocirc;ng số kỹ thuật:</strong><br />\r\nTầm đo: Huyết &aacute;p 20 &ndash; 280 mmHg<br />\r\nNhịp tim: 40 &ndash; 200 nhịp/ph&uacute;t<br />\r\nBộ nhớ: Tự động lưu kết quả 30 lần đo gần nhất c&ugrave;ng ng&agrave;y giờ<br />\r\nNguồn điện: Sử dụng pin AA</p>\r\n\r\n<p><strong>Ưu điểm nổi bật:&nbsp;</strong><br />\r\nĐược trang bị c&ocirc;ng nghệ IMT (c&ocirc;ng nghệ đo trong l&uacute;c bơm)<br />\r\nChức năng kiểm tra t&uacute;i hơi<br />\r\nThiết kế si&ecirc;u mỏng v&agrave; nhẹ<br />\r\nM&agrave;n h&igrave;nh hiển thị r&otilde; r&agrave;ng, dễ d&agrave;ng xem kết quả<br />\r\nM&aacute;y đo tự động, mang lại trải nghiệm đo huyết &aacute;p thoải m&aacute;i, dễ chịu, thời gian đo ngắn hơn với c&ugrave;ng mức ch&iacute;nh x&aacute;c</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong>&nbsp;Đ&iacute;nh k&egrave;m trong hộp</p>\r\n\r\n<p><strong>Bảo h&agrave;nh:</strong>&nbsp;5 năm</p>\r\n\r\n<p><strong>Bảo quản:&nbsp;</strong>Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh nhiệt độ v&agrave; độ ẩm cao</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Thụy Sĩ&nbsp;</p>', 1, 5, 'cái', 'Thụy Sĩ', '2021-06-27 07:37:57', '2021-06-27 07:37:57'),
(61, 'Máy đo huyết áp Kachi MK167', 'may-do-huyet-ap-kachi-mk167', 'P19794_1_l.jpg', 599000, 0, 'Máy đo huyết áp Kachi MK167 với công nghệ hiện đại tự động hoàn toàn và độ chính xác cao, đặc biệt có giọng nói hướng dẫn, giúp đo huyết áp tại nhà đơn giản và nhanh chóng, phù hợp để kiểm soát và theo dõi hiệu quả điều trị cao huyết áp.', '<p><strong>C&ocirc;ng dụng</strong><br />\r\nĐo huyết &aacute;p, c&oacute; giọng n&oacute;i th&ocirc;ng b&aacute;o qu&aacute; tr&igrave;nh đo.</p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng nổi bật của M&aacute;y đo huyết &aacute;p Kachi MK167<br />\r\n&ndash; M&aacute;y hiển thị đ&egrave;n chỉ dẫn quấn v&ograve;ng b&iacute;t cho người d&ugrave;ng dễ d&agrave;ng thao t&aacute;c khi sử dụng m&aacute;y. M&aacute;y đo huyết &aacute;p tự động Kachi MK-167 c&oacute; bộ nhớ lưu lại kết quả đo trước 99 lần/người.<br />\r\n&ndash; M&agrave;n h&igrave;nh LCD hiển thị cực rộng &amp; lớn, gi&uacute;p bạn đọc được c&aacute;c chỉ số đo một c&aacute;ch r&otilde; r&agrave;ng nhất, ph&ugrave; hợp cho mọi đối tượng sử dụng kể cả người gi&agrave; mắt k&eacute;m. C&aacute;c n&uacute;t điều khiển cũng được thiết kế lớn, điều chỉnh nhẹ nh&agrave;ng cho bạn thao t&aacute;c tr&ecirc;n m&aacute;y một c&aacute;ch thoải m&aacute;i nhất.<br />\r\n&ndash; M&aacute;y đo huyết &aacute;p tự động Kachi MK167 c&oacute; biểu tượng b&aacute;o huyết &aacute;p cao hiển thị sau qu&aacute; tr&igrave;nh đo gi&uacute;p người bệnh biết được mức huyết &aacute;p v&agrave; uống thuốc kịp thời. M&aacute;y hiển thị biểu tượng v&ograve;ng b&iacute;t đ&uacute;ng cho kết quả th&ecirc;m ch&iacute;nh x&aacute;c.<br />\r\n&ndash; M&aacute;y đo huyết &aacute;p tự động Kachi MK167 sử dụng 4 pin AAA, c&oacute; cổng nguồn DC-6V d&ugrave;ng trực tiếp khi cần (tiết kiệm pin). M&aacute;y c&oacute; n&uacute;t chuyển đổi người d&ugrave;ng, c&oacute; thể sử dụng cho 2 đối tượng ri&ecirc;ng biệt.<br />\r\n&ndash; M&aacute;y c&oacute; ống dẫn kh&iacute; để lắp ống kh&iacute; bơm nhả kh&iacute; lớp vải đo huyết &aacute;p. Đi k&egrave;m lớp vải đo huyết &aacute;p chất lượng, mềm &amp; chắn chắn.</p>\r\n\r\n<p><strong>Th&ocirc;ng tin kỹ thuật</strong><br />\r\nChất liệu: Nhựa ABS<br />\r\nK&iacute;ch thước sản phẩm: 13.6 x 9.8 x 7.15 cm<br />\r\nK&iacute;ch thước đ&oacute;ng g&oacute;i: 16.5 x 19 x 8.5 cm<br />\r\nKhối lượng (N.w): 288gr<br />\r\nKhối lượng (G.w): 480gr</p>', 1, 5, 'cái', 'Anh', '2021-06-27 07:38:48', '2021-07-12 02:36:10'),
(62, 'Sữa Ensure Vanilla 237Ml', 'sua-ensure-vanilla-237ml', '00006974.jpg', 330000, 0, 'Sữa Ensure 237ml hương vani dạng lỏng là sản phẩm dinh dưỡng đầy đủ và cân đối cho người lớn, hỗ trợ tiêu hóa, thích hợp để thay thế hoàn toàn bữa ăn hoặc dùng ăn bổ sung cho người cần cải thiện tình trạng dinh dưỡng, người bệnh cần phục hồi nhanh.', '<h5>Th&agrave;nh phần</h5>\r\n\r\n<p>Năng lượng 250 kcal; Chất đạm 9g; Chất b&eacute;o 6g; Chất b&eacute;o no 1g; Chất b&eacute;o đơn chức kh&ocirc;ng no 2g; Chất b&eacute;o đa chức kh&ocirc;ng no 3g; Bột đường 42g; Chất xơ thực phẩm 3 g; Vitamin A 1250IU; Vitamin D 160IU; Vitamin E 7,5IU; Vitamin K 20mcg; Vitamin C 30mg; Vitamin B1 0,38mg; Vitamin B2 0,43mg; Vitamin B6 0,50mg; Vitamin B12 1,5mcg; Niacin 5,0mg; Acid pantothenic 2,5mg; Acid folic 100mcg ; Biotin 75mcg; Cholin 82mg; Natri 200mg; Kali 370mg; Clo 270mg; Canxi 300mg; Phospho 250mg; Magi&ecirc; 100mg; Sắt 4,5mg; Kẽm 3,8mg; Mangan 1,2mg; Đồng 0,50mg; Iod 38mcg; Selen 18mcg; Cr&ocirc;m 30mcg; Molybden 38mcg.</p>\r\n\r\n<p>Kh&ocirc;ng chứa Gluten v&agrave; Lactose.&nbsp;</p>\r\n\r\n<h5>C&ocirc;ng dụng</h5>\r\n\r\n<p>Ensure dạng lỏng l&agrave; sản phẩm dinh dưỡng đầy đủ v&agrave; c&acirc;n đối cho người lớn, hỗ trợ ti&ecirc;u h&oacute;a, th&iacute;ch hợp để thay thế ho&agrave;n to&agrave;n bữa ăn hoặc d&ugrave;ng ăn bổ sung cho người cần cải thiện t&igrave;nh trạng dinh dưỡng, người bệnh cần phục hồi nhanh.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Mỹ</p>\r\n\r\n<p>&nbsp;</p>', 1, 4, 'chai', 'Mỹ', '2021-06-27 07:40:26', '2021-07-12 02:40:58'),
(63, 'Sữa Abbott Ensure Gold', 'sua-abbott-ensure-gold', '00013645.jpg', 343000, 0, 'Sữa bột Ensure Gold hương vani là nguồn dinh dưỡng đầy đủ và cân đối, giúp đáp ứng nhu cầu dinh dưỡng cho người lớn tuổi, hỗ trợ tăng cường sức khỏe, thể chất và chất lượng cuộc sống, được chứng minh qua 30 nghiên cứu lâm sàng trong suốt 45 năm.', '<p><strong>C&ocirc;ng dụng:</strong>&nbsp;Bổ sung dinh dưỡng đầy đủ v&agrave; c&acirc;n đối, vitamin, kho&aacute;ng chất gi&uacute;p phục hồi v&agrave; tăng cường sức khỏe.</p>\r\n\r\n<p><strong>Đối tượng sử dụng:</strong>&nbsp;Người lớn, người ăn uống k&eacute;m, người bệnh cần phục hồi nhanh.</p>\r\n\r\n<p>Ch&uacute; &yacute;: Sử dụng Ensure Gold dưới sự gi&aacute;m s&aacute;t của b&aacute;c sỹ hoặc chuy&ecirc;n gia dinh dưỡng, cần theo đ&uacute;ng c&aacute;c hướng dẫn.</p>\r\n\r\n<p>Kh&ocirc;ng d&ugrave;ng cho người bệnh Galactosemia<br />\r\nKh&ocirc;ng d&ugrave;ng qua đường tĩnh mạch<br />\r\nKh&ocirc;ng d&ugrave;ng cho trẻ em trừ khi c&oacute; hướng dẫn của chuy&ecirc;n gia y tế</p>\r\n\r\n<p><strong>Th&agrave;nh phần</strong></p>\r\n\r\n<p>HMB (&beta;-hydroxy-&beta;-methylbutyrat) v&agrave; protein chất lượng cao, hỗ trợ x&acirc;y dựng v&agrave; ph&aacute;t triển khối cơ<br />\r\n28 vitamin v&agrave; kho&aacute;ng chất thiết yếu gi&uacute;p cơ thể khỏe mạnh. Gi&agrave;u Canxi, Phospho v&agrave; Vitamin D gi&uacute;p xương chắc khỏe<br />\r\nChất xơ FOS nu&ocirc;i dưỡng vi sinh vật c&oacute; lợi v&agrave; gi&uacute;p hệ ti&ecirc;u h&oacute;a khỏe mạnh<br />\r\nC&aacute;c chất chống oxi &ndash; h&oacute;a (beta caroten, vitamin C, E, kẽm v&agrave; selen) gi&uacute;p bảo vệ cơ thể<br />\r\nHỗn hợp chất b&eacute;o thực vật gi&agrave;u acid b&eacute;o Omega 3-6-9 tốt cho tim mạch. H&agrave;m lượng acid b&eacute;o no v&agrave; cholesterol thấp c&oacute; lợi cho chế độ ăn l&agrave;nh mạnh</p>\r\n\r\n<p>Th&agrave;nh phần chi tiết: Tinh bột bắp thuỷ ph&acirc;n, sucrose, DẦU THỰC VẬT (dầu hướng dương gi&agrave;u oleic, dầu đậu n&agrave;nh, dầu hạt cải), natri caseinat, đạm đậu n&agrave;nh tinh chế, oligofructose, đạm whey c&ocirc; đặc, KHO&Aacute;NG CHẤT (kali citrat, tricanxi phosphat, magi&ecirc; sulfat, magi&ecirc; clorid, kali clorid, natri citrat, dikali hydrophosphat, natri clorid, canxi carbonat, kali hydroxid, sắt sulfat, kẽm sulfat, mangan sulfat, đồng sulfat, cr&ocirc;m clorid, kali iodid, natri molybdat, natri selenat), anxi &beta;-hydroxy-&beta;-methylbutyrat monohydrat (CaHMB), hương vani tổng hợp, cholin clorid, taurin, I-car nitin, VITAMIN (acid ascorbic, ascorbyl palmitat, vitamin E, hỗn hợp tocopherol, canxi pantothenat, niacinamid, pyridoxin hydroclorid, riboflavin, thiamin hydroclorid, vitamin A palmitat, vitamin D3, acid folic, beta caroten, phylloquinon, biotin, cyanocobalamin.</p>\r\n\r\n<p>Kh&ocirc;ng chứa Trans Fat (acid b&eacute;o chuyển đổi)<br />\r\nKh&ocirc;ng chứa Gluten v&agrave; Lactose</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong>&nbsp;Để pha 230 ml, cho 185 ml nước ch&iacute;n nguội v&agrave;o ly, vừa từ từ cho v&agrave;o 6 muỗng gạt ngang (muỗng c&oacute; sẵn trong hộp, mỗi muỗng tương đương 10,1 g) hay 60,6 g bột Ensure vừa khuấy đều cho đến khi bột tan hết.</p>\r\n\r\n<p>Khi pha theo đ&uacute;ng hướng dẫn, 1ml Ensure Gold cung cấp khoảng 1,14 kcal. Hộp 400g bột c&oacute; thể pha được khoảng 7 ly, mỗi ly 230 ml.</p>\r\n\r\n<p><em><strong>&ndash; Bổ sung chế độ ăn:</strong></em>&nbsp;2 ly/ ng&agrave;y hoặc theo hướng dẫn của chuy&ecirc;n vi&ecirc;n dinh dưỡng.</p>\r\n\r\n<p><em><strong>&ndash; Thay thế ho&agrave;n to&agrave;n bữa ăn qua ống th&ocirc;ng cho người bệnh:</strong></em>&nbsp;Theo hướng dẫn của b&aacute;c sỹ hoặc chuy&ecirc;n vi&ecirc;n dinh dưỡng.</p>\r\n\r\n<p><strong>Khối lượng:</strong>&nbsp;400g</p>\r\n\r\n<p><strong>Bảo quản</strong><br />\r\nBảo quản hộp chưa mở ở nhiệt độ ph&ograve;ng. Hộp đ&atilde; mở phải được đậy k&iacute;n v&agrave; bảo quản ở nơi kh&ocirc; m&aacute;t, nhưng kh&ocirc;ng cho v&agrave;o tủ lạnh. Khi đ&atilde; mở phải sử dụng trong v&ograve;ng 3 tuần. Ensure Gold đ&atilde; pha phải d&ugrave;ng ngay hay đậy k&iacute;n cho v&agrave;o tủ lạnh v&agrave; d&ugrave;ng trong v&ograve;ng 24 giờ.</p>\r\n\r\n<p>Xuất xứ thương hiệu: Mỹ</p>\r\n\r\n<p>&nbsp;</p>', 1, 4, 'hộp', 'Mỹ', '2021-07-06 06:47:12', '2021-07-12 02:43:18'),
(65, 'Re-Energize hương cam', 're-energize-huong-cam', 'P18564_4.jpg', 64000, 0, 'Thực phẩm bảo vệ sức khỏe Re-Energize hương cam hỗ trợ tăng cường sức khỏe.', '<p><strong>Th&agrave;nh phần</strong><br />\r\nMỗi vi&ecirc;n n&eacute;n sủi 4,7g gồm:<br />\r\nCanxi (Calci carbonat) 100mg<br />\r\nMagi&ecirc; (Magnesi carbonat) 100mg<br />\r\nKẽm (kẽm gluconate) 10mg<br />\r\nVitamin C (Acid ascorbic) 500mg<br />\r\nNicotinamide 20mg<br />\r\nAcid pantothenic (Vitamin B5) 23mg<br />\r\nVitamin B2 (Riboflavin) 15mg<br />\r\nVitamin B1 (Thiamin hydroclorid) 15mg<br />\r\nVitamin B6 (Pyridoxin hydroclorid) 10mg<br />\r\nVitamin B9 (Acid folic) 400mcg<br />\r\nBiotin 150mcg<br />\r\nVitamin B12 (Cyanocobalamin) 10mcg<br />\r\nPhụ liệu: Sucralose, natri saccarin, acid citric, natri hydrocarbonat, natri carbonat, kali hydrocarbonat, HPMC (hydroxy propyl methyl cellulose), tween 80 (polyoxyethylene sorbitan monooleat), PEG 6000 (polyethylen glycol), sunset yellow, hương cam tổng hợp vừa đủ.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng</strong><br />\r\nGi&uacute;p bổ sung vitamin v&agrave; kho&aacute;ng chất, hỗ trợ tăng cường sức khỏe, n&acirc;ng cao sức đề kh&aacute;ng.</p>\r\n\r\n<p><strong>Đối tượng sử dụng</strong><br />\r\nTrẻ em tr&ecirc;n 12 tuổi v&agrave; người lớn c&oacute; nhu cầu bổ sung vitamin v&agrave; kho&aacute;ng chất. Người mới ốm dậy, sức đề kh&aacute;ng k&eacute;m.</p>\r\n\r\n<p><strong>C&aacute;ch d&ugrave;ng</strong><br />\r\nUống 1 vi&ecirc;n n&eacute;n sủi mỗi ng&agrave;y. N&ecirc;n uống sau khi ăn s&aacute;ng hoặc ăn trưa. H&ograve;a tan ho&agrave;n to&agrave;n vi&ecirc;n sủi trong 1 ly nước khoảng 200ml trước khi uống.</p>\r\n\r\n<p>Chỉ d&ugrave;ng sản phẩm trong v&ograve;ng 1 th&aacute;ng sau khi mở nắp.<br />\r\nCảnh b&aacute;o về sức khỏe: Kh&ocirc;ng d&ugrave;ng cho người mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của sản phẩm.</p>\r\n\r\n<p><em><strong>Lưu &yacute;: Thực phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc v&agrave; kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh.</strong></em></p>\r\n\r\n<p><strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc;, tr&aacute;nh &aacute;nh s&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30 độ C. Đ&oacute;ng chặt nắp sau mỗi lần sử dụng.</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam</p>', 1, 4, 'tuýt', 'Việt Nam', '2021-07-06 06:40:24', '2021-07-12 02:44:37'),
(67, 'Nhiệt kế hồng ngoại đo trán Microlife', 'nhiet-ke-hong-ngoai-do-tran-microlife', 'P08456_1_l.jpg', 1000000, 899000, 'Nhiệt kế hồng ngoại đo trán Microlife FR1MF1 là sản phẩm đo thân nhiệt cao cấp của Microlife, được thiết kế sang trọng, công nghệ cảm ứng nhiệt hồng ngoại vùng trán thông minh không cần chạm, cho kết quả nhanh và chính xác trong 1 giây. Nhiệt kế hồng ngoại đo trán FR1MF1 sử dụng phụ hợp cho mọi lứa tuổi, mọi lúc, mọi nơi.', '<p><strong>Th&agrave;nh phần:</strong><br />\r\nM&aacute;y đo nhiệt độ, 2 pin AAA, k&ecirc; đựng.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\nĐo th&acirc;n nhiệt v&ugrave;ng tr&aacute;n kh&ocirc;ng tiếp x&uacute;c, nhiệt độ m&ocirc;i trường.</p>\r\n\r\n<p><strong>Đặc t&iacute;nh kỹ thuật:</strong><br />\r\nCảm biến hồng ngoại cho kết quả trong 1 gi&acirc;y, đ&egrave;n Led, 30 lần nhớ k&egrave;m ng&agrave;y giờ, th&aacute;ng, năm. B&aacute;o sốt bằng &acirc;m thanh v&agrave; m&agrave;u nền.</p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\nĐọc kỹ tờ hướng dẫn sử dụng c&oacute; trong hộp.&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng v&agrave; nhiệt độ cao.</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Thụy Sĩ</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Trung Quốc</p>', 1, 5, 'cái', 'Trung Quốc', '2021-07-20 17:03:37', '2021-07-20 17:03:37'),
(68, 'Cao sao vàng', 'cao-sao-vang', 'P09708_1.jpg', 5900, 0, 'Cao sao vàng là một sản phẩm cần phải có trong mọi gia đình. Với công thức bào chế riêng biệt, cao sao vàng của Dược phẩm OPC có thể sử dụng trong rất nhiều trường hợp, chuyên trị từ cảm cúm, nhức đầu, sổ mũi, cho đến buồn nôn, say tàu xe hay bị muỗi, côn trùng đốt.', '<p><strong>1. Chống chỉ định:</strong><br />\r\nKh&ocirc;ng d&ugrave;ng cho trẻ em dưới 2 tuổi. Kh&ocirc;ng b&ocirc;i v&agrave;o mắt.<br />\r\nKh&ocirc;ng d&ugrave;ng cho người mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của thuốc.</p>\r\n\r\n<p><strong>2. Hướng dẫn sử dụng:<br />\r\nChỉ định:</strong><br />\r\nCảm c&uacute;m, nhức đầu, sổ mũi, buồn n&ocirc;n, đau bụng, say t&agrave;u xe, bị muỗi v&agrave; c&aacute;c c&ocirc;n tr&ugrave;ng kh&aacute;c đốt.<br />\r\n<strong>Liều d&ugrave;ng v&agrave; c&aacute;ch d&ugrave;ng:</strong><br />\r\nNhức đầu, ch&oacute;ng mặt, cảm c&uacute;m, say t&agrave;u xe: Xoa dầu cao v&agrave;o hai b&ecirc;n th&aacute;i dương v&agrave; g&aacute;y, xoa v&agrave;o lỗ mũi.<br />\r\nKhi bị muỗi v&agrave; c&aacute;c c&ocirc;n tr&ugrave;ng kh&aacute;c đốt: Xoa dầu cao v&agrave;o chỗ bị đốt.<br />\r\nC&oacute; thể x&ocirc;ng: Cho khoảng 1 &ndash; 2 đầu đũa dầu cao v&agrave;o nước s&ocirc;i để x&ocirc;ng khi bị cảm c&uacute;m.<br />\r\n<strong>Bảo quản:</strong><br />\r\nNơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, nhiệt độ dưới 30 độ C.</p>\r\n\r\n<p><strong>3. Th&ocirc;ng tin thuốc:<br />\r\nTh&agrave;nh phần:</strong><br />\r\nCamphor 849,20 mg<br />\r\nMenthol 425,20 mg<br />\r\nTinh dầu Bạc h&agrave; 525,20 mg<br />\r\nTinh dầu Tr&agrave;m 210,80 mg<br />\r\nTinh dầu Hương nhu 130,80 mg<br />\r\nTinh dầu Quế 53,20 mg<br />\r\nT&aacute; dược vừa đủ 4g<br />\r\n(Dầu parafin, s&aacute;p ong v&agrave;ng, lanolin, vaselin, parafin rắn)<br />\r\n<strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;Lọ 4g<br />\r\n<strong>Xuất xứ thương hiệu:&nbsp;</strong>Việt Nam<br />\r\n<strong>Nh&agrave; sản xuất:</strong>&nbsp;OPC</p>', 1, 1, 'hộp', 'Việt Nam', '2021-07-25 11:28:48', '2021-07-25 11:28:48'),
(69, 'SRM Dưỡng Trắng Hada Labo', 'srm-duong-trang-hada-labo', '00016644.jpg', 67000, 0, 'Sữa rửa mặt trắng da Hada Labo Perfect White Cleanser với Công nghệ Amino Acid nhẹ nhàng loại bỏ lớp tế bào chết, bụi bẩn. Arbutin tinh khiết, Vitamin C và B3 dưỡng trắng các vùng da sậm màu. HA và Nano HA cung cấp độ ẩm toàn diện.', '<h5>Th&agrave;nh phần của Hada Labo Perfect White</h5>\r\n\r\n<p>Water, Glycerin, Sodium Cocoyl Glycinate, Butylene Glycol, Hydroxypropyl Starch Phosphate, Cocamidopropyl Betaine, PEG-20M, Lauryl Glucoside, Sodium Lauroyl Aspartate, Potassium Cocoyl Glycinate, Sodium Stearoyl Glutamate, Glyceryl Stearate, Arbutin, Niacinamide, Magnesium Ascorbyl Phosphate, Sodium Hyaluronate, Hydrolyzed Hyaluronic Acid, Algae Extract, Acanthopanax Senticosus (Eleuthero) Root Extract, Lentinus Edodes (Shiitake Mushroom) Extract, Ganoderma Lucidum (Reishi Mushroom) Stem Extract, Ginkgo Biloba Leaf Extract, Morus Nigra (Black Mulberry) Leaf Extract, Cynara Scolymus (Artichoke) Leaf Extract, Cananga Odorata (Ylang Ylang) Flower Oil, Propylene Glycol, Sorbitol, Hydroxypropyltrimonium Hyaluronate, Hydroxypropyl Methylcellulose, Polyquaternium-7, Polyquaternium-52, PEG-32, BHT, Disodium EDTA, Citric Acid, Lauric Acid, Stearic Acid, Methylparaben, Propylparaben.</p>\r\n\r\n<h5>C&ocirc;ng dụng của Hada Labo Perfect White</h5>\r\n\r\n<p>Nhẹ nh&agrave;ng l&agrave;m sạch, loại bỏ c&aacute;c bụi bẩn dầu thừa s&acirc;u trong lỗ ch&acirc;n l&ocirc;ng.</p>\r\n\r\n<p>Cung cấp độ ẩm to&agrave;n diện gi&uacute;p l&agrave;n da ẩm mượt, s&aacute;ng mịn.</p>\r\n\r\n<p>Gi&uacute;p oxy h&oacute;a, dưỡng trắng v&ugrave;ng da sậm m&agrave;u.</p>\r\n\r\n<h5>Liều d&ugrave;ng</h5>\r\n\r\n<p>L&agrave;m ướt mặt, lấy một lượng kem vừa đủ v&agrave;o l&ograve;ng b&agrave;n tay, tạo bọt, thoa đều l&ecirc;n mặt, rửa sạch với nước.</p>\r\n\r\n<p>N&ecirc;n d&ugrave;ng kết hợp với dung dịch/sữa/kem Hada Labo PERFECT WHITE để đạt hiệu quả tối ưu.</p>\r\n\r\n<h5>Lưu &yacute;</h5>\r\n\r\n<p>Sản phẩm chỉ d&ugrave;ng ngo&agrave;i da. Tr&aacute;nh để tiếp x&uacute;c trực tiếp với mắt.&nbsp;</p>\r\n\r\n<p>Đọc kỹ hướng dẫn sử dụng trước khi d&ugrave;ng.</p>\r\n\r\n<h5>Nh&agrave; sản xuất</h5>\r\n\r\n<p>V.Rohto</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 1, 2, 'tuýt', 'Việt Nam', '2021-07-27 15:54:37', '2021-07-27 15:57:22');
