INSERT INTO `tin_tuc` (`id`, `tieu_de`, `tom_tat`, `chi_tiet`, `tac_gia`, `nhan_vien`, `hinh_tin_tuc`, `trang_thai`, `created_at`, `updated_at`) VALUES
(2, 'Thời điểm uống cà phê gây hại cho sức khỏe.', 'Uống cạn tách cà phê trước bữa ăn đầu tiên trong ngày là một sai lầm lớn.', '<p>Đối với nhiều người, ng&agrave;y mới chỉ bắt đầu sau khi họ thưởng thức một t&aacute;ch c&agrave; ph&ecirc;. Nghi&ecirc;n cứu được c&ocirc;ng bố tr&ecirc;n tạp ch&iacute; Dinh dưỡng Anh cho thấy tốt hơn hết, bạn n&ecirc;n ăn một ch&uacute;t g&igrave; đ&oacute; trước khi uống c&agrave; ph&ecirc; v&igrave; lợi &iacute;ch&nbsp;sức khỏe&nbsp;của bạn.</p>\r\n\r\n<p>Nghi&ecirc;n cứu của Trung t&acirc;m Dinh dưỡng, Thể dục v&agrave; Trao đổi chất tại Đại học Bath (Anh) đ&atilde; xem x&eacute;t những t&aacute;c động của một t&aacute;ch c&agrave; ph&ecirc; đen đối với 29 người đ&agrave;n &ocirc;ng v&agrave; phụ nữ khỏe mạnh.</p>\r\n\r\n<p>C&aacute;c đối tượng được y&ecirc;u cầu trải qua 3 điều kiện kh&aacute;c nhau theo thứ tự ngẫu nhi&ecirc;n:</p>\r\n\r\n<p>- Một người c&oacute; giấc ngủ b&igrave;nh thường v&agrave;o ban đ&ecirc;m, sau đ&oacute; d&ugrave;ng đồ uống c&oacute; đường v&agrave;o buổi s&aacute;ng</p>\r\n\r\n<p>- Một người được đ&aacute;nh thức 5 ph&uacute;t sau mỗi giờ v&agrave; d&ugrave;ng đồ uống c&oacute; đường v&agrave;o bữa s&aacute;ng</p>\r\n\r\n<p>- Một người c&oacute; giấc ngủ bị gi&aacute;n đoạn được uống một t&aacute;ch c&agrave; ph&ecirc; nửa giờ trước khi d&ugrave;ng đồ uống c&oacute; đường v&agrave;o buổi s&aacute;ng.</p>\r\n\r\n<p>Sau khi lấy mẫu m&aacute;u của những người tham gia, c&aacute;c nh&agrave; nghi&ecirc;n cứu ph&aacute;t hiện những người uống c&agrave; ph&ecirc; trước khi d&ugrave;ng đồ uống c&oacute; đường c&oacute; lượng đường huyết tăng đột biến 50% so với&nbsp;c&aacute;c&nbsp;đối tượng kh&aacute;c.</p>\r\n\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu n&oacute;i rằng mặc d&ugrave; uống c&agrave; ph&ecirc; c&oacute; nhiều t&aacute;c dụng nhưng d&ugrave;ng trước bữa s&aacute;ng l&agrave;m tăng t&igrave;nh trạng kh&aacute;ng insulin. Theo thời gian, điều n&agrave;y dẫn tới nguy cơ mắc bệnh tiểu đường v&agrave; bệnh tim.</p>\r\n\r\n<p>&quot;Việc kiểm so&aacute;t lượng đường trong m&aacute;u của ch&uacute;ng ta bị suy giảm khi thực phẩm đầu ti&ecirc;n m&agrave; cơ thể ch&uacute;ng ta hấp thụ l&agrave; c&agrave; ph&ecirc;, đặc biệt l&agrave; sau một đ&ecirc;m mất ngủ&quot;, đồng t&aacute;c giả nghi&ecirc;n cứu James Betts cho biết.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta c&oacute; thể cải thiện điều n&agrave;y bằng c&aacute;ch ăn trước rồi uống c&agrave; ph&ecirc; sau nếu cảm thấy vẫn cần. Biết được điều n&agrave;y mang lại những lợi &iacute;ch sức khỏe cho tất cả ch&uacute;ng ta&quot;.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, uống c&agrave; ph&ecirc; v&agrave;o buổi s&aacute;ng sớm kh&ocirc;ng phải l&agrave; c&aacute;ch tốt nhất để bạn đạt được hiệu quả cao nhất trong qu&aacute; tr&igrave;nh tăng cường năng lượng m&agrave; bạn mong đợi.</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; c&aacute;ch tốt nhất l&agrave; tu&acirc;n theo mức độ cortisol - hormone kiểm so&aacute;t stress, được tiết ra trong ng&agrave;y. Giả sử bạn thức dậy l&uacute;c 6h30, thời gian tốt nhất để uống c&agrave; ph&ecirc; c&oacute; chứa caffein l&agrave; từ 9h30 đến 11h30 để tr&aacute;nh bất kỳ phản ứng bất lợi n&agrave;o.</p>', 'vietnamnet.vn', 'An Yên', 'thoi-diem-uong-ca-phe-gay-hai-cho-suc-khoe.jpg', 1, '2021-06-28 05:42:51', '2021-07-03 08:00:55'),
(3, 'Bộ Y tế truyền đi thông điệp “Bạn vẫn đẹp khi đeo khẩu trang”.', 'Phòng chống dịch Covid-19, Bộ Y tế truyền đi thông điệp “Bạn vẫn đẹp khi đeo khẩu trang”.', '<p>Ng&agrave;y 26.8, Bộ Y tế ch&iacute;nh thức ph&aacute;t đi th&ocirc;ng điệp v&agrave; bộ ảnh mang t&ecirc;n &ldquo;Bạn vẫn đẹp khi đeo khẩu trang&rdquo;&nbsp;được rất nhiều nghệ sĩ, diễn vi&ecirc;n, hoa hậu, cầu thủ v&agrave; nhiều người nổi tiếng tham gia v&agrave; hưởng ứng, nhằm ph&ograve;ng chống dịch&nbsp;COVID-19.</p>\r\n<p>Trong chuỗi hoạt động hưởng ứng chiến dịch truyền th&ocirc;ng &ldquo;Niềm tin chiến thắng&rdquo;, Bộ Y tế ph&aacute;t động th&ocirc;ng điệp tr&ecirc;n với mong muốn k&ecirc;u gọi cộng đồng h&atilde;y thường xuy&ecirc;n đeo khẩu trang đ&uacute;ng c&aacute;ch để ph&ograve;ng tr&aacute;nh l&acirc;y nhiễm&nbsp;COVID-19.</p>\r\n<p>Biện ph&aacute;p thường xuy&ecirc;n đeo khẩu trang đ&uacute;ng c&aacute;ch tuy l&agrave; h&agrave;nh động nhỏ nhưng khi tạo th&agrave;nh một th&oacute;i quen tốt th&igrave; sẽ v&ocirc; c&ugrave;ng &yacute; nghĩa, thể hiện &yacute; thức bảo vệ sức khỏe cho ch&iacute;nh bản th&acirc;n, gia đ&igrave;nh v&agrave; những người xung quanh. Đồng thời, cho thấy sự đo&agrave;n kết, tinh thần quyết t&acirc;m chống dịch của Việt Nam.</p>\r\n<p>Bộ Y tế mong muốn cộng đồng c&ugrave;ng hưởng ứng chiến dịch &ldquo;Bạn vẫn đẹp khi đeo khẩu trang&rdquo;, bằng c&aacute;ch:</p>\r\n<p>Thường xuy&ecirc;n đeo khẩu trang khi đi ra ngo&agrave;i, đến nơi c&ocirc;ng cộng, đi tr&ecirc;n c&aacute;c phương tiện giao th&ocirc;ng, khi tới nơi đ&ocirc;ng người, đến cơ sở y tế v&agrave; vệ sinh khẩu trang vải hằng ng&agrave;y; thải bỏ, thu gom khẩu trang y tế đ&uacute;ng nơi quy định.</p>\r\n<p>D&ugrave; bạn ở đ&acirc;u hay bạn l&agrave; ai, h&atilde;y cho ch&uacute;ng t&ocirc;i thấy sự đo&agrave;n kết nhất tr&iacute; của bạn trong việc tu&acirc;n thủ c&aacute;c nguy&ecirc;n tắc y tế cộng đồng v&agrave; đeo khẩu trang nhằm đảm bảo an to&agrave;n sức khỏe, ph&ograve;ng tr&aacute;nh l&acirc;y nhiễm COVID-19.</p>\r\n<p>Bộ Y tế k&ecirc;u gọi ch&uacute;ng ta h&atilde;y c&ugrave;ng nhau x&acirc;y dựng th&oacute;i quen mới để chung sống an to&agrave;n với dịch v&agrave; vững tin Việt Nam quyết thắng dịch bệnh n&agrave;y!</p>\r\n', 'Bộ Y tế', 'Bộ Y tế', 'Deo-Khau-Trang-2.jpg', 1, '2021-06-28 07:36:47', '2021-06-28 07:36:47'),
(4, 'Những điều cần biết để có một tủ thuốc gia đình', 'Mỗi nhà đều cần có một tủ thuốc mini với những loại thuốc cần thiết phòng trường hợp đau ốm nhẹ. Nhưng với thị trường thuốc đa dạng như hiện nay, ai cũng bối rối, không biết nên chọn những loại thuốc nào thật cần thiết nhất cho tủ thuốc tại nhà. Hãy tham khảo những lời khuyên dưới đây .', '<p>Đặc biệt, tủ thuốc mini của bạn c&oacute; thể chứa nhiều loại thuốc v&agrave; vật dụng y tế, nhưng kh&ocirc;ng thể thiếu 10 loại sau.</p>\r\n\r\n<p>1. Aspirin</p>\r\n\r\n<p>Thuốc chứa Aspirin l&agrave; những loại thuốc v&ocirc; c&ugrave;ng th&ocirc;ng dụng v&agrave; cần thiết. N&oacute; c&oacute; t&aacute;c dụng giảm đau đầu, vi&ecirc;m,sưng. Hầu như bất k&igrave; gia đ&igrave;nh n&agrave;o cũng c&oacute; 1 v&agrave;i vỉ thuốc chứa Aspirin tại nh&agrave;.</p>\r\n\r\n<p>2. Thuốc ho</p>\r\n\r\n<p>Ho l&agrave; triệu chứng của những loại bệnh phổ biến, thường gặp khi c&uacute;m, ốm sốt. &nbsp;Ho, nếu kh&ocirc;ng trị kịp thời, c&oacute; thể g&acirc;y ra cho bạn nhiều phiền to&aacute;i. V&igrave; vậy, những vỉ thuốc ho l&agrave; rất quan trọng. Lựa chọn tốt nhất l&agrave; bạn n&ecirc;n uống thuốc ho kh&ocirc;ng chứa codeine, để tr&aacute;nh g&acirc;y buồn ngủ.</p>\r\n\r\n<p>3.Thuốc khử tr&ugrave;ng</p>\r\n\r\n<p>Đ&acirc;y l&agrave; loại thuốc chống nhiễm tr&ugrave;ng khi bạn bị đứt hay trầy xước tay ch&acirc;n. Nếu những đứa trẻ nh&agrave; bạn hay nghịch ngợm th&igrave; đừng qu&ecirc;n mua một &iacute;t thuốc khử tr&ugrave;ng dự ph&ograve;ng.</p>\r\n\r\n<p>4.Thuốc hạ sốt</p>\r\n\r\n<p>C&oacute; t&aacute;c dụng hạ sốt, giảm đau, chống vi&ecirc;m.</p>\r\n\r\n<p>5. Urgo</p>\r\n\r\n<p>Urgo l&agrave; vật n&ecirc;n c&oacute; mặt thường xuy&ecirc;n trong tủ thuốc. Khi bạn bị đứt tay, hay trầy xước, h&atilde;y d&aacute;n urgo ngay để sơ cứu, chống bụi bẩn g&acirc;y nhiễm tr&ugrave;ng. Ngo&agrave;i ra, bạn n&ecirc;n c&oacute; băng cứu thương, b&ocirc;ng.</p>\r\n\r\n<p>6. Cặp nhiệt độ</p>\r\n\r\n<p>Đo nhiệt độ cơ thể l&agrave; c&aacute;ch chuẩn đo&aacute;n một số loại bệnh từ th&ocirc;ng thường đến nghi&ecirc;m trọng. H&atilde;y gọi cho b&aacute;c sỹ khi nhiệt độ cơ thể cao hơn 39 độ C để nhận được tư vấn d&ugrave;ng thuốc hợp l&yacute;.</p>\r\n\r\n<p>7. Thuốc chống ti&ecirc;u chảy</p>\r\n\r\n<p>Trong điều kiện ăn uống sinh hoạt như hiện nay, thuốc chống ti&ecirc;u chảy l&agrave; sự lựa chọn kh&ocirc;ng thể thiếu của bạn.&nbsp; Nếu triệu chứng ti&ecirc;u chảy k&eacute;o d&agrave;i nhiều ng&agrave;y, bạn phải đến b&aacute;c sỹ ngay.</p>\r\n\r\n<p>8.Kem chống nắng</p>\r\n\r\n<p>Bạn n&ecirc;n b&ocirc;i kem chống nắng c&oacute; chỉ số SF 15 quanh năm. Ngo&agrave;i t&aacute;c dụng chống nắng, n&oacute; c&ograve;n gi&uacute;p bạn chống lại lũ c&ocirc;n tr&ugrave;ng đ&aacute;ng gh&eacute;t.</p>\r\n\r\n<p>9. Băng giảm đau</p>\r\n\r\n<p>D&ugrave;ng để d&aacute;n v&agrave;o c&aacute;c chỗ đau khi bạn vận động mạnh hay chơi thể thao. N&oacute; c&oacute; t&aacute;c dụng l&agrave;m tan c&aacute;c vết sưng, giảm đau.</p>\r\n\r\n<p>10. Thuốc giảm đau bụng</p>\r\n\r\n<p>Đ&ocirc;i khi bạn bị đau bụng do dạ d&agrave;y thừa axit. H&atilde;y d&ugrave;ng những loại thuốc c&oacute; chứa c&aacute;c chất giảm axit (antacid). N&oacute; sẽ l&agrave;m bạn hết đau bụng trong gi&acirc;y l&aacute;t.</p>\r\n\r\n<p>&nbsp;</p>', 'suckhoedoisong.vn', 'VTC', 'tuthuoc.jpg', 1, '2021-06-28 09:43:32', '2021-06-28 09:43:32'),
(5, 'Thảo dược Thất Sơn - Kỳ 1: Phố… thuốc núi', 'Phố… thuốc núi nằm sát chân núi Trà Sư (khóm Trà Sư, thị trấn Nhà Bàng, H.Tịnh Biên, An Giang). Chỉ có 5 hộ (mỗi hộ vài ba cửa hàng) kinh doanh ngành hàng này mà mùi thơm của thảo dược lan tỏa ngạt ngào khắp phố.\r\nÔng Đặng Văn My (Năm My), chủ nhân Phòng Chẩn trị y học cổ truyền - Cửa hàng dược liệu Lộc Quý, cho biết phần lớn cây thuốc bày bán ở đây do đồng bào Khmer xóm Hào Xểnh (ấp Tây Hưng, xã Nhơn Hưng, H.Tịnh Biên) khai thác từ dãy Thất Sơn huyền bí.', '<p><strong>Thuốc g&igrave; cũng c&oacute;</strong></p>\r\n\r\n<p>Từ 7 giờ s&aacute;ng đến 12 giờ trưa hằng ng&agrave;y l&agrave; thời gian &ocirc;ng Chau Ru&ocirc;l &ldquo;quần&rdquo; h&aacute;i thuốc. &Ocirc;ng h&aacute;i đủ thứ thảo dược: mần ri, cỏ xước, nh&acirc;n trần, rau đắng, b&ocirc;ng dừa kiểng, c&oacute;c k&egrave;n, v&otilde; vẽ, đỗ trọng, c&acirc;y bạch hoa thảo&hellip; C&aacute;c loại thuốc đem về bằm nhỏ, phơi kh&ocirc;, bẻ k&ecirc;u r&ocirc;m rốp, để l&acirc;u kh&ocirc;ng bị mốc mới b&aacute;n được. Thuốc v&ocirc; bao, v&aacute;c vai, lội bộ, hoặc chở xe đạp đi b&aacute;n. Mỗi ng&agrave;y một gia đ&igrave;nh l&agrave;m nghề h&aacute;i thuốc chỉ thu nhập được v&agrave;i ba chục ng&agrave;n đồng. D&ugrave; mức thu nhập qu&aacute; thấp, nhưng nghề h&aacute;i thuốc đ&atilde; gi&uacute;p cho 50 hộ kh&ocirc;ng nghề nghiệp, kh&ocirc;ng ruộng rẫy ở&nbsp; x&oacute;m H&agrave;o Xểnh được no c&aacute;i bụng.</p>\r\n\r\n<p>Ngo&agrave;i nguồn c&acirc;y thuốc h&aacute;i ở Thất Sơn, &ldquo;phố thuốc n&uacute;i&rdquo; c&ograve;n b&aacute;n nhiều loại &ldquo;sung dược&rdquo; được khai th&aacute;c từ nước bạn Campuchia, như: mối ch&uacute;a, b&ugrave; kẹp, bửa củi, tắc k&egrave; b&ocirc;ng... Nghe đồn, những thứ n&agrave;y ng&acirc;m rượu sẽ đạt chuẩn &ldquo;&ocirc;ng uống b&agrave; khen&rdquo;. Ngo&agrave;i ra, người d&acirc;n nước bạn c&ograve;n l&eacute;n l&uacute;t khai th&aacute;c v&agrave; vận chuyển c&aacute;c loại dược liệu qu&yacute; như nam đỗ trọng, s&acirc;m hồng, b&igrave;m bịp, c&agrave; dăm, h&agrave; thủ &ocirc; trắng... đưa sang chợ Nh&agrave; B&agrave;ng theo c&aacute;c con đường m&ograve;n nối liền phum s&oacute;c để &ldquo;n&eacute;&rdquo; c&aacute;c trạm kiểm so&aacute;t.</p>\r\n\r\n<p><strong>Quấn ch&acirc;n du kh&aacute;ch</strong></p>\r\n\r\n<p>&ldquo;Phố thuốc n&uacute;i&rdquo; l&uacute;c n&agrave;o cũng c&oacute; kh&aacute;ch đến mua, nhất l&agrave; m&ugrave;a V&iacute;a B&agrave; (từ th&aacute;ng gi&ecirc;ng đến cuối th&aacute;ng 4 &acirc;m lịch). Người ta mua c&aacute;c loại thảo dược đặc trị c&aacute;c bệnh T&acirc;y y &ldquo;b&oacute; tay&rdquo;, d&acirc;n gian đồn tụng trị dứt, như dứa gai chẳng hạn. &Ocirc;ng Đỗ Văn Trọng (qu&ecirc; Tr&agrave; Vinh) cho biết: &ldquo;Dứa gai đem về t&aacute;ch tr&aacute;i, chặt nhỏ, phơi kh&ocirc;, nấu nước uống trị bịnh tiểu đường đại t&agrave;i&rdquo;. Nghe vậy, b&agrave; Vương Lệ Hoa (qu&ecirc; S&oacute;c&nbsp; Trăng), b&egrave;n mua năm, s&aacute;u tr&aacute;i dứa gai đem về uống&hellip; ngừa bệnh.&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Bảy &ldquo;b&aacute;nh m&igrave;&rdquo; l&agrave; th&acirc;n chủ của d&atilde;y cửa h&agrave;ng dược liệu n&agrave;y. D&ugrave; mới 44 tuổi, nhưng &ocirc;ng Bảy nổi tiếng l&agrave; một thầy thuốc giỏi đất V&agrave;m Rầy (H.H&ograve;n Đất, Ki&ecirc;n Giang), chuy&ecirc;n trị những chứng bệnh &ldquo;thầy chạy, b&aacute;c sĩ ch&ecirc;&rdquo;. Bữa nay &ocirc;ng tới phố n&uacute;i hốt thuốc n&uacute;i đem về trị cho th&acirc;n chủ. Những người chuy&ecirc;n mua đi b&aacute;n lại cũng nhiều. Mỗi chuyến đi, họ &ldquo;bổ&rdquo; nhiều loại thuốc, chất đầy boọc-ba-ga v&agrave; sườn xe gắn m&aacute;y đem về b&aacute;n. Họ ở nhiều nơi, như: Rạch Gi&aacute;, Long An, Vĩnh Long... C&oacute; khi, chỉ cần một c&uacute; điện thoại l&agrave; họ nhận được dược liệu chuyển qua bằng xe đ&ograve;.</p>\r\n\r\n<p>C&aacute;c cửa h&agrave;ng dược liệu ở đ&acirc;y c&oacute; đặc điểm chung l&agrave; b&agrave;y tr&iacute; h&agrave;ng h&oacute;a rất&hellip; lộn xộn. Thứ đựng trong hũ nhựa, c&aacute;i treo lủng lẳng tr&ecirc;n trần, rồi la liệt nhiều loại thuốc được đặt, k&ecirc; ở bất cứ nơi n&agrave;o c&oacute; thể. C&aacute;c &ocirc;ng b&agrave; chủ kh&ocirc;ng ai biết cửa h&agrave;ng m&igrave;nh c&oacute; cả thảy bao nhi&ecirc;u loại thuốc. Nhưng c&ocirc;ng dụng của dược liệu th&igrave; họ r&agrave;nh. Bột huyền, bột củ m&agrave;i (bột nưa) quậy nước s&ocirc;i pha ch&uacute;t đường hoặc quậy nước lạnh với ch&uacute;t đường d&ugrave;ng m&aacute;t người, trị kiết đại t&agrave;i. Mỏ quạ trị nhức mỏi, (40.000 đồng/kg). B&iacute; kỳ nam bổ phổi, m&aacute;t gan. Ngải đen trị đau bụng gi&oacute;, nhậu say ngậm một miếng tỉnh lại; u n&atilde;o, suy thận mỗi s&aacute;ng ngậm một miếng l&agrave; đủ sức l&agrave;m việc nặng. Gấm đen trị ban, nhất l&agrave; tiểu đường. Đặc biệt mật nh&acirc;n gi&aacute; rẻ đến bất ngờ: 15.000 đồng/kg đ&atilde; b&agrave;o l&aacute;t sẵn, từ Campuchia đem qua. Mật nh&acirc;n (t&ecirc;n khoa học l&agrave; Eurycoma longifolia) c&oacute; thể ngăn chặn xơ gan, ức chế sự tiến triển của virus vi&ecirc;m gan B, giải rượu mạnh, chống mệt mỏi, k&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch sinh l&yacute; nam giới&hellip; Ở H.Ph&uacute; Quốc (Ki&ecirc;n Giang), mật nh&acirc;n được b&aacute;n với gi&aacute;&nbsp; 50.000 đồng/kg; c&ograve;n ở Cần Thơ, c&oacute; người &ldquo;h&eacute;t&rdquo; 80.000 đồng/kg&hellip;</p>\r\n\r\n<p>Phố n&uacute;i b&aacute;n thuốc n&uacute;i, thực hiện danh ng&ocirc;n m&agrave; danh y Tuệ Tĩnh cổ s&uacute;y &ldquo;Nam dược trị Nam nh&acirc;n&rdquo;; do vậy cần c&oacute; quyết s&aacute;ch bảo tồn, ph&aacute;t triển để nh&acirc;n d&acirc;n ta c&oacute; cơ hội sử dụng c&aacute;c loại dược liệu trong nước l&acirc;u bền. V&agrave; để &ldquo;phố thuốc n&uacute;i&rdquo; Nh&agrave; B&agrave;ng c&oacute; từ trăm năm nay sẽ c&ograve;n tồn tại m&atilde;i c&ugrave;ng năm th&aacute;ng.</p>', 'thanhnien.vn', 'Phương Kiều', 'thuocthatson.jpg', 1, '2021-06-28 09:54:08', '2021-06-28 09:54:08'),
(6, 'Vaccin Covid-19 hoạt động như thế nào?', 'Trước tình hình dịch bệnh bùng phát trở lại tại Việt Nam, việc tiêm chủng vaccin để tạo miễn dịch cộng đồng lại cấp thiết hơn bao giờ hết. Hãy cùng tìm hiểu về cơ chế hoạt động của vaccin Covid-19 để có sự chuẩn bị kiến thức đầy đủ trước khi tiêm chủng nhé.', '<h2>Vaccin Covid-19 gi&uacute;p tạo ra kh&aacute;ng thể</h2>\r\n\r\n<p>Khi mầm bệnh như virus COVID-19 tấn c&ocirc;ng cơ thể ch&uacute;ng ta, ch&uacute;ng sinh s&ocirc;i nảy nở v&agrave; g&acirc;y bệnh. Sau khi cơ thể nhiễm bệnh, hệ miễn dịch ghi nhớ những g&igrave; n&oacute; đ&atilde; học hỏi về c&aacute;ch bảo vệ cơ thể chống lại bệnh đ&oacute;.</p>\r\n\r\n<p>Tế b&agrave;o lympho T ch&iacute;nh l&agrave; &ldquo;tế b&agrave;o ghi nhớ&rdquo;. Khi cơ thể gặp lại đ&uacute;ng loại virus đ&oacute;, tế b&agrave;o lympho T ngay lập tức h&agrave;nh động, c&ograve;n tế b&agrave;o lympho B gi&uacute;p &ldquo;tạo ra kh&aacute;ng thể&rdquo; &ndash; chất chống lại virus v&agrave; bảo vệ cơ thể.</p>\r\n\r\n<p>Tuy nhi&ecirc;n nhờ c&oacute; vaccin, cơ thể ch&uacute;ng ta ph&aacute;t triển khả năng miễn dịch chống lại virus g&acirc;y bệnh m&agrave; kh&ocirc;ng cần nhiễm bệnh. Vaccin dạy cơ thể ghi nhớ t&aacute;c nh&acirc;n g&acirc;y bệnh, đồng thời k&iacute;ch th&iacute;ch ra kh&aacute;ng thể để chống lại virus trong tương lai.</p>\r\n\r\n<h2>Khi n&agrave;o vaccin Covid-19 ph&aacute;t huy t&aacute;c dụng?</h2>\r\n\r\n<p>Th&ocirc;ng thường v&agrave;i tuần sau khi ti&ecirc;m chủng, cơ thể mới sản sinh ra tế b&agrave;o lympho T v&agrave; lympho B. Do vậy c&oacute; thể c&oacute; trường hợp người bị nhiễm virus g&acirc;y bệnh COVID-19 ngay trước hoặc sau khi ti&ecirc;m vaccin, rồi sau đ&oacute; nhiễm bệnh do vaccin chưa c&oacute; đủ thời gian để tạo ra miễn dịch.</p>\r\n\r\n<p>Đ&ocirc;i khi sau khi ti&ecirc;m vaccin, qu&aacute; tr&igrave;nh tạo miễn dịch c&oacute; thể g&acirc;y ra c&aacute;c triệu chứng như sốt. C&aacute;c triệu chứng n&agrave;y l&agrave; b&igrave;nh thường v&agrave; l&agrave; những dấu hiệu cho thấy cơ thể đang h&igrave;nh th&agrave;nh khả năng miễn dịch.</p>\r\n\r\n<h2>Cần ti&ecirc;m bao nhi&ecirc;u mũi vaccin Covid-19?</h2>\r\n\r\n<p>Để được ti&ecirc;m chủng đầy đủ, bạn sẽ cần một liều hoặc hai liều t&ugrave;y loại vaccin COVID-19.</p>\r\n\r\n<p>Hai mũi ti&ecirc;m: Nếu ti&ecirc;m vaccin COVID-19 cần 2 mũi ti&ecirc;m, bạn được xem l&agrave; ti&ecirc;m chủng đầy đủ sau&nbsp;<em>2 tuần</em>&nbsp;kể từ khi ti&ecirc;m mũi thứ hai.</p>\r\n\r\n<p>Một mũi ti&ecirc;m: Nếu ti&ecirc;m vắc-xin COVID-19 chỉ y&ecirc;u cầu 1 mũi ti&ecirc;m, bẹn được xem l&agrave; đ&atilde; ti&ecirc;m chủng đầy đủ sau&nbsp;<em>2 tuần</em>&nbsp;kể từ khi ti&ecirc;m.</p>\r\n\r\n<p>Nếu mới ti&ecirc;m chưa đủ hai tuần hoặc vẫn c&ograve;n cần được ti&ecirc;m liều thứ hai, khi đ&oacute; cơ thể CHƯA tạo miễn dịch ho&agrave;n to&agrave;n. Do vậy cần tiếp tục thực hiện 5K của Bộ Y tế cho đến khi được ti&ecirc;m chủng đầy đủ (hai tuần sau liều cuối c&ugrave;ng).</p>\r\n\r\n<h2>Ti&ecirc;m vaccin Covid-19 c&oacute; nguy hiểm kh&ocirc;ng?</h2>\r\n\r\n<p>Sau mỗi liều vắc-xin, c&oacute; thể xuất hiện một số dấu hiệu cho thấy cơ thể đang nỗ lực để x&acirc;y dựng h&agrave;ng r&agrave;o bảo vệ chống lại virus. C&aacute;c t&aacute;c dụng phụ n&agrave;y c&oacute; thể ảnh hưởng đến khả năng thực hiện c&aacute;c hoạt động h&agrave;ng ng&agrave;y, nhưng sẽ biến mất sau v&agrave;i ng&agrave;y. Ở một số người kh&ocirc;ng c&oacute; biểu hiện của t&aacute;c dụng phụ.</p>\r\n\r\n<p>Tr&ecirc;n c&aacute;nh tay nơi được ti&ecirc;m c&oacute; thể gặp c&aacute;c triệu chứng:</p>\r\n\r\n<ul>\r\n	<li>Đau</li>\r\n	<li>Mẩn đỏ</li>\r\n	<li>Sưng tấy</li>\r\n</ul>\r\n\r\n<p>Ngo&agrave;i ra c&oacute; thể mệt mỏi, đau đầu, đau cơ, ớn lạnh, sốt, buồn n&ocirc;n sau ti&ecirc;m.</p>\r\n\r\n<p>Đối với loại vaccin Covid-19 ti&ecirc;m 2 mũi, t&aacute;c dụng phụ sau mũi ti&ecirc;m thứ hai c&oacute; thể nhiều hơn mũi thứ nhất. Đ&acirc;y l&agrave; dấu hiệu b&igrave;nh thường của qu&aacute; tr&igrave;nh x&acirc;y dựng miễn dịch v&agrave; sẽ biến mất trong v&ograve;ng v&agrave;i ng&agrave;y.</p>\r\n\r\n<p>Để giảm giảm đau v&agrave; cảm gi&aacute;c kh&oacute; chịu ở vị tr&iacute; ti&ecirc;m, c&oacute; thể &aacute;p khăn sạch, m&aacute;t v&agrave; ẩm l&ecirc;n khu vực đ&oacute; hoặc tập thể dục cho c&aacute;nh tay. Với trường hợp sốt sau ti&ecirc;m, n&ecirc;n mặc trang phục mỏng nhẹ v&agrave; uống nhiều nước để giảm cảm gi&aacute;c kh&oacute; chịu.</p>\r\n\r\n<p>Nếu c&aacute;c triệu chứng tr&ecirc;n kh&ocirc;ng mất đi sau v&agrave;i ng&agrave;y, cần đến ngay cơ sở y tế gần nhất để được hướng dẫn.</p>\r\n\r\n<h2>Sốc phản vệ sau khi ti&ecirc;m vaccin Covid-19</h2>\r\n\r\n<p>Tỷ lệ sốc phản vệ sau khi ti&ecirc;m vaccin ph&ograve;ng Covid-19 kh&ocirc;ng cao v&agrave; t&ugrave;y thuộc v&agrave;o từng loại vaccin.</p>\r\n\r\n<p>Để tr&aacute;nh phản ứng phản vệ khi ti&ecirc;m vaccin, người ch&iacute;ch ngừa phải hợp t&aacute;c tốt với nh&acirc;n vi&ecirc;n y tế. Nh&acirc;n vi&ecirc;n y tế trước khi ch&iacute;ch phải hỏi người ti&ecirc;m vaccin c&aacute;c c&acirc;u hỏi theo bảng s&agrave;ng lọc. V&iacute; dụ c&oacute; bị dị ứng g&igrave; kh&ocirc;ng, ăn hải sản c&oacute; bị ngứa hay kh&ocirc;ng, hiện c&oacute; bị bệnh cấp t&iacute;nh hay đang điều trị bệnh g&igrave; để xem c&oacute; tương th&iacute;ch với loại vaccin n&agrave;y kh&ocirc;ng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, việc s&agrave;ng lọc n&agrave;y kh&ocirc;ng thể đ&aacute;nh gi&aacute; 100% được bởi c&oacute; những người trước đ&acirc;y rất khoẻ mạnh, chưa bao giờ dị ứng với thức ăn, thuốc, ch&iacute;ch ngừa nhưng vẫn c&oacute; nguy cơ bị phản vệ khi ti&ecirc;m vaccin. Ch&iacute;nh v&igrave; vậy sau khi ch&iacute;ch ngừa, nh&acirc;n vi&ecirc;n y tế y&ecirc;u cầu người được ch&iacute;ch ngừa phải ở lại 30 ph&uacute;t đến 1 giờ tại điểm ch&iacute;ch đ&oacute; để nếu c&oacute; bất kỳ vấn đề g&igrave; xảy ra, họ c&oacute; thể hỗ trợ cấp cứu tại chỗ ngay lập tức.</p>\r\n\r\n<p>Sau 3 ng&agrave;y ti&ecirc;m vaccin Covid-19, nếu c&oacute; bất cứ triệu chứng n&agrave;o bất thường th&igrave; n&ecirc;n quay lại cơ sở y tế v&agrave; cầm theo giấy ch&iacute;ch ngừa để nh&acirc;n vi&ecirc;n y tế c&oacute; hướng điều trị ph&ugrave; hợp nhất.</p>\r\n\r\n<p>Cho tới hiện tại tr&ecirc;n to&agrave;n thế giới cũng chỉ c&oacute; rải r&aacute;c một số trường hợp phản ứng phản vệ mạnh dẫn tới diễn tiến qu&aacute; nặng, kh&ocirc;ng cứu chữa được. C&ograve;n đại đa số c&aacute;c trường hợp phản vệ l&agrave; cấp cứu được.</p>', 'pharmacity.vn', 'moh.gov.vn', '210614_VaccinCovid.jpg', 1, '2021-07-02 10:08:54', '2021-07-02 10:08:54');
