INSERT INTO `y_kien_khach_hang` (`id`, `ho_ten_khach_hang`, `dia_chi`, `dien_thoai`, `email`, `noi_dung`, `hinh`, `noi_dung_tra_loi`, `trang_thai`, `muc_dich`, `created_at`, `updated_at`) VALUES
(1, 'Nguyen Gia Minh', 'An Giang', '0356792299', 'giaminng@gmail.com', 'Đi đến hiệu thuốc này để tìm Levothyroxine Sodium. Nhân viên rất hữu ích, không có thời gian chờ đợi. Họ có sẵn một số nhãn hiệu khác nhau (Levosum, Levothyrox trong 50mcg & 100mcg). Giá khoảng 50k-90k VND / 100 viên.', 'quannh.JPG', '', 0, 'ý kiến', '2021-06-21 08:01:24', '2021-07-22 14:31:16'),
(2, 'Phát CEO FPT', 'Trần Xuân Soạn, Quận 7, TP Hồ Chí Minh', '09090901212', 'ceofptphatnt@gmail.com', 'Ồ giờ mới biết nhà thuốc tây cũng có bán cả thực phẩm dinh dưỡng như mật ong hay sữa bột nữa kìa mấy chế.', 'phatntFPT.jpg', ' ', 0, 'ý kiến', '2021-06-21 08:16:27', '2021-06-21 08:16:27'),
(3, 'Nguyễn Viết Anh', 'Tăng Nhơn Phú A, Quận 9, Tp Hồ Chí Minh', '0165999111', 'anhnguyen@gmail.com', 'Hiện giờ tôi ưu tiên mua các sản phẩm chăm sóc sức khỏe của công ty dược phẩm tại cửa hàng vì luôn được tư vấn rất kỹ.', 'vietanh.jpg', ' ', 0, 'ý kiến', '2021-06-21 08:21:26', '2021-06-21 08:21:26'),
(4, 'Nguyễn Hồng Quân', 'An Giang', '0352996792', 'quankhung214.9@gmail.com', 'Tôi hay bỏ bữa sáng, gần đây hơi đau bụng, xin hỏi cần dùng thuốc gì để giảm đau?', '', 'Bạn đang có dấu hiệu của đau dạ dày.  Bạn nên dùng Thuốc chữa đau dạ dày chữ P. link sản phẩm http://cuahangthuoc.com/san-pham/danh-sach/phosphalugel-54\r\nThành phần thuốc có chứa Aluminium phosphate 20% giúp kháng lại acid trong dạ dày và cải thiện tình trạng viêm loét. Trường hợp mắc các chứng bệnh liên quan đến dạ dày – hành tá tràng, rối loạn tiêu hóa hoặc ngộ độc dẫn đến xuất huyết đều có thể sử dụng thuốc chữ P. \r\nChúc bạn mau hết bệnh.', 1, 'tư vấn', '2021-07-22 14:45:35', '2021-07-22 14:49:56'),
(5, 'Nguyễn Viết Anh', 'Bình Dương', '0909090909', 'anhvietnguyen1997@gmail.com', 'một vài sản phẩm giá cao hơn thị trường. Đề nghị giải thích', '', 'cảm ơn bạn., bên mình sẽ xem xét.', 1, 'tư vấn', '2021-07-23 14:35:29', '2021-07-23 14:42:13'),
(6, 'Nguyễn Hồng Quân', 'An Giang', '0352996792', 'quankhung214.9@gmail.com', 'dạo gần đây tôi thường mất ngủ, nên dùng sản phẩm nào ạ', '', 'bạn nên sử dụng goldream để cải thiện giấc ngủ.\r\nLink sản phẩm: http://cuahangthuoc.com/san-pham/danh-sach/goldream-16', 1, 'tư vấn', '2021-07-24 12:41:47', '2021-07-28 06:25:45'),
(7, 'Vũ Trung Hiếu', '350/54 Lê Đức Thọ Gò Vấp', '0932146989', 'Vutrunghieu2205@gmail.com', 'bán thuốc chất lượng thuốc tốt', 'trunghieu.JPG', ' ', 0, 'ý kiến', '2021-07-24 12:52:09', '2021-07-24 12:52:09'),
(8, 'Nguyễn Hồng Quân', 'An Giang', '0352996792', 'quankhung214.9@gmail.com', 'tôi bị dau họng', '', 'ban nên dùng thuốc...', 1, 'tư vấn', '2021-07-25 11:11:20', '2021-07-25 11:11:52'),
(9, 'Nguyễn Hồng Quân', 'An Giang', '0352996792', 'quankhung214.9@gmail.com', 'cửa hàng có bán thuốc đuổi muỗi không a', '', 'dạ bên mình vẫn chưa nhập sản phẩm chống muỗi, cảm ơn câu hỏi của bạn, bên mình sẽ tính toán nhập về ạ.', 1, 'tư vấn', '2021-07-27 02:19:45', '2021-07-28 06:39:03'),
(10, 'Nguyễn Hồng Quân', 'An Giang', '0352996792', 'quankhung214.9@gmail.com', 'Chào PharmaNQH, mình đặt hàng nhưng vẫn chưa được nhận được .', 'chuyenhangtre.JPG', ' ', 0, 'tư vấn', '2021-07-27 16:07:58', '2021-07-27 16:07:58');
